import React, { useState } from "react";

function Newsletter({ onNewsletterSubmit, isLoading }) {
  const [email, setEmail] = useState("");
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);

  async function handleSubmit(e) {
    e.preventDefault();
    setError(null);
    setSuccess(false);
    
    const url = 'https://pets.xn--80ahdri7a.site/api/subscription';
    
    try {
      const response = await fetch(url, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ email: email })
      });

      // Читаем ответ сервера в любом случае
      const result = await response.json();
      
      if (!response.ok) {
        // Обрабатываем ошибку, НЕ бросая исключение
        console.log("Server error response:", result);
        
        if (result.errors && result.errors.email) {
          setError(result.errors.email[0]);
        } else if (result.error) {
          setError(result.error);
        } else if (result.message) {
          setError(result.message);
        } else {
          setError(`Ошибка ${response.status}: Не удалось подписаться`);
        }
        return; // Выходим из функции
      }
      
      // Успешный ответ
      console.log("Success:", result);
      setSuccess(true);
      setEmail("");
      
      if (onNewsletterSubmit) {
        onNewsletterSubmit(result);
      }
      
    } catch (error) {
      // Сюда попадаем только при сетевых ошибках или проблемах с JSON
      console.error("Network or JSON parsing error:", error);
      setError("Ошибка подключения к серверу. Проверьте интернет-соединение.");
    }
  };

  return (
    <>
      <h2 className="text-center text-white bg-dark m-2">Подписка на новости</h2>
      <form className="w-50 m-auto p-3" style={{minWidth: 300}} onSubmit={handleSubmit} noValidate>
        <div className="mb-3">
          <label htmlFor="exampleInputEmail1" className="form-label">
            Введите адрес электронной почты
          </label>
          <input 
            type="email" 
            className={`form-control ${error ? 'is-invalid' : success ? 'is-valid' : ''}`}
            id="exampleInputEmail1" 
            aria-describedby="emailHelp"
            value={email}
            onChange={(e) => {
              setEmail(e.target.value);
              setError(null);
            }}
            required
          />
          <div id="emailHelp" className="form-text">
            Мы никогда не делимся Вашими e-mail ни с кем.
          </div>
        </div>

        {error && (
          <div className="alert alert-danger mt-2" role="alert">
            {error}
          </div>
        )}
        
        {success && (
          <div className="alert alert-success mt-2" role="alert">
            Вы успешно подписались на рассылку!
          </div>
        )}

        <button 
          type="submit" 
          className="btn btn-dark" 
          disabled={isLoading || !email}
        >
          {isLoading ? "Подписка..." : "Подписаться"}
        </button>
      </form>
    </>
  );
}

export default Newsletter;
