import React, { useState, useEffect } from 'react';
import Slider from './slider';
import Svyz from './svyz';
import AuthModal from './AuthModal';
import Profile from './profile';
import Nahodka from './nahodka';
import Content from './content';
import Modal_Cat from './cat_card';

function Home({ siteInfo, userInfo, onLogin, onLogout }) {
  const [showNewsletter, setShowNewsletter] = useState(true);
  const [isLoading, setIsLoading] = useState(false);
  const [foundAnimals, setFoundAnimals] = useState([]);
  const [sliderAnimals, setSliderAnimals] = useState([]);
  const [sliderLoading, setSliderLoading] = useState(true);

  // Загрузка слайдера
  useEffect(() => {
    const fetchSliderData = async () => {
      setSliderLoading(true);
      try {
        const response = await fetch('https://pets.xn--80ahdri7a.site/api/pets/slider');
        if (response.ok) {
          const data = await response.json();
          if (data.data && data.data.pets) {
            setSliderAnimals(data.data.pets);
          }
        }
      } catch (err) {
        console.error('Ошибка загрузки слайдера:', err);
        // Пробуем загрузить пустой слайдер
        try {
          const emptyResponse = await fetch('https://pets.xn--80ahdri7a.site/api/pets/slider/empty');
          if (emptyResponse.ok) {
            const emptyData = await emptyResponse.json();
            if (emptyData.data && emptyData.data.pets) {
              setSliderAnimals(emptyData.data.pets);
            }
          }
        } catch (emptyErr) {
          console.error('Ошибка загрузки пустого слайдера:', emptyErr);
        }
      } finally {
        setSliderLoading(false);
      }
    };

    fetchSliderData();
  }, []);

  // Обработчик для создания карточки из формы находки
  const handleFoundAnimalSubmit = async (animalData) => {
    setIsLoading(true);
    
    // Собираем FormData для отправки
    const formData = new FormData();
    
    // Базовые данные
    formData.append('name', userInfo.isLoggedIn ? userInfo.userName : animalData.name);
    formData.append('phone', userInfo.isLoggedIn ? userInfo.phone : animalData.phone);
    formData.append('email', userInfo.isLoggedIn ? userInfo.email : animalData.email);
    formData.append('kind', animalData.animalType === 'cat' ? 'кошка' : 
                    animalData.animalType === 'dog' ? 'собака' : 
                    animalData.animalType === 'rabbit' ? 'кролик' : 'другое');
    
    // Описание
    formData.append('description', animalData.description || 'Описание отсутствует');
    
    // Фото
    if (animalData.file) {
      formData.append('photo1', animalData.file);
    }
    
    // Дополнительные поля
    if (animalData.mark) formData.append('mark', animalData.mark);
    if (animalData.features) formData.append('description', 
      animalData.description + '\n\nОсобенности: ' + animalData.features);
    
    formData.append('confirm', 1);
    
    // Если пользователь не залогинен, добавляем данные для регистрации
    if (!userInfo.isLoggedIn) {
      formData.append('register', 1);
      formData.append('password', 'paSSword1'); // В реальном приложении нужно запросить у пользователя
      formData.append('password_confirmation', 'paSSword1');
    }
    
    try {
      const response = await fetch('https://pets.xn--80ahdri7a.site/api/pets/new', {
        method: 'POST',
        body: formData
      });
      
      if (response.ok) {
        const result = await response.json();
        
        const newAnimal = {
          id: result.data?.id || Date.now(),
          title: `Найдено: ${animalData.animalType === 'cat' ? 'кот/кошка' : 
                  animalData.animalType === 'dog' ? 'собака' : 'животное'}`,
          description: animalData.description,
          image: animalData.imageUrl || 'default-image.jpg',
          date: new Date().toLocaleDateString('ru-RU'),
          type: animalData.animalType || 'other',
          location: animalData.location,
          age: animalData.age || 'неизвестно',
          contact: animalData.contact || userInfo.phone,
          isNew: true
        };
        
        setFoundAnimals(prev => [...prev, newAnimal]);
        alert('Объявление успешно добавлено!');
      } else if (response.status === 422) {
        const errorData = await response.json();
        alert(`Ошибка валидации: ${JSON.stringify(errorData.error?.errors)}`);
      } else {
        alert('Ошибка при добавлении объявления');
      }
    } catch (error) {
      console.error('Ошибка при отправке объявления:', error);
      alert('Ошибка сети при отправке объявления');
    } finally {
      setIsLoading(false);
    }
  };

  const handleNewsletterSubmit = (e) => {
    e.preventDefault();
    setIsLoading(true);
    setTimeout(() => {
      alert("Вы успешно подписались на новости!");
      setIsLoading(false);
      setShowNewsletter(false);
    }, 1000);
  };

  return (
    <div className="App">
      <AuthModal 
        userInfo={userInfo} 
        onLogin={onLogin}
        onRegister={(userData) => {
          onLogin(userData);
        }}
      />
      <Profile userInfo={userInfo} />
      <Nahodka onCreateAnimalCard={handleFoundAnimalSubmit} />
      <Slider animals={sliderAnimals} loading={sliderLoading} />
      <main style={{ minHeight: '70vh' }}>
        <div>
          <Content 
            onNewsletterSubmit={handleNewsletterSubmit}
            showNewsletter={showNewsletter}
            isLoading={isLoading}
            foundAnimals={foundAnimals}
            userInfo={userInfo}
          />
          <Modal_Cat />
        </div>
      </main>
      <Svyz siteInfo={siteInfo} />
    </div>
  );
}

export default Home;
