import React, { useState, useEffect } from "react";

function Profile({ userInfo }) {
  const [userData, setUserData] = useState({
    email: "",
    phone: "",
    name: "",
    registrationDate: "",
    ordersCount: 0,
    petsCount: 0,
    id: ""
  });
  
  const [userOrders, setUserOrders] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [isFetching, setIsFetching] = useState(true);
  const [isFetchingOrders, setIsFetchingOrders] = useState(true);
  const [error, setError] = useState("");
  const [successMessage, setSuccessMessage] = useState("");
  const [daysSinceRegistration, setDaysSinceRegistration] = useState(0);

  // Функция для вычисления дней с момента регистрации
  const calculateDaysSinceRegistration = (registrationDate) => {
    if (!registrationDate) return 0;
    
    try {
      // Парсим дату в формате "DD-MM-YYYY"
      const [day, month, year] = registrationDate.split('-').map(Number);
      const regDate = new Date(year, month - 1, day); // Месяцы в JS начинаются с 0
      const currentDate = new Date();
      
      // Вычисляем разницу в миллисекундах
      const diffTime = Math.abs(currentDate - regDate);
      const diffDays = Math.floor(diffTime / (1000 * 60 * 60 * 24));
      
      return diffDays;
    } catch (err) {
      console.error("Ошибка парсинга даты:", err);
      return 0;
    }
  };

  // Функция для получения ID пользователя из токена
  const getUserIdFromToken = () => {
    const token = localStorage.getItem('authToken');
    if (token) {
      try {
        // Декодируем JWT токен
        const payload = token.split('.')[1];
        const decoded = JSON.parse(atob(payload));
        return decoded.user_id || decoded.id || decoded.sub;
      } catch (e) {
        console.error("Ошибка декодирования токена:", e);
        return null;
      }
    }
    return null;
  };

  // Функция для получения данных пользователя
  const fetchUserData = async () => {
    if (!userInfo?.isLoggedIn) {
      setIsFetching(false);
      return;
    }

    const token = localStorage.getItem('authToken');
    if (!token) {
      setError("Токен авторизации отсутствует");
      setIsFetching(false);
      return;
    }

    // Получаем ID пользователя из токена
    const userId = getUserIdFromToken();
    if (!userId) {
      setError("Не удалось получить ID пользователя");
      setIsFetching(false);
      return;
    }

    try {
      setIsFetching(true);
      setError("");
      
      const response = await fetch(`https://pets.xn--80ahdri7a.site/api/users/${userId}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.status === 401) {
        // Неавторизован
        setError("Требуется авторизация");
        // Закрываем модальное окно и открываем окно аутентификации
        document.querySelector('#myPage .btn-close')?.click();
        setTimeout(() => {
          document.getElementById('authModalButton')?.click();
        }, 300);
        return;
      }

      if (response.status === 404) {
        // Пользователь не найден
        setError("Пользователь не найден");
        setIsFetching(false);
        return;
      }

      if (!response.ok) {
        throw new Error(`Ошибка сервера: ${response.status}`);
      }

      const result = await response.json();
      
      if (result.data && result.data.user && result.data.user.length > 0) {
        const user = result.data.user[0];
        setUserData({
          email: user.email || "",
          phone: user.phone || "",
          name: user.name || "",
          registrationDate: user.registrationDate || "",
          ordersCount: user.ordersCount || 0,
          petsCount: user.petsCount || 0,
          id: user.id || userId
        });
        
        // Вычисляем количество дней с момента регистрации
        if (user.registrationDate) {
          const days = calculateDaysSinceRegistration(user.registrationDate);
          setDaysSinceRegistration(days);
        }
        
        // Сохраняем данные в localStorage для будущего использования
        localStorage.setItem('userData', JSON.stringify({
          id: user.id || userId,
          name: user.name || "",
          email: user.email || "",
          phone: user.phone || ""
        }));
      }
    } catch (err) {
      console.error("Ошибка при загрузке данных пользователя:", err);
      setError("Не удалось загрузить данные пользователя");
    } finally {
      setIsFetching(false);
    }
  };

  // Функция для получения объявлений пользователя
  const fetchUserOrders = async () => {
    if (!userInfo?.isLoggedIn) {
      setIsFetchingOrders(false);
      return;
    }

    const token = localStorage.getItem('authToken');
    if (!token) {
      setIsFetchingOrders(false);
      return;
    }

    try {
      setIsFetchingOrders(true);
      
      // Получаем ID пользователя
      const userId = getUserIdFromToken();
      if (!userId) {
        setUserOrders([]);
        setIsFetchingOrders(false);
        return;
      }
      
      const response = await fetch(`https://pets.xn--80ahdri7a.site/api/users/${userId}/orders/`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        }
      });
      
      if (response.ok && response.status !== 204) {
        const result = await response.json();
        if (result.data && result.data.orders) {
          setUserOrders(result.data.orders);
        } else {
          setUserOrders([]);
        }
      } else if (response.status === 204) {
        // Нет объявлений
        setUserOrders([]);
      } else {
        setUserOrders([]);
      }
      
    } catch (err) {
      console.error("Ошибка при загрузке объявлений:", err);
      setUserOrders([]);
    } finally {
      setIsFetchingOrders(false);
    }
  };

  // Загружаем данные пользователя и его объявления при монтировании компонента
  useEffect(() => {
    if (userInfo?.isLoggedIn) {
      fetchUserData();
      fetchUserOrders();
    } else {
      setIsFetching(false);
      setIsFetchingOrders(false);
    }
  }, [userInfo?.isLoggedIn]);

  // Функция для обновления телефона
  const updatePhone = async (newPhone) => {
    const token = localStorage.getItem('authToken');
    const userId = getUserIdFromToken();
    
    if (!token || !userId) {
      return { success: false, error: "Требуется авторизация" };
    }
    
    try {
      const response = await fetch(`https://pets.xn--80ahdri7a.site/api/users/${userId}/phone`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({ phone: newPhone })
      });
      
      if (response.ok) {
        return { success: true };
      } else {
        const errorData = await response.json();
        return { success: false, error: errorData.error?.message || "Ошибка обновления" };
      }
    } catch (err) {
      return { success: false, error: "Ошибка сети" };
    }
  };

  // Функция для обновления email
  const updateEmail = async (newEmail) => {
    const token = localStorage.getItem('authToken');
    const userId = getUserIdFromToken();
    
    if (!token || !userId) {
      return { success: false, error: "Требуется авторизация" };
    }
    
    try {
      const response = await fetch(`https://pets.xn--80ahdri7a.site/api/users/${userId}/email`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({ email: newEmail })
      });
      
      if (response.ok) {
        return { success: true };
      } else {
        const errorData = await response.json();
        return { success: false, error: errorData.error?.message || "Ошибка обновления" };
      }
    } catch (err) {
      return { success: false, error: "Ошибка сети" };
    }
  };

  const handleSave = async () => {
    setIsLoading(true);
    setError("");
    setSuccessMessage("");

    const token = localStorage.getItem('authToken');
    if (!token) {
      setError("Токен авторизации отсутствует");
      setIsLoading(false);
      return;
    }

    // Получаем ID пользователя
    const userId = getUserIdFromToken();
    if (!userId) {
      setError("ID пользователя не найден");
      setIsLoading(false);
      return;
    }

    try {
      let updatePromises = [];
      
      // Проверяем, что изменилось и отправляем соответствующие запросы
      const originalUserData = JSON.parse(localStorage.getItem('userData') || '{}');
      
      if (userData.phone !== originalUserData.phone) {
        updatePromises.push(updatePhone(userData.phone));
      }
      
      if (userData.email !== originalUserData.email) {
        updatePromises.push(updateEmail(userData.email));
      }
      
      // Если есть изменения, отправляем запросы
      if (updatePromises.length > 0) {
        const results = await Promise.all(updatePromises);
        const failedUpdates = results.filter(result => !result.success);
        
        if (failedUpdates.length > 0) {
          // Собираем все ошибки
          const errorMessages = failedUpdates.map(f => f.error).join('. ');
          setError(`Ошибки при сохранении: ${errorMessages}`);
          setIsLoading(false);
          return;
        }
        
        // Обновляем данные в localStorage
        localStorage.setItem('userData', JSON.stringify({
          ...originalUserData,
          email: userData.email,
          phone: userData.phone,
          name: userData.name
        }));
        
        // Показываем сообщение об успешном сохранении
        setSuccessMessage("Изменения успешно сохранены!");
        
        // Обновляем данные пользователя
        fetchUserData();
        
        // Отключаем режим редактирования через 2 секунды
        setTimeout(() => {
          setIsEditing(false);
          setSuccessMessage("");
        }, 2000);
      } else {
        setSuccessMessage("Нет изменений для сохранения");
        setTimeout(() => {
          setSuccessMessage("");
          setIsEditing(false);
        }, 2000);
      }
    } catch (err) {
      console.error("Ошибка при сохранении данных:", err);
      setError("Не удалось сохранить изменения");
    } finally {
      setIsLoading(false);
    }
  };

  // Функция для удаления объявления
  const handleDeleteOrder = async (orderId) => {
    if (!window.confirm("Вы уверены, что хотите удалить это объявление?")) {
      return;
    }

    const token = localStorage.getItem('authToken');
    const userId = getUserIdFromToken();
    
    if (!token || !userId) {
      setError("Требуется авторизация");
      return;
    }

    try {
      const response = await fetch(`https://pets.xn--80ahdri7a.site/api/users/${userId}/orders/${orderId}`, {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        }
      });
      
      if (response.ok) {
        // Обновляем список объявлений
        fetchUserOrders();
        setSuccessMessage("Объявление успешно удалено");
        setTimeout(() => setSuccessMessage(""), 3000);
      } else if (response.status === 403) {
        setError("Нет прав для удаления этого объявления");
      } else {
        setError("Ошибка при удалении объявления");
      }
    } catch (err) {
      console.error("Ошибка при удалении объявления:", err);
      setError("Ошибка сети при удалении объявления");
    }
  };

  // Функция для выхода из аккаунта
  const handleLogout = () => {
    // Удаляем токен и данные пользователя из localStorage
    localStorage.removeItem('authToken');
    localStorage.removeItem('userData');
    
    // Показываем сообщение об успешном выходе
    alert("Вы успешно вышли из аккаунта");
    
    // Закрываем модальное окно
    document.querySelector('#myPage .btn-close')?.click();
    
    // Перезагружаем страницу для обновления состояния
    setTimeout(() => {
      window.location.reload();
    }, 100);
  };

  // Если пользователь не авторизован, показываем сообщение
  if (!userInfo?.isLoggedIn) {
    return (
      <div className="modal fade" id="myPage" tabIndex={-1} aria-hidden="true">
        <div className="modal-dialog modal-lg">
          <div className="modal-content">
            <div className="modal-header">
              <h1 className="modal-title fs-5">Личный кабинет</h1>
              <button type="button" className="btn-close" data-bs-dismiss="modal" />
            </div>
            <div className="modal-body">
              <div className="text-center p-4">
                <div className="alert alert-warning" role="alert">
                  <h5 className="alert-heading">Требуется авторизация!</h5>
                  <p>Для доступа к личному кабинету необходимо войти в систему.</p>
                  <hr />
                  <button 
                    className="btn btn-dark"
                    data-bs-dismiss="modal"
                    data-bs-toggle="modal"
                    data-bs-target="#authModal"
                  >
                    Перейти к авторизации
                  </button>
                </div>
              </div>
            </div>
            <div className="modal-footer">
              <button 
                type="button" 
                className="btn btn-danger" 
                data-bs-dismiss="modal"
              >
                Закрыть
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="modal fade" id="myPage" tabIndex={-1} aria-hidden="true">
      <div className="modal-dialog modal-lg">
        <div className="modal-content">
          <div className="modal-header">
            <h1 className="modal-title fs-5">
              Личный кабинет - {userData.name || userInfo.userName || "Пользователь"}
            </h1>
            <button type="button" className="btn-close" data-bs-dismiss="modal" />
          </div>
          <div className="modal-body">
            {/* Уведомления об успехе */}
            {successMessage && (
              <div className="alert alert-success alert-dismissible fade show" role="alert">
                {successMessage}
                <button type="button" className="btn-close" onClick={() => setSuccessMessage("")} />
              </div>
            )}

            {/* Уведомления об ошибках */}
            {error && !successMessage && (
              <div className="alert alert-danger alert-dismissible fade show" role="alert">
                {error}
                <button type="button" className="btn-close" onClick={() => setError("")} />
              </div>
            )}

            {/* Индикатор загрузки */}
            {isFetching ? (
              <div className="text-center p-4">
                <div className="spinner-border text-dark" role="status">
                  <span className="visually-hidden">Загрузка...</span>
                </div>
                <p className="mt-2">Загрузка данных пользователя...</p>
              </div>
            ) : (
              <div className="row">
                <div className="col-md-6">
                  <h5>Данные пользователя</h5>
                  
                  <div className="mb-3">
                    <label className="form-label small">Имя:</label>
                    <input 
                      type="text" 
                      className="form-control" 
                      placeholder="Имя" 
                      value={userData.name}
                      onChange={(e) => setUserData({...userData, name: e.target.value})}
                      disabled={!isEditing || isLoading}
                    />
                  </div>
                  
                  <div className="mb-3">
                    <label className="form-label small">Email:</label>
                    <input 
                      type="email" 
                      className="form-control" 
                      placeholder="email" 
                      value={userData.email}
                      onChange={(e) => setUserData({...userData, email: e.target.value})}
                      disabled={!isEditing || isLoading}
                    />
                  </div>
                  
                  <div className="mb-3">
                    <label className="form-label small">Телефон:</label>
                    <input 
                      type="text" 
                      className="form-control" 
                      placeholder="phone" 
                      value={userData.phone}
                      onChange={(e) => setUserData({...userData, phone: e.target.value})}
                      disabled={!isEditing || isLoading}
                    />
                  </div>
                  
                  <div className="mt-4">
                    <h6>Статистика</h6>
                    <div className="row text-center">
                      <div className="col-4">
                        <div className="card bg-light">
                          <div className="card-body">
                            <h5 className="card-title">{userData.ordersCount}</h5>
                            <p className="card-text small">Объявления</p>
                          </div>
                        </div>
                      </div>
                      <div className="col-4">
                        <div className="card bg-light">
                          <div className="card-body">
                            <h5 className="card-title">{userData.petsCount}</h5>
                            <p className="card-text small">Питомцы с хозяевами</p>
                          </div>
                        </div>
                      </div>
                      <div className="col-4">
                        <div className="card bg-light">
                          <div className="card-body">
                            <h5 className="card-title">{daysSinceRegistration}</h5>
                            <p className="card-text small">Дней на сайте</p>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                  
                  <div className="mt-3">
                    <p className="text-muted small">
                      <strong>ID пользователя:</strong> {userData.id || "Не указан"}<br />
                      <strong>Дата регистрации:</strong> {userData.registrationDate || "Не указана"}<br />
                      <strong>Статус:</strong> Активен<br />
                      <strong>На сайте:</strong> {daysSinceRegistration} дней
                    </p>
                  </div>
                </div>
                
                <div className="col-md-6">
                  <h5>Мои объявления</h5>
                  
                  {isFetchingOrders ? (
                    <div className="text-center p-3">
                      <div className="spinner-border spinner-border-sm text-secondary" role="status">
                        <span className="visually-hidden">Загрузка...</span>
                      </div>
                      <p className="mt-2 small">Загрузка объявлений...</p>
                    </div>
                  ) : userOrders.length > 0 ? (
                    <div className="list-group" style={{ maxHeight: "300px", overflowY: "auto" }}>
                      {userOrders.map(order => (
                        <div key={order.id} className="list-group-item">
                          <div className="d-flex justify-content-between align-items-center">
                            <div style={{ maxWidth: "70%" }}>
                              <strong>{order.kind || "Животное"}</strong>
                              <br />
                              <small className="text-muted d-block">{order.description?.substring(0, 50)}...</small>
                              <small className="text-muted">{order.district || "Район не указан"}</small>
                            </div>
                            <div className="d-flex flex-column align-items-end">
                              <span className="badge bg-secondary mb-1">{order.date}</span>
                              <button 
                                className="btn btn-sm btn-outline-danger mt-1"
                                onClick={() => handleDeleteOrder(order.id)}
                              >
                                <i className="bi bi-trash"></i>
                              </button>
                            </div>
                          </div>
                        </div>
                      ))}
                    </div>
                  ) : (
                    <div className="text-center p-4">
                      <p className="text-muted">У вас еще нет объявлений</p>
                      <button 
                        className="btn btn-sm btn-outline-dark" 
                        data-bs-dismiss="modal"
                        data-bs-toggle="modal" 
                        data-bs-target="#newPost"
                        onClick={() => document.querySelector('#myPage .btn-close')?.click()}
                      >
                        Создать объявление
                      </button>
                    </div>
                  )}
                  
                  {/* Кнопка выхода */}
                  <div className="mt-4 pt-3 border-top">
                    <button 
                      className="btn btn-outline-danger w-100"
                      onClick={handleLogout}
                      disabled={isLoading}
                    >
                      Выйти из аккаунта
                    </button>
                    <small className="text-muted d-block mt-2">
                      При выходе из аккаунта все несохраненные данные будут утеряны.
                    </small>
                  </div>
                </div>
              </div>
            )}
          </div>
          <div className="modal-footer">
            <button 
              type="button" 
              className="btn btn-danger" 
              data-bs-dismiss="modal"
              disabled={isLoading}
            >
              Закрыть
            </button>
            
            <button 
              type="button" 
              className="btn btn-warning"
              onClick={() => {
                if (isEditing) {
                  // Если отменяем редактирование, загружаем исходные данные
                  fetchUserData();
                  setError("");
                  setSuccessMessage("");
                }
                setIsEditing(!isEditing);
              }}
              disabled={isLoading || isFetching}
            >
              {isEditing ? "Отменить" : "Редактировать"}
            </button>
            
            <button 
              type="button" 
              className="btn btn-dark"
              onClick={handleSave}
              disabled={!isEditing || isLoading || isFetching}
            >
              {isLoading ? (
                <>
                  <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span>
                  Сохранение...
                </>
              ) : "Сохранить"}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Profile;
