import React, { useState, useEffect } from "react";
import image2 from './image/image2.jfif';
import image3 from './image/image3.jfif';
import image from './image/image.jfif';
import Newsletter from './Newsletter';
import Modal_Cat from './cat_card';

function Content({ onNewsletterSubmit, showNewsletter, isLoading }) {
  const [animals, setAnimals] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedPetId, setSelectedPetId] = useState(null);
  const [showModal, setShowModal] = useState(false);

  // Загружаем данные с API
  useEffect(() => {
    fetchAnimals();
  }, []);

  const fetchAnimals = async () => {
    try {
      setLoading(true);
      const response = await fetch('https://pets.xn--80ahdri7a.site/api/pets', {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json'
        }
      });

      if (!response.ok) {
        throw new Error('Ошибка при загрузке данных');
      }

      const data = await response.json();

      // Преобразуем данные API в формат нашего приложения
      const apiAnimals = data.data.orders
        .filter(animal => animal.registred) // Только зарегистрированные
        .map(animal => ({
          id: animal.id,
          title: `${animal.kind}, найдено ${formatDate(animal.date)}`,
          description: animal.description,
          image: animal.photo ? `https://pets.xn--80ahdri7a.site${animal.photo}` : getDefaultImageByKind(animal.kind),
          date: formatDateForSort(animal.date),
          type: getAnimalTypeFromKind(animal.kind),
          age: "неизвестно",
          location: animal.district || "Не указан",
          phone: animal.phone,
          name: animal.name,
          mark: animal.mark,
          photo: animal.photo // сохраняем оригинальный путь
        }))
        .sort((a, b) => new Date(b.date) - new Date(a.date)) // Сортировка по убыванию даты
        .slice(0, 3); // Берем только 3 последних

      setAnimals(apiAnimals);
    } catch (error) {
      console.error('Ошибка при загрузке животных:', error);
      setError('Не удалось загрузить данные о животных');
    } finally {
      setLoading(false);
    }
  };

  // Функция для получения типа животного (cat/dog/rabbit/other) из kind
  const getAnimalTypeFromKind = (kind) => {
    const kindLower = kind.toLowerCase();
    if (kindLower.includes('кошка') || kindLower.includes('кот')) return 'cat';
    if (kindLower.includes('собака')) return 'dog';
    if (kindLower.includes('кролик')) return 'rabbit';
    return 'other';
  };

  // Функция для получения изображения по умолчанию в зависимости от типа
  const getDefaultImageByKind = (kind) => {
    const type = getAnimalTypeFromKind(kind);
    switch(type) {
      case 'cat': return image;
      case 'dog': return image2;
      case 'rabbit': return image3;
      default: return image;
    }
  };

  // Форматирование даты для отображения
  const formatDate = (dateString) => {
    const [day, month, year] = dateString.split('-');
    return `${day}.${month}.${year}`;
  };

  // Форматирование даты для сортировки
  const formatDateForSort = (dateString) => {
    const [day, month, year] = dateString.split('-');
    return `${year}-${month}-${day}`;
  };

  // Функция для открытия модального окна с выбранным питомцем
  const handleOpenModal = (petId) => {
    setSelectedPetId(petId);
    setShowModal(true);
  };

  // Функция для закрытия модального окна
  const handleCloseModal = () => {
    setShowModal(false);
    setSelectedPetId(null);
  };

  if (loading) {
    return (
      <div className="container mt-5 text-center">
        <div className="spinner-border text-dark" role="status">
          <span className="visually-hidden">Загрузка...</span>
        </div>
        <p className="mt-2">Загрузка последних объявлений...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container mt-5">
        <div className="alert alert-danger" role="alert">
          {error}
        </div>
      </div>
    );
  }

  return (
    <div>
      {/* Модальное окно */}
      {showModal && selectedPetId && (
        <Modal_Cat 
          petId={selectedPetId}
          onClose={handleCloseModal}
        />
      )}

      <div className="container mt-5">
        <div className="row mb-5">
          <div className="col-12 text-center">
            <h2>Последние найденные животные</h2>
            <p className="lead">
              Всего животных: {animals.length}
            </p>
          </div>
        </div>

        <div className="row mb-5">
          {animals.map(animal => (
            <div key={animal.id} className="col-md-4 mb-4">
              <div className="card animal-card h-100 d-flex flex-column">
                <img 
                  src={animal.image} 
                  className="card-img-top" 
                  alt={animal.type}
                  style={{
                    width: '100%',
                    height: '300px',
                    objectFit: 'cover'
                  }}
                  onError={(e) => {
                    e.target.src = getDefaultImageByKind(animal.type);
                  }}
                />
                <div className="card-body d-flex flex-column">
                  <h5 className="card-title">
                    {animal.title}
                    {animal.location && (
                      <small className="d-block text-muted">
                        <i className="bi bi-geo-alt me-1"></i>
                        {animal.location}
                      </small>
                    )}
                  </h5>

                  <p className="card-text flex-grow-1">{animal.description}</p>

                  <div className="mb-2">
                    <span className="badge bg-secondary me-2">
                      <i className="bi bi-calendar me-1"></i>
                      {formatDate(animal.date)}
                    </span>
                    <span className={`badge ${
                      animal.type === 'cat' ? 'bg-info' :
                      animal.type === 'dog' ? 'bg-warning text-dark' :
                      animal.type === 'rabbit' ? 'bg-purple' : 'bg-secondary'
                    }`}>
                      {animal.type === 'cat' ? 'кошка/кот' : 
                       animal.type === 'dog' ? 'собака' : 
                       animal.type === 'rabbit' ? 'кролик' : 'другое'}
                    </span>
                    {animal.age && (
                      <span className="badge bg-dark ms-2">
                        <i className="bi bi-clock-history me-1"></i>
                        {animal.age}
                      </span>
                    )}
                  </div>

                  <div className="mt-auto">
                    <button 
                      className="btn btn-dark me-3" 
                      onClick={() => handleOpenModal(animal.id)}
                    >
                      Подробнее
                    </button>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* Призыв к действию */}
        <div className="row mb-5">
          <div className="col-12">
            <div className="jumbotron jumbotron-fluid text-center rounded bg-dark text-white p-5">
              <div className="container">
                <h1 className="display-5">Помогите животным найти дом!</h1>
                <p className="lead">
                  Если вы нашли животное или готовы приютить одного из них, заполните форму находки или свяжитесь с нами.
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>

      {showNewsletter && (
        <Newsletter 
          onNewsletterSubmit={onNewsletterSubmit}
          isLoading={isLoading}
        />
      )}

      <style>{`
        .bg-purple {
