import React, { useState, useEffect } from 'react';
import { Routes, Route } from 'react-router-dom';
import Home from './components/Home';
import Find_Animals from './components/Find_Animals';
import Header from './components/header';
import Footer from './components/footer';
import AuthModal from './components/AuthModal';

function App() {
  const siteInfo = {
    title: "WebPets",
    year: 2025,
    email: "helpAnimal@mail.ru",
    phone: "+7 (931) 562-231-32"
  };

  // Восстанавливаем состояние из localStorage при загрузке
  const [userInfo, setUserInfo] = useState(() => {
    const savedUserInfo = localStorage.getItem('userInfo');
    return savedUserInfo ? JSON.parse(savedUserInfo) : {
      isLoggedIn: false,
      userName: "Гость",
      email: "",
      phone: "",
      token: null
    };
  });

  // Сохраняем состояние в localStorage при изменении
  useEffect(() => {
    localStorage.setItem('userInfo', JSON.stringify(userInfo));
  }, [userInfo]);

  // Функция для входа - теперь сохраняем токен
  const handleLogin = async (token) => {
    try {
      // Получаем данные пользователя с сервера
      const response = await fetch('https://pets.xn--80ahdri7a.site/api/users', {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.ok) {
        const userData = await response.json();

        const newUserInfo = {
          isLoggedIn: true,
          userName: userData.name || "Пользователь",
          email: userData.email,
          phone: userData.phone,
          token: token,
          userData: userData // сохраняем полные данные пользователя
        };

        setUserInfo(newUserInfo);

        // Также сохраняем токен отдельно для удобства
        localStorage.setItem('authToken', token);

        return true;
      } else {
        console.error('Ошибка получения данных пользователя');
        return false;
      }
    } catch (error) {
      console.error('Ошибка при получении данных пользователя:', error);
      return false;
    }
  };

  // Функция для выхода
  const handleLogout = () => {
    // Очищаем localStorage
    localStorage.removeItem('userInfo');
    localStorage.removeItem('authToken');
    localStorage.removeItem('userEmail');

    setUserInfo({
      isLoggedIn: false,
      userName: "Гость",
      email: "",
      phone: "",
      token: null,
      userData: null
    });
  };

  // Проверяем токен при загрузке приложения
  useEffect(() => {
    const checkAuthStatus = async () => {
      const token = localStorage.getItem('authToken');

      if (token && !userInfo.isLoggedIn) {
        // Пробуем обновить данные пользователя
        try {
          const response = await fetch('https://pets.xn--80ahdri7a.site/api/users', {
            method: 'GET',
            headers: {
              'Content-Type': 'application/json',
              'Authorization': `Bearer ${token}`
            }
          });

          if (response.ok) {
            const userData = await response.json();

            setUserInfo({
              isLoggedIn: true,
              userName: userData.name || "Пользователь",
              email: userData.email,
              phone: userData.phone,
              token: token,
              userData: userData
            });
          } else {
            // Если токен невалидный, разлогиниваем
            handleLogout();
          }
        } catch (error) {
          console.error('Ошибка проверки авторизации:', error);
        }
      }
    };

    checkAuthStatus();
  }, []);

  return (
    <div>
      <Header 
        siteTitle={siteInfo.title} 
        userInfo={userInfo}
        onLogout={handleLogout}
      />
      <Routes>
        <Route 
          path={'/'} 
          element={
            <Home 
              siteInfo={siteInfo}
              userInfo={userInfo}
              onLogin={handleLogin}
              onLogout={handleLogout}
              />
          }
        />
        <Route 
          path={'/find_animals'} 
          element={
            <Find_Animals 
              siteInfo={siteInfo}
              userInfo={userInfo}
              onLogin={handleLogin}
              onLogout={handleLogout}
            />
          }
        />
      </Routes>
      <Footer 
        siteInfo={siteInfo}
      />
    </div>
  );
}

export default App;
