import React, { useState } from "react";

import Icon2 from './image/icon/mail.ico';
import Icon3 from './image/icon/z.ico';
import RegistrationForm from "./register";

function AuthModal({ userInfo, onLogin, onRegister }) {
  const [isLoginMode, setIsLoginMode] = useState(true);
  const [loginData, setLoginData] = useState({
    email: "",
    password: ""
  });
  const [registerData, setRegisterData] = useState({
    name: "",
    phone: "",
    email: "",
    password: "",
    confirmPassword: "",
    agreeToTerms: false
  });
  const [errors, setErrors] = useState({});
  const [authError, setAuthError] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [successMessage, setSuccessMessage] = useState("");

  // Валидация формы входа
  const validateLogin = () => {
    const newErrors = {};
    if (!loginData.email.trim()) newErrors.loginEmail = "Email обязателен";
    else if (!loginData.email.includes('@')) newErrors.loginEmail = "Некорректный email";
    
    if (!loginData.password.trim()) newErrors.loginPassword = "Пароль обязателен";
    else if (loginData.password.length < 6) newErrors.loginPassword = "Пароль должен быть не менее 6 символов";
    
    return newErrors;
  };

  // Обработка входа
  const handleLogin = async () => {
    setAuthError("");
    setSuccessMessage("");
    const validationErrors = validateLogin();
    
    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors);
      return;
    }

    setIsLoading(true);
    
    try {
      const response = await fetch('https://pets.xn--80ahdri7a.site/api/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          email: loginData.email,
          password: loginData.password
        })
      });

      const data = await response.json();

      if (response.ok) {
        // Успешная аутентификация
        const mockUser = {
          name: "Иван Петров",
          email: loginData.email,
          phone: "+7 (999) 123-45-67",
          token: data.data.token
        };
        onLogin(mockUser);
        setSuccessMessage("Вход выполнен успешно!");
        
        // Закрываем модальное окно через 1.5 секунды
        setTimeout(() => {
          document.getElementById('closeAuthModal')?.click();
          // Сбрасываем сообщение при закрытии
          setSuccessMessage("");
          setAuthError("");
        }, 1500);
      } else {
        // Обработка ошибок
        if (response.status === 422) {
          // Ошибки валидации
          if (data.error && data.error.errors) {
            const validationErrors = {};
            Object.keys(data.error.errors).forEach(key => {
              if (key === 'email' || key === 'password') {
                validationErrors[`login${key.charAt(0).toUpperCase() + key.slice(1)}`] = 
                  data.error.errors[key][0];
              }
            });
            setErrors(validationErrors);
          } else {
            setAuthError("Ошибка валидации данных");
          }
        } else if (response.status === 401) {
          // Неавторизован
          setAuthError("Неверный email или пароль");
        } else {
          // Другие ошибки
          setAuthError(data.error?.message || "Произошла ошибка при входе");
        }
      }
    } catch (error) {
      console.error("Login error:", error);
      setAuthError("Ошибка соединения с сервером");
    } finally {
      setIsLoading(false);
    }
  };

  // Обработка регистрации
  const handleRegister = () => {
    const validationErrors = validateRegister();
    if (Object.keys(validationErrors).length === 0) {
      // Имитация успешной регистрации
      onRegister(registerData);
      setSuccessMessage("Регистрация успешна! Теперь вы можете войти.");
      
      // Переключаемся на вход через 1.5 секунды
      setTimeout(() => {
        setIsLoginMode(true);
        setSuccessMessage("");
        setRegisterData({
          name: "",
          phone: "",
          email: "",
          password: "",
          confirmPassword: "",
          agreeToTerms: false
        });
      }, 1500);
    } else {
      setErrors(validationErrors);
    }
  };

  const handleChangeLogin = (field, value) => {
    setLoginData(prev => ({ ...prev, [field]: value }));
    // Очищаем ошибки при изменении поля
    if (errors[`login${field.charAt(0).toUpperCase() + field.slice(1)}`] || authError) {
      setErrors(prev => {
        const newErrors = { ...prev };
        delete newErrors[`login${field.charAt(0).toUpperCase() + field.slice(1)}`];
        return newErrors;
      });
      setAuthError("");
    }
  };

  const handleChangeRegister = (field, value) => {
    setRegisterData(prev => ({ ...prev, [field]: value }));
    if (errors[field]) {
      setErrors(prev => ({ ...prev, [field]: null }));
    }
  };

  return (
    <div className="modal fade" id="authModal" tabIndex={-1} aria-labelledby="authModalLabel" aria-hidden="true">
      <div className="modal-dialog">
        <div className="modal-content">
          <div className="modal-header">
            <h1 className="modal-title fs-5" id="authModalLabel">
              {isLoginMode ? "Вход в аккаунт" : "Регистрация"}
            </h1>
            <button 
              id="closeAuthModal"
              type="button" 
              className="btn-close" 
              data-bs-dismiss="modal" 
              aria-label="Закрыть"
              onClick={() => {
                setAuthError("");
                setSuccessMessage("");
              }}
            />
          </div>
          
          <div className="modal-body">
            {/* Переключение между входом и регистрацией */}
            <div className="mb-4">
              <div className="btn-group w-100" role="group">
                <button
                  type="button"
                  className={`btn ${isLoginMode ? 'btn-dark' : 'btn-outline-dark'}`}
                  onClick={() => {
                    setIsLoginMode(true);
                    setAuthError("");
                    setSuccessMessage("");
                  }}
                >
                  Вход
                </button>
                <button
                  type="button"
                  className={`btn ${!isLoginMode ? 'btn-dark' : 'btn-outline-dark'}`}
                  onClick={() => {
                    setIsLoginMode(false);
                    setAuthError("");
                    setSuccessMessage("");
                  }}
                >
                  Регистрация
                </button>
              </div>
            </div>

            {/* Сообщения об ошибках и успехе */}
            {authError && (
              <div className="alert alert-danger alert-dismissible fade show" role="alert">
                {authError}
                <button 
                  type="button" 
                  className="btn-close" 
                  onClick={() => setAuthError("")}
                />
              </div>
            )}

            {successMessage && (
              <div className="alert alert-success alert-dismissible fade show" role="alert">
                {successMessage}
                <button 
                  type="button" 
                  className="btn-close" 
                  onClick={() => setSuccessMessage("")}
                />
              </div>
            )}

            {/* Форма входа */}
            {isLoginMode ? (
              <div>
                <div className="input-group flex-nowrap mb-2">
                  <span className="input-group-text"><img src={Icon2} alt="Email" /></span>
                  <input 
                    type="email" 
                    className={`form-control ${errors.loginEmail ? 'is-invalid' : ''}`}
                    placeholder="Email" 
                    value={loginData.email}
                    onChange={(e) => handleChangeLogin('email', e.target.value)}
                    disabled={isLoading}
                  />
                  {errors.loginEmail && <div className="invalid-feedback">{errors.loginEmail}</div>}
                </div>
                
                <div className="input-group flex-nowrap mb-2">
                  <span className="input-group-text"><img src={Icon3} alt="Пароль" /></span>
                  <input 
                    type="password" 
                    className={`form-control ${errors.loginPassword ? 'is-invalid' : ''}`}
                    placeholder="Пароль" 
                    value={loginData.password}
                    onChange={(e) => handleChangeLogin('password', e.target.value)}
                    disabled={isLoading}
                  />
                  {errors.loginPassword && <div className="invalid-feedback">{errors.loginPassword}</div>}
                </div>
                
                <div className="form-text mb-3">
                  Тестовый аккаунт: test@example.com / password123
                </div>
              </div>
            ) : (
              <RegistrationForm/>
            )}
          </div>
          
          <div className="modal-footer">
            <button 
              type="button" 
              className="btn btn-danger" 
              data-bs-dismiss="modal"
              onClick={() => {
                setAuthError("");
                setSuccessMessage("");
              }}
            >
              Закрыть
            </button>
            <button 
              type="button" 
              className="btn btn-dark"
              onClick={isLoginMode ? handleLogin : handleRegister}
              disabled={isLoading}
            >
              {isLoading ? (
                <>
                  <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span>
                  Загрузка...
                </>
              ) : (
                isLoginMode ? "Войти" : "Зарегистрироваться"
              )}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default AuthModal;
