// profile.jsx
import React, { useEffect, useState } from "react";

function Profile({ userInfo }) {
  const [userData, setUserData] = useState({
    email: userInfo?.userData?.email || "",
    phone: userInfo?.userData?.phone || "",
    name: userInfo?.userData?.name || ""
  });
  const [isEditing, setIsEditing] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [isLoadingOrders, setIsLoadingOrders] = useState(false);
  const [userOrders, setUserOrders] = useState([]);
  const [apiMessage, setApiMessage] = useState({ type: '', text: '' });

  const host = 'https://pets.xn--80ahdri7a.site';
  const userId = userInfo?.userData?.id;

  useEffect(() => {
    if (userInfo?.isLoggedIn) {
      getUserData();
      getUserOrders();
    }
  }, [userInfo?.isLoggedIn]);

  async function getUserData() {
    const url = `${host}/api/users`;
    const token = localStorage.getItem('authToken');

    if (!token) return;

    try {
      const response = await fetch(url, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.ok) {
        const result = await response.json();
        setUserData(result);
      } else if (response.status === 401) {
        console.log("Неавторизован");
      }
    } catch (error) {
      console.log(error);
    }
  }

  async function getUserOrders() {
    if (!userId) return;

    const url = `${host}/api/users/orders`;
    const token = localStorage.getItem('authToken');

    if (!token) return;

    setIsLoadingOrders(true);
    try {
      const response = await fetch(url, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.ok) {
        const result = await response.json();
        if (result.data && result.data.orders) {
          setUserOrders(result.data.orders);
        }
      } else if (response.status === 204) {
        setUserOrders([]);
      } else if (response.status === 401) {
        console.log("Неавторизован");
      }
    } catch (error) {
      console.log(error);
    } finally {
      setIsLoadingOrders(false);
    }
  }

  const updatePhone = async (newPhone) => {
    const url = `${host}/api/users/phone`;
    const token = localStorage.getItem('authToken');

    if (!token) {
      setApiMessage({ type: 'error', text: 'Требуется авторизация' });
      return false;
    }

    if (!newPhone || newPhone.trim() === '') {
      setApiMessage({ type: 'error', text: 'Телефон обязателен для заполнения' });
      return false;
    }

    try {
      const response = await fetch(url, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({ phone: newPhone })
      });

      if (response.ok) {
        const result = await response.json();
        setApiMessage({ type: 'success', text: 'Номер телефона успешно обновлен' });
        return true;
      } else if (response.status === 422) {
        const result = await response.json();
        setApiMessage({ 
          type: 'error', 
          text: result.error?.error || 'Ошибка валидации номера телефона' 
        });
        return false;
      } else if (response.status === 401) {
        setApiMessage({ type: 'error', text: 'Требуется авторизация' });
        return false;
      } else {
        setApiMessage({ type: 'error', text: 'Ошибка сервера' });
        return false;
      }
    } catch (error) {
      console.error(error);
      setApiMessage({ type: 'error', text: 'Ошибка сети' });
      return false;
    }
  };

  const updateEmail = async (newEmail) => {
    const url = `${host}/api/users/email`;
    const token = localStorage.getItem('authToken');

    if (!token) {
      setApiMessage({ type: 'error', text: 'Требуется авторизация' });
      return false;
    }

    if (!newEmail || newEmail.trim() === '') {
      setApiMessage({ type: 'error', text: 'Email обязателен для заполнения' });
      return false;
    }

    // Базовая валидация email на клиенте
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(newEmail)) {
      setApiMessage({ type: 'error', text: 'Некорректный формат email' });
      return false;
    }

    try {
      const response = await fetch(url, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({ email: newEmail })
      });

      if (response.ok) {
        const result = await response.json();
        setApiMessage({ type: 'success', text: 'Email успешно обновлен' });

        // Обновляем email в localStorage
        localStorage.setItem('userEmail', newEmail);

        return true;
      } else if (response.status === 422) {
        const result = await response.json();
        setApiMessage({ 
          type: 'error', 
          text: result.error?.error || 'Ошибка валидации email' 
        });
        return false;
      } else if (response.status === 401) {
        setApiMessage({ type: 'error', text: 'Требуется авторизация' });
        return false;
      } else {
        setApiMessage({ type: 'error', text: 'Ошибка сервера' });
        return false;
      }
    } catch (error) {
      console.error(error);
      setApiMessage({ type: 'error', text: 'Ошибка сети' });
      return false;
    }
  };

  const handleSave = async () => {
    setIsLoading(true);
    setApiMessage({ type: '', text: '' });

    let phoneUpdated = true;
    let emailUpdated = true;

    // Обновляем телефон, если он изменился
    if (userData.phone !== userInfo?.userData?.phone) {
      phoneUpdated = await updatePhone(userData.phone);
    }

    // Обновляем email, если он изменился
    if (userData.email !== userInfo?.userData?.email) {
      emailUpdated = await updateEmail(userData.email);
    }

    // Если оба обновления успешны или ничего не менялось
    if (phoneUpdated && emailUpdated) {
      // Обновляем данные пользователя
      await getUserData();

      if (userData.phone === userInfo?.userData?.phone && 
          userData.email === userInfo?.userData?.email) {
        setApiMessage({ type: 'info', text: 'Изменений не обнаружено' });
      } else {
        setApiMessage({ type: 'success', text: 'Изменения сохранены успешно!' });
      }

      setTimeout(() => {
        setIsEditing(false);
        setIsLoading(false);
      }, 1000);
    } else {
      setIsLoading(false);
    }
  };

  const handleCancel = () => {
    // Восстанавливаем исходные данные
    setUserData({
      email: userInfo?.userData?.email || "",
      phone: userInfo?.userData?.phone || "",
      name: userInfo?.userData?.name || ""
    });
    setIsEditing(false);
    setApiMessage({ type: '', text: '' });
  };

  const getStatusBadgeClass = (status) => {
    switch (status) {
      case 'active':
        return 'badge bg-success';
      case 'wasFound':
        return 'badge bg-primary';
      case 'onModeration':
        return 'badge bg-warning';
      case 'archive':
        return 'badge bg-secondary';
      default:
        return 'badge bg-light text-dark';
    }
  };

  const getStatusText = (status) => {
    switch (status) {
      case 'active':
        return 'Активное';
      case 'wasFound':
        return 'Хозяин найден';
      case 'onModeration':
        return 'На модерации';
      case 'archive':
        return 'В архиве';
      default:
        return status;
    }
  };

  return (
    <div className="modal fade" id="myPage" tabIndex={-1} aria-hidden="true">
      <div className="modal-dialog modal-lg">
        <div className="modal-content">
          <div className="modal-header">
            <h1 className="modal-title fs-5">
              {userInfo?.isLoggedIn ? `Личный кабинет - ${userInfo.userName}` : "Личный кабинет"}
            </h1>
            <button type="button" className="btn-close" data-bs-dismiss="modal" />
          </div>
          <div className="modal-body">
            {!userInfo?.isLoggedIn ? (
              <div className="text-center p-4">
                <p>Для доступа к личному кабинету необходимо войти в систему.</p>
              </div>
            ) : (
              <>
                {/* Сообщения API */}
                {apiMessage.text && (
                  <div className={`alert alert-${apiMessage.type === 'success' ? 'success' : apiMessage.type === 'error' ? 'danger' : 'info'} mb-3`}>
                    {apiMessage.text}
                  </div>
                )}

                <div className="row">
                  <div className="col-md-6">
                    <h5>Данные пользователя</h5>
                    <div className="mb-3">
                      <label className="form-label small">Имя:</label>
                      <input 
                        type="text" 
                        className="form-control" 
                        placeholder="Имя" 
                        value={userData.name}
                        onChange={(e) => setUserData({...userData, name: e.target.value})}
                        disabled={!isEditing || isLoading}
                      />
                      <small className="text-muted">Изменить имя можно только при регистрации</small>
                    </div>
                    <div className="mb-3">
                      <label className="form-label small">Email:</label>
                      <input 
                        type="email" 
                        className="form-control" 
                        placeholder="email" 
                        value={userData.email}
                        onChange={(e) => setUserData({...userData, email: e.target.value})}
                        disabled={!isEditing || isLoading}
                      />
                      {isEditing && <small className="text-muted">Обязательное поле, должен быть валидным email</small>}
                    </div>
                    <div className="mb-3">
                      <label className="form-label small">Телефон:</label>
                      <input 
                        type="text" 
                        className="form-control" 
                        placeholder="+79112345678" 
                        value={userData.phone}
                        onChange={(e) => setUserData({...userData, phone: e.target.value})}
                        disabled={!isEditing || isLoading}
                      />
                      {isEditing && <small className="text-muted">Обязательное поле</small>}
                    </div>

                    <div className="mt-3">
                      <p className="text-muted small">
                        <strong>ID пользователя:</strong> <br />
                        <strong>Статус:</strong> Активен
                      </p>
                    </div>
                  </div>

                  <div className="col-md-6">
                    <h5>Мои объявления</h5>

                    {isLoadingOrders ? (
                      <div className="text-center p-4">
                        <div className="spinner-border text-dark" role="status">
                          <span className="visually-hidden">Загрузка...</span>
                        </div>
                        <p className="mt-2">Загрузка объявлений...</p>
                      </div>
                    ) : userOrders.length === 0 ? (
                      <div className="text-center p-4">
                        <p className="text-muted">У вас еще нет объявлений</p>
                        <button 
                          className="btn btn-sm btn-outline-dark" 
                          data-bs-toggle="modal" 
                          data-bs-target="#newPost"
                          onClick={() => document.querySelector('#myPage .btn-close')?.click()}
                        >
                          Создать объявление
                        </button>
                      </div>
                    ) : (
                      <div className="orders-list">
                        {userOrders.map((order) => (
                          <div key={order.id} className="card mb-3">
                            {order.photos && (
                              <img 
                                src={order.photos} 
                                className="card-img-top" 
                                alt={order.kind || 'Объявление'}
                                style={{ height: '150px', objectFit: 'cover' }}
                              />
                            )}
                            <div className="card-body">
                              <div className="d-flex justify-content-between align-items-start">
                                <h6 className="card-title mb-1">{order.kind || 'Животное'}</h6>
                                <span className={getStatusBadgeClass(order.status)}>
                                  {getStatusText(order.status)}
                                </span>
                              </div>
                              <p className="card-text small mb-1">
                                {order.description?.substring(0, 100)}
                                {order.description?.length > 100 ? '...' : ''}
                              </p>
                              <div className="row small text-muted mt-2">
                                <div className="col-6">
                                  <strong>Метка:</strong> {order.mark || 'Не указана'}
                                </div>
                                <div className="col-6">
                                  <strong>Район:</strong> {order.district || 'Не указан'}
                                </div>
                                <div className="col-12 mt-1">
                                  <strong>Дата:</strong> {order.date || 'Не указана'}
                                </div>
                              </div>
                            </div>
                          </div>
                        ))}
                        <div className="text-center mt-3">
                          <button 
                            className="btn btn-sm btn-outline-dark" 
                            data-bs-toggle="modal" 
                            data-bs-target="#newPost"
                            onClick={() => document.querySelector('#myPage .btn-close')?.click()}
                          >
                            Создать новое объявление
                          </button>
                        </div>
                      </div>
                    )}
                  </div>
                </div>
              </>
            )}
          </div>
          <div className="modal-footer">
            <button 
              type="button" 
              className="btn btn-danger" 
              data-bs-dismiss="modal"
              disabled={isLoading}
              onClick={() => {
                handleCancel();
                setApiMessage({ type: '', text: '' });
              }}
            >
              Закрыть
            </button>
            {userInfo?.isLoggedIn && (
              <>
                <button 
                  type="button" 
                  className="btn btn-warning"
                  onClick={() => isEditing ? handleCancel() : setIsEditing(true)}
                  disabled={isLoading}
                >
                  {isEditing ? "Отменить" : "Редактировать"}
                </button>
                <button 
                  type="button" 
                  className="btn btn-dark"
                  onClick={handleSave}
                  disabled={!isEditing || isLoading}
                >
                  {isLoading ? "Сохранение..." : "Сохранить"}
                </button>
              </>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Profile;
