import React, { useState, useEffect } from "react";
import image1 from './image/image1.jfif';
import image2 from './image/image2.jfif';
import image3 from './image/image3.jfif';
import './css/style.css';

function Slider() {
  const [slides] = useState([
    {
      id: 1,
      image: image1,
      title: "Найден кот в районе парка",
      description: "Полосатый кот, найден 15 октября в Центральном парке. Очень ласковый, ищет хозяев.",
      animalType: "cat"
    },
    {
      id: 2,
      image: image2,
      title: "Найдена собака породы мопс",
      description: "Собака найдена в районе ул. Ленина, 12 октября. Очень активная, знает команды.",
      animalType: "dog"
    },
    {
      id: 3,
      image: image3,
      title: "Найден кролик в центре города",
      description: "Пушистый кролик, найден 10 октября в центре города.",
      animalType: "rabbit"
    }
  ]);

  const [currentSlide, setCurrentSlide] = useState(0);
  const [autoPlay, setAutoPlay] = useState(true);

  useEffect(() => {
    let interval;
    if (autoPlay) {
      interval = setInterval(() => {
        setCurrentSlide((prev) => (prev + 1) % slides.length);
      }, 5000);
    }
    return () => clearInterval(interval);
  }, [autoPlay, slides.length]);

  const goToSlide = (index) => {
    setCurrentSlide(index);
    setAutoPlay(false);
    setTimeout(() => setAutoPlay(true), 10000);
  };

  const nextSlide = () => {
    setCurrentSlide((prev) => (prev + 1) % slides.length);
    setAutoPlay(false);
    setTimeout(() => setAutoPlay(true), 10000);
  };

  const prevSlide = () => {
    setCurrentSlide((prev) => (prev - 1 + slides.length) % slides.length);
    setAutoPlay(false);
    setTimeout(() => setAutoPlay(true), 10000);
  };

  return (
    <div id="animalCarousel" className="carousel slide" data-bs-ride="carousel">
      <div className="carousel-indicators">
        {slides.map((slide, index) => (
          <button 
            key={slide.id}
            type="button" 
            data-bs-target="#animalCarousel" 
            data-bs-slide-to={index}
            className={index === currentSlide ? "active" : ""}
            aria-current={index === currentSlide ? "true" : "false"}
            aria-label={`Слайд ${index + 1}`}
            onClick={() => goToSlide(index)}
          />
        ))}
      </div>

      <div className="carousel-inner">
        {slides.map((slide, index) => (
          <div 
            key={slide.id}
            className={`carousel-item ${index === currentSlide ? "active" : ""}`}
          >
            <img 
              src={slide.image} 
              className="d-block w-100" 
              alt={slide.title}
              style={{ height: "500px", objectFit: "cover" }}
            />
            <div className="carousel-caption d-none d-md-block bg-dark bg-opacity-50 rounded p-3">
              <h5>{slide.title}</h5>
              <p>{slide.description}</p>
              <span className="badge bg-light text-dark">
                {slide.animalType === "cat" ? "Кошка" : 
                 slide.animalType === "dog" ? "Собака" : "Кролик"}
              </span>
            </div>
          </div>
        ))}
      </div>

      <button className="carousel-control-prev" type="button" onClick={prevSlide}>
        <span className="carousel-control-prev-icon" aria-hidden="true" />
        <span className="visually-hidden">Предыдущий</span>
      </button>
      <button className="carousel-control-next" type="button" onClick={nextSlide}>
        <span className="carousel-control-next-icon" aria-hidden="true" />
        <span className="visually-hidden">Следующий</span>
      </button>
    </div>
  );
}

export default Slider;
