import React, { useState, useEffect } from 'react';
import cat from './image/image.jfif';
import './css/card-style.css';

function Modal_Cat() {
  const [animalInfo, setAnimalInfo] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [isInterested, setIsInterested] = useState(false);
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  // Загрузка данных животного при открытии модального окна
  useEffect(() => {
    const loadAnimalData = async () => {
      // Получаем ID животного из localStorage или других источников
      const storedAnimal = localStorage.getItem('selectedAnimal');
      let animalId = null;
      
      if (storedAnimal) {
        try {
          const animal = JSON.parse(storedAnimal);
          animalId = animal.id;
        } catch (err) {
          console.error('Ошибка парсинга животного:', err);
        }
      }
      
      if (!animalId) return;
      
      setLoading(true);
      setError('');
      
      try {
        const response = await fetch(`https://pets.xn--80ahdri7a.site/api/pets/${animalId}`, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json'
          }
        });
        
        if (!response.ok) {
          if (response.status === 204) {
            throw new Error('Животное не найдено');
          }
          throw new Error(`Ошибка сервера: ${response.status}`);
        }
        
        const data = await response.json();
        
        if (data.data && data.data.pet && data.data.pet.length > 0) {
          const pet = data.data.pet[0];
          setAnimalInfo({
            name: pet.name || "Безымянный",
            location: pet.district || "Место не указано",
            description: pet.description || "Описание отсутствует",
            age: "возраст не указан",
            gender: "не определен",
            features: pet.mark ? `Клеймо: ${pet.mark}` : "Особенности не указаны",
            contact: pet.phone || "Телефон не указан",
            email: pet.email || "Email не указан",
            foundDate: pet.date || "Дата не указана",
            kind: pet.kind || "Животное",
            photos: pet.photos || [cat]
          });
        } else {
          setError('Данные о животном не найдены');
        }
      } catch (err) {
        console.error('Ошибка загрузки животного:', err);
        setError(err.message || 'Не удалось загрузить информацию о животном');
      } finally {
        setLoading(false);
      }
    };
    
    // Слушатель события показа модального окна
    const modalElement = document.getElementById('cart');
    if (modalElement) {
      const showModalHandler = () => {
        loadAnimalData();
      };
      
      modalElement.addEventListener('show.bs.modal', showModalHandler);
      
      return () => {
        modalElement.removeEventListener('show.bs.modal', showModalHandler);
      };
    }
  }, []);

  const handleAdopt = () => {
    setIsInterested(true);
    const message = `Вы проявили интерес к ${animalInfo?.name || 'животному'}. `;
    message += `Мы свяжемся с вами по телефону ${animalInfo?.contact || 'в ближайшее время'}!`;
    alert(message);
    
    // Отправка уведомления на сервер можно добавить здесь
  };

  const nextImage = () => {
    if (animalInfo?.photos && animalInfo.photos.length > 1) {
      setCurrentImageIndex((prev) => (prev + 1) % animalInfo.photos.length);
    }
  };

  const prevImage = () => {
    if (animalInfo?.photos && animalInfo.photos.length > 1) {
      setCurrentImageIndex((prev) => (prev - 1 + animalInfo.photos.length) % animalInfo.photos.length);
    }
  };

  if (!animalInfo && !loading && !error) {
    // Используем данные по умолчанию если нет загруженных
    const defaultAnimal = {
      name: "Котёнок",
      location: "район ул. Пушкина",
      description: "Маленький серенький котенок. Очень игривый и ласковый.",
      age: "примерно 2 месяца",
      gender: "мужской",
      features: "Приучен к лотку, обработан от паразитов",
      contact: "+7 (999) 999-99-99",
      email: "contact@example.com",
      foundDate: "12 октября 2025",
      kind: "кошка",
      photos: [cat]
    };
    
    setAnimalInfo(defaultAnimal);
  }

  return (
    <div className="modal fade" id="cart" tabIndex={-1} aria-labelledby="exampleModalLabel" aria-hidden="true">
      <div className="modal-dialog modal-lg">
        <div className="modal-content">
          <div className="modal-header">
            <h1 className="modal-title fs-5" id="exampleModalLabel">
              {loading ? "Загрузка..." : animalInfo?.name || "Животное"}
            </h1>
            <button 
              type="button" 
              className="btn-close" 
              data-bs-dismiss="modal" 
              aria-label="Закрыть"
              onClick={() => {
                setAnimalInfo(null);
                setCurrentImageIndex(0);
                setIsInterested(false);
              }}
            />
          </div>
          <div className="modal-body">
            {loading ? (
              <div className="text-center p-4">
                <div className="spinner-border text-dark" role="status">
                  <span className="visually-hidden">Загрузка...</span>
                </div>
                <p>Загрузка информации о животном...</p>
              </div>
            ) : error ? (
              <div className="alert alert-danger">
                <h5>Ошибка</h5>
                <p>{error}</p>
                <button 
                  className="btn btn-dark btn-sm" 
                  onClick={() => window.location.reload()}
                >
                  Обновить страницу
                </button>
              </div>
            ) : animalInfo ? (
              <div className="row">
                <div className="col-md-6">
                  <div className="info-img position-relative">
                    <img 
                      src={animalInfo.photos[currentImageIndex] || cat} 
                      className="card-img-bottom info-img-img" 
                      alt={animalInfo.name}
                      style={{ height: "300px", objectFit: "cover" }}
                      onError={(e) => {
                        e.target.src = cat;
                      }}
                    />
                    
                    {animalInfo.photos && animalInfo.photos.length > 1 && (
                      <>
                        <button 
                          className="position-absolute top-50 start-0 translate-middle-y btn btn-dark btn-sm"
                          onClick={prevImage}
                          style={{ zIndex: 10 }}
                        >
                          ←
                        </button>
                        <button 
                          className="position-absolute top-50 end-0 translate-middle-y btn btn-dark btn-sm"
                          onClick={nextImage}
                          style={{ zIndex: 10 }}
                        >
                          →
                        </button>
                        
                        <div className="position-absolute bottom-0 start-0 w-100 text-center mb-2">
                          <span className="badge bg-dark">
                            Фото {currentImageIndex + 1} из {animalInfo.photos.length}
                          </span>
                        </div>
                      </>
                    )}
                  </div>
                </div>
                <div className="col-md-6">
                  <p><strong>Вид животного:</strong> {animalInfo.kind}</p>
                  <p><strong>Дата находки:</strong> {animalInfo.foundDate}</p>
                  <p><strong>Место находки:</strong> {animalInfo.location}</p>
                  <p><strong>Описание:</strong> {animalInfo.description}</p>
                  <p><strong>Возраст:</strong> {animalInfo.age}</p>
                  <p><strong>Пол:</strong> {animalInfo.gender}</p>
                  <p><strong>Особенности:</strong> {animalInfo.features}</p>
                  <p><strong>Контакты для связи:</strong> {animalInfo.contact}</p>
                  {animalInfo.email && (
                    <p><strong>Email:</strong> {animalInfo.email}</p>
                  )}
                </div>
              </div>
            ) : (
              <div className="text-center p-4">
                <p>Информация о животном не загружена</p>
              </div>
            )}
          </div>
          <div className="modal-footer">
            <button 
              type="button" 
              className="btn btn-danger" 
              data-bs-dismiss="modal"
              onClick={() => {
                setAnimalInfo(null);
                setCurrentImageIndex(0);
                setIsInterested(false);
              }}
            >
              Закрыть
            </button>
            <button 
              type="button" 
              className="btn btn-dark"
              onClick={handleAdopt}
              disabled={isInterested || loading || error}
            >
              {isInterested ? "Заявка отправлена" : "Забрать"}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Modal_Cat;
