import React, { useState } from "react";
import Icon from './image/icon/v.ico';
import Icon1 from './image/icon/phone.ico';
import Icon2 from './image/icon/mail.ico';
import Icon3 from './image/icon/z.ico';

function AuthModal({ userInfo, onLogin, onRegister }) {
  const [isLoginMode, setIsLoginMode] = useState(true);
  const [loginData, setLoginData] = useState({
    email: "",
    password: ""
  });
  const [registerData, setRegisterData] = useState({
    name: "",
    phone: "",
    email: "",
    password: "",
    confirmPassword: "",
    agreeToTerms: false
  });
  const [errors, setErrors] = useState({});
  const [isLoading, setIsLoading] = useState(false);

  // Валидация формы входа
  const validateLogin = () => {
    const newErrors = {};
    if (!loginData.email.includes('@')) newErrors.loginEmail = "Некорректный email";
    if (loginData.password.length < 6) newErrors.loginPassword = "Пароль должен быть не менее 6 символов";
    return newErrors;
  };

  // Валидация формы регистрации
  const validateRegister = () => {
    const newErrors = {};
    if (!registerData.name.trim()) newErrors.name = "Имя обязательно";
    if (!registerData.phone.trim()) newErrors.phone = "Телефон обязателен";
    if (!registerData.email.includes('@')) newErrors.email = "Некорректный email";
    if (registerData.password.length < 6) newErrors.password = "Пароль должен быть не менее 6 символов";
    if (registerData.password !== registerData.confirmPassword) newErrors.confirmPassword = "Пароли не совпадают";
    if (!registerData.agreeToTerms) newErrors.agreeToTerms = "Необходимо согласие";
    return newErrors;
  };

  // Обработка входа через API
  const handleLogin = async () => {
    const validationErrors = validateLogin();
    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors);
      return;
    }

    setIsLoading(true);
    setErrors({});

    try {
      const response = await fetch('https://pets.xn--80ahdri7a.site/api/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          email: loginData.email,
          password: loginData.password
        })
      });

      if (response.ok) {
        const data = await response.json();
        // Сохраняем токен (например, в localStorage или в контексте)
        localStorage.setItem('authToken', data.data.token);
        
        // Получаем информацию о пользователе (если есть отдельный эндпоинт)
        // или используем данные из ответа
        const userData = {
          name: "Имя пользователя", // Нужно получить с сервера
          email: loginData.email,
          phone: "Телефон" // Нужно получить с сервера
        };
        
        onLogin(userData);
        alert("Вход выполнен успешно!");
        document.getElementById('closeAuthModal')?.click();
      } else if (response.status === 422) {
        // Ошибка валидации
        const errorData = await response.json();
        if (errorData.error?.errors) {
          const apiErrors = {};
          Object.entries(errorData.error.errors).forEach(([key, messages]) => {
            if (Array.isArray(messages)) {
              apiErrors[`login${key.charAt(0).toUpperCase() + key.slice(1)}`] = messages[0];
            }
          });
          setErrors(apiErrors);
        } else {
          setErrors({ loginGeneral: "Ошибка валидации данных" });
        }
      } else if (response.status === 401) {
        // Неавторизован
        const errorData = await response.json();
        setErrors({ 
          loginGeneral: errorData.error?.message || "Неверный email или пароль",
          loginEmail: "Неверный email или пароль",
          loginPassword: "Неверный email или пароль"
        });
      } else {
        setErrors({ loginGeneral: "Произошла ошибка при входе" });
      }
    } catch (error) {
      console.error("Login error:", error);
      setErrors({ loginGeneral: "Ошибка сети. Проверьте соединение." });
    } finally {
      setIsLoading(false);
    }
  };

  // Обработка регистрации
  const handleRegister = async () => {
    const validationErrors = validateRegister();
    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors);
      return;
    }

    setIsLoading(true);
    setErrors({});

    try {
      // Для регистрации нужен соответствующий API эндпоинт
      // Предположим, что он есть на том же домене
      const response = await fetch('https://pets.xn--80ahdri7a.site/api/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          name: registerData.name,
          phone: registerData.phone,
          email: registerData.email,
          password: registerData.password
          // confirmPassword и agreeToTerms отправляются только для валидации на клиенте
        })
      });

      if (response.ok) {
        const data = await response.json();
        // После успешной регистрации можно автоматически войти
        // или показать сообщение об успехе
        alert("Регистрация успешна! Теперь вы можете войти.");
        setIsLoginMode(true);
        setRegisterData({
          name: "",
          phone: "",
          email: "",
          password: "",
          confirmPassword: "",
          agreeToTerms: false
        });
      } else if (response.status === 422) {
        // Ошибка валидации от сервера
        const errorData = await response.json();
        if (errorData.error?.errors) {
          const apiErrors = {};
          Object.entries(errorData.error.errors).forEach(([key, messages]) => {
            if (Array.isArray(messages)) {
              apiErrors[key] = messages[0];
            }
          });
          setErrors(apiErrors);
        } else {
          setErrors({ general: "Ошибка валидации данных" });
        }
      } else {
        setErrors({ general: "Произошла ошибка при регистрации" });
      }
    } catch (error) {
      console.error("Registration error:", error);
      setErrors({ general: "Ошибка сети. Проверьте соединение." });
    } finally {
      setIsLoading(false);
    }
  };

  const handleChangeLogin = (field, value) => {
    setLoginData(prev => ({ ...prev, [field]: value }));
    if (errors[`login${field.charAt(0).toUpperCase() + field.slice(1)}`]) {
      setErrors(prev => ({ ...prev, [`login${field.charAt(0).toUpperCase() + field.slice(1)}`]: null }));
    }
    if (errors.loginGeneral) {
      setErrors(prev => ({ ...prev, loginGeneral: null }));
    }
  };

  const handleChangeRegister = (field, value) => {
    setRegisterData(prev => ({ ...prev, [field]: value }));
    if (errors[field]) {
      setErrors(prev => ({ ...prev, [field]: null }));
    }
    if (errors.general) {
      setErrors(prev => ({ ...prev, general: null }));
    }
  };

  return (
    <div className="modal fade" id="authModal" tabIndex={-1} aria-labelledby="authModalLabel" aria-hidden="true">
      <div className="modal-dialog">
        <div className="modal-content">
          <div className="modal-header">
            <h1 className="modal-title fs-5" id="authModalLabel">
              {isLoginMode ? "Вход в аккаунт" : "Регистрация"}
            </h1>
            <button 
              id="closeAuthModal"
              type="button" 
              className="btn-close" 
              data-bs-dismiss="modal" 
              aria-label="Закрыть"
            />
          </div>
          
          <div className="modal-body">
            {/* Общие ошибки */}
            {errors.loginGeneral && (
              <div className="alert alert-danger small mb-3">
                {errors.loginGeneral}
              </div>
            )}
            {errors.general && (
              <div className="alert alert-danger small mb-3">
                {errors.general}
              </div>
            )}

            {/* Переключение между входом и регистрацией */}
            <div className="mb-4">
              <div className="btn-group w-100" role="group">
                <button
                  type="button"
                  className={`btn ${isLoginMode ? 'btn-dark' : 'btn-outline-dark'}`}
                  onClick={() => setIsLoginMode(true)}
                  disabled={isLoading}
                >
                  Вход
                </button>
                <button
                  type="button"
                  className={`btn ${!isLoginMode ? 'btn-dark' : 'btn-outline-dark'}`}
                  onClick={() => setIsLoginMode(false)}
                  disabled={isLoading}
                >
                  Регистрация
                </button>
              </div>
            </div>

            {/* Форма входа */}
            {isLoginMode ? (
              <div>
                <div className="input-group flex-nowrap mb-2">
                  <span className="input-group-text"><img src={Icon2} alt="Email" /></span>
                  <input 
                    type="email" 
                    className={`form-control ${errors.loginEmail ? 'is-invalid' : ''}`}
                    placeholder="Email" 
                    value={loginData.email}
                    onChange={(e) => handleChangeLogin('email', e.target.value)}
                    disabled={isLoading}
                  />
                  {errors.loginEmail && <div className="invalid-feedback">{errors.loginEmail}</div>}
                </div>
                
                <div className="input-group flex-nowrap mb-2">
                  <span className="input-group-text"><img src={Icon3} alt="Пароль" /></span>
                  <input 
                    type="password" 
                    className={`form-control ${errors.loginPassword ? 'is-invalid' : ''}`}
                    placeholder="Пароль" 
                    value={loginData.password}
                    onChange={(e) => handleChangeLogin('password', e.target.value)}
                    disabled={isLoading}
                  />
                  {errors.loginPassword && <div className="invalid-feedback">{errors.loginPassword}</div>}
                </div>
                
                <div className="form-text mb-3">
                  Тестовый аккаунт: user@user.ru / paSSword1
                </div>
              </div>
            ) : (
              /* Форма регистрации */
              <div>
                <div className="input-group flex-nowrap mb-2">
                  <span className="input-group-text"><img src={Icon} alt="Имя" /></span>
                  <input 
                    type="text" 
                    className={`form-control ${errors.name ? 'is-invalid' : ''}`}
                    placeholder="Имя" 
                    value={registerData.name}
                    onChange={(e) => handleChangeRegister('name', e.target.value)}
                    disabled={isLoading}
                  />
                  {errors.name && <div className="invalid-feedback">{errors.name}</div>}
                </div>
                
                <div className="input-group flex-nowrap mb-2">
                  <span className="input-group-text"><img src={Icon1} alt="Телефон" /></span>
                  <input 
                    type="text" 
                    className={`form-control ${errors.phone ? 'is-invalid' : ''}`}
                    placeholder="Телефон" 
                    value={registerData.phone}
                    onChange={(e) => handleChangeRegister('phone', e.target.value)}
                    disabled={isLoading}
                  />
                  {errors.phone && <div className="invalid-feedback">{errors.phone}</div>}
                </div>
                
                <div className="input-group flex-nowrap mb-2">
                  <span className="input-group-text"><img src={Icon2} alt="Email" /></span>
                  <input 
                    type="email" 
                    className={`form-control ${errors.email ? 'is-invalid' : ''}`}
                    placeholder="Email" 
                    value={registerData.email}
                    onChange={(e) => handleChangeRegister('email', e.target.value)}
                    disabled={isLoading}
                  />
                  {errors.email && <div className="invalid-feedback">{errors.email}</div>}
                </div>
                
                <div className="input-group flex-nowrap mb-2">
                  <span className="input-group-text"><img src={Icon3} alt="Пароль" /></span>
                  <input 
                    type="password" 
                    className={`form-control ${errors.password ? 'is-invalid' : ''}`}
                    placeholder="Пароль" 
                    value={registerData.password}
                    onChange={(e) => handleChangeRegister('password', e.target.value)}
                    disabled={isLoading}
                  />
                  {errors.password && <div className="invalid-feedback">{errors.password}</div>}
                </div>
                
                <div className="input-group flex-nowrap mb-2">
                  <span className="input-group-text"><img src={Icon3} alt="Подтверждение пароля" /></span>
                  <input 
                    type="password" 
                    className={`form-control ${errors.confirmPassword ? 'is-invalid' : ''}`}
                    placeholder="Подтвердите пароль" 
                    value={registerData.confirmPassword}
                    onChange={(e) => handleChangeRegister('confirmPassword', e.target.value)}
                    disabled={isLoading}
                  />
                  {errors.confirmPassword && <div className="invalid-feedback">{errors.confirmPassword}</div>}
                </div>
                
                <div className="form-check mb-3">
                  <input 
                    className="form-check-input" 
                    type="checkbox" 
                    id="agreeToTerms"
                    checked={registerData.agreeToTerms}
                    onChange={(e) => handleChangeRegister('agreeToTerms', e.target.checked)}
                    disabled={isLoading}
                  />
                  <label className="form-check-label" htmlFor="agreeToTerms">
                    Я даю согласие на обработку персональных данных
                  </label>
                  {errors.agreeToTerms && <div className="text-danger small">{errors.agreeToTerms}</div>}
                </div>
              </div>
            )}
          </div>
          
          <div className="modal-footer">
            <button 
              type="button" 
              className="btn btn-danger" 
              data-bs-dismiss="modal"
              disabled={isLoading}
            >
              Закрыть
            </button>
            <button 
              type="button" 
              className="btn btn-dark"
              onClick={isLoginMode ? handleLogin : handleRegister}
              disabled={isLoading}
            >
              {isLoading ? (
                <span>
                  <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span>
                  {isLoginMode ? "Вход..." : "Регистрация..."}
                </span>
              ) : (
                isLoginMode ? "Войти" : "Зарегистрироваться"
              )}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default AuthModal;
