import { useEffect, useState } from "react";

function User() {
    const [userData, setUserData] = useState({
        email: "",
        phone: "",
        name: "",
        registrationDate: "",
        ordersCount: 0,
        petsCount: 0,
        id: ""
      });

      useEffect(loaduser(), [])
      function loaduser(){
        var myHeaders = new Headers();
        myHeaders.append("Authorization", "Bearer "+localStorage.authToken);

var requestOptions = {
  method: 'GET',
  headers: myHeaders,
  redirect: 'follow'
};

fetch("https://pets.xn--80ahdri7a.site/api/users/", requestOptions)
  .then(response => response.json())
  .then(result => {console.log(result)
    if ('id' in result){
        setUserData(result)
    }

})
  .catch(error => console.log('error', error));
      }

    return (  <div >
    <div className="modal-dialog modal-lg">
      <div className="modal-content">
        <div className="modal-header">
          <h1 className="modal-title fs-5">
            Личный кабинет - {userData.name || "Пользователь"}
          </h1>
          <button type="button" className="btn-close" data-bs-dismiss="modal" />
        </div>
        <div className="modal-body">
          {/* Уведомления об успехе */}
                      <div className="row">
              <div className="col-md-6">
                <h5>Данные пользователя</h5>
                
                <div className="mb-3">
                  <label className="form-label small">Имя:</label>
                  <input 
                    type="text" 
                    className="form-control" 
                    placeholder="Имя" 
                    value={userData.name}
                    onChange={(e) => setUserData({...userData, name: e.target.value})}
                    
                  />
                </div>
                
                <div className="mb-3">
                  <label className="form-label small">Email:</label>
                  <input 
                    type="email" 
                    className="form-control" 
                    placeholder="email" 
                    value={userData.email}
                    onChange={(e) => setUserData({...userData, email: e.target.value})}
                    
                  />
                </div>
                
                <div className="mb-3">
                  <label className="form-label small">Телефон:</label>
                  <input 
                    type="text" 
                    className="form-control" 
                    placeholder="phone" 
                    value={userData.phone}
                    onChange={(e) => setUserData({...userData, phone: e.target.value})}
                   
                  />
                </div> 
                </div>
                </div>
                
                
         </div>
         </div>
         </div>
         </div>
    );}       

export default User;
