import React, { useState, useEffect } from 'react';
import { Routes, Route } from 'react-router-dom';
import HomePage from './components/pages/HomePage';
import FindAnimalsPage from './components/pages/FindAnimalsPage';
import SearchPage from './components/pages/SearchPage';
import Header from './components/layout/Header';
import Footer from './components/layout/Footer';
import { AuthProvider } from './components/auth/AuthContext';
import AnimalModal from './components/animals/AnimalModal';

function App() {
  const [selectedAnimalId, setSelectedAnimalId] = useState(null);

  // Слушаем глобальные события для открытия модальных окон
  useEffect(() => {
    const handleOpenAnimalModal = (event) => {
      setSelectedAnimalId(event.detail.animalId);
    };

    // Событие для принудительного обновления данных
    const handleRefreshAnimals = () => {
      // Отправляем событие для обновления данных
      window.dispatchEvent(new CustomEvent('refreshAnimals'));
    };

    window.addEventListener('openAnimalModal', handleOpenAnimalModal);
    window.addEventListener('refreshAnimals', handleRefreshAnimals);

    return () => {
      window.removeEventListener('openAnimalModal', handleOpenAnimalModal);
      window.removeEventListener('refreshAnimals', handleRefreshAnimals);
    };
  }, []);

  return (
    <AuthProvider>
      <div className="app-container">
        <Header onAnimalClick={setSelectedAnimalId} />
        <main className="main-content">
          <Routes>
            <Route path="/" element={<HomePage />} />
            <Route path="/find-animals" element={<FindAnimalsPage />} />
            <Route path="/search" element={<SearchPage />} />
          </Routes>
        </main>
        <Footer />

        {/* Глобальные модальные окна */}
        {selectedAnimalId && (
          <AnimalModal 
            animalId={selectedAnimalId} 
            onClose={() => setSelectedAnimalId(null)}
          />
        )}
      </div>
    </AuthProvider>
  );
}

export default App;
