import React, { useState } from "react";
import Icon from './image/icon/v.ico';
import Icon1 from './image/icon/phone.ico';
import Icon2 from './image/icon/mail.ico';
import Icon3 from './image/icon/z.ico';

function Register({ userInfo }) {
  const [formData, setFormData] = useState({
    name: "",
    phone: "",
    email: "",
    password: "",
    confirmPassword: "",
    agreeToTerms: false
  });

  const [errors, setErrors] = useState({});

  const validateForm = () => {
    const newErrors = {};

    if (!formData.name.trim()) newErrors.name = "Имя обязательно";
    if (!formData.phone.trim()) newErrors.phone = "Телефон обязателен";
    if (!formData.email.includes('@')) newErrors.email = "Некорректный email";
    if (formData.password.length < 6) newErrors.password = "Пароль должен быть не менее 6 символов";
    if (formData.password !== formData.confirmPassword) newErrors.confirmPassword = "Пароли не совпадают";
    if (!formData.agreeToTerms) newErrors.agreeToTerms = "Необходимо согласие";

    return newErrors;
  };

  const handleSubmit = () => {
    const validationErrors = validateForm();

    if (Object.keys(validationErrors).length === 0) {
      alert("Регистрация успешна!");
      // Здесь обычно отправка данных на сервер
    } else {
      setErrors(validationErrors);
      alert("Пожалуйста, исправьте ошибки в форме");
    }
  };

  const handleChange = (field, value) => {
    setFormData(prev => ({ ...prev, [field]: value }));
    // Очищаем ошибку при изменении поля
    if (errors[field]) {
      setErrors(prev => ({ ...prev, [field]: null }));
    }
  };

  return (
    <div className="modal fade" id="exampleModal" tabIndex={-1} aria-labelledby="exampleModalLabel" aria-hidden="true">
      <div className="modal-dialog">
        <div className="modal-content">
          <div className="modal-header">
            <h1 className="modal-title fs-5" id="exampleModalLabel">
              {userInfo?.isLoggedIn ? "Выход" : "Регистрация"}
            </h1>
            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Закрыть" />
          </div>
          <div className="modal-body">
            <div className="input-group flex-nowrap mb-2">
              <span className="input-group-text"><img src={Icon} alt="Имя" /></span>
              <input 
                type="text" 
                className={`form-control ${errors.name ? 'is-invalid' : ''}`}
                placeholder="Имя" 
                value={formData.name}
                onChange={(e) => handleChange('name', e.target.value)}
              />
              {errors.name && <div className="invalid-feedback">{errors.name}</div>}
            </div>

            <div className="input-group flex-nowrap mb-2">
              <span className="input-group-text"><img src={Icon1} alt="Телефон" /></span>
              <input 
                type="text" 
                className={`form-control ${errors.phone ? 'is-invalid' : ''}`}
                placeholder="Телефон" 
                value={formData.phone}
                onChange={(e) => handleChange('phone', e.target.value)}
              />
              {errors.phone && <div className="invalid-feedback">{errors.phone}</div>}
            </div>

            <div className="input-group flex-nowrap mb-2">
              <span className="input-group-text"><img src={Icon2} alt="Email" /></span>
              <input 
                type="email" 
                className={`form-control ${errors.email ? 'is-invalid' : ''}`}
                placeholder="Email" 
                value={formData.email}
                onChange={(e) => handleChange('email', e.target.value)}
              />
              {errors.email && <div className="invalid-feedback">{errors.email}</div>}
            </div>

            <div className="input-group flex-nowrap mb-2">
              <span className="input-group-text"><img src={Icon3} alt="Пароль" /></span>
              <input 
                type="password" 
                className={`form-control ${errors.password ? 'is-invalid' : ''}`}
                placeholder="Пароль" 
                value={formData.password}
                onChange={(e) => handleChange('password', e.target.value)}
              />
              {errors.password && <div className="invalid-feedback">{errors.password}</div>}
            </div>

            <div className="input-group flex-nowrap mb-2">
              <span className="input-group-text"><img src={Icon3} alt="Подтверждение пароля" /></span>
              <input 
                type="password" 
                className={`form-control ${errors.confirmPassword ? 'is-invalid' : ''}`}
                placeholder="Подтвердите пароль" 
                value={formData.confirmPassword}
                onChange={(e) => handleChange('confirmPassword', e.target.value)}
              />
              {errors.confirmPassword && <div className="invalid-feedback">{errors.confirmPassword}</div>}
            </div>

            <div className="input-group flex-nowrap mb-2">
              <input 
                className="me-1" 
                type="checkbox" 
                checked={formData.agreeToTerms}
                onChange={(e) => handleChange('agreeToTerms', e.target.checked)}
              />
              <label>
                Я даю согласие на обработку персональных данных
              </label>
              {errors.agreeToTerms && <div className="text-danger small">{errors.agreeToTerms}</div>}
            </div>
          </div>

          <div className="modal-footer">
            <button type="button" className="btn btn-danger" data-bs-dismiss="modal">Закрыть</button>
            <button 
              type="button" 
              className="btn btn-dark"
              onClick={handleSubmit}
            >
              {userInfo?.isLoggedIn ? "Выйти" : "Зарегистрироваться"}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Register;
