import React, { useState } from "react";

function Nahodka({ onCreateAnimalCard }) {
  const [formData, setFormData] = useState({
    petType: '',
    petDescription: '',
    mark: '',
    petLocation: '',
    name: '',
    phone: '',
    email: '',
    password: '',
    password_confirmation: '',
    confirm: false
  });

  const [images, setImages] = useState([null, null, null]);
  const [imagePreviews, setImagePreviews] = useState([]);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState("");
  const [successMessage, setSuccessMessage] = useState("");
  const [debugInfo, setDebugInfo] = useState("");
  const [showPasswordFields, setShowPasswordFields] = useState(false);
  const [validationErrors, setValidationErrors] = useState({});

  // Список районов
  const [districts] = useState([
    'Адмиралтейский район',
    'Василеостровский район',
    'Выборгский район',
    'Калининский район',
    'Кировский район',
    'Колпинский район',
    'Красногвардейский район',
    'Красносельский район',
    'Кронштадтский район',
    'Курортный район',
    'Московский район',
    'Невский район',
    'Петроградский район',
    'Петродворцовый район',
    'Приморский район',
    'Пушкинский район',
    'Фрунзенский район',
    'Центральный район'
  ]);

  const handleFileChange = (e, index) => {
    const file = e.target.files[0];

    if (!file) return;

    const newImages = [...images];
    newImages[index] = file;
    setImages(newImages);

    // Создаем превью
    const reader = new FileReader();
    reader.onload = (e) => {
      const newPreviews = [...imagePreviews];
      newPreviews[index] = e.target.result;
      setImagePreviews(newPreviews);
    };
    reader.readAsDataURL(file);
  };

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const removeImage = (index) => {
    const newImages = [...images];
    newImages[index] = null;
    setImages(newImages);

    const newPreviews = [...imagePreviews];
    newPreviews[index] = null;
    setImagePreviews(newPreviews);
  };

  const validateForm = () => {
    setError("");
    setValidationErrors({});

    // Проверка обязательных полей
    if (!formData.petType.trim()) {
      setError('Укажите вид животного');
      return false;
    }

    if (!formData.petDescription.trim()) {
      setError('Введите описание');
      return false;
    }

    if (!formData.petLocation) {
      setError('Выберите район');
      return false;
    }

    if (!images[0]) {
      setError('Добавьте хотя бы одну фотографию');
      return false;
    }

    if (!formData.name.trim()) {
      setError('Введите ваше имя');
      return false;
    }

    if (!formData.phone.trim()) {
      setError('Введите телефон');
      return false;
    } else {
      const phoneRegex = /^\+7\s?\(\d{3}\)\s?\d{3}-\d{2}-\d{2}$/;
      if (!phoneRegex.test(formData.phone)) {
        setError("Некорректный формат телефона. Используйте формат: +7 (999) 999-99-99");
        return false;
      }
    }

    if (!formData.email.trim()) {
      setError('Введите email');
      return false;
    } else {
      const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
      if (!emailRegex.test(formData.email)) {
        setError("Введите корректный адрес электронной почты");
        return false;
      }
    }

    // Проверка пароля при показе полей пароля
    if (showPasswordFields) {
      if (!formData.password.trim()) {
        setError("Введите пароль");
        return false;
      }

      if (formData.password.length < 6) {
        setError("Пароль должен содержать минимум 6 символов");
        return false;
      }

      if (formData.password !== formData.password_confirmation) {
        setError("Пароли не совпадают");
        return false;
      }
    }

    // Согласие на обработку данных
    if (!formData.confirm) {
      setError('Необходимо согласие на обработку персональных данных');
      return false;
    }

    return true;
  };

  const renderValidationError = (fieldName) => {
    if (validationErrors[fieldName]) {
      return (
        <div className="text-danger small mt-1">
          {validationErrors[fieldName].join(", ")}
        </div>
      );
    }
    return null;
  };

  const renderPasswordRequirements = () => {
    if (!showPasswordFields) return null;

    const password = formData.password;
    const requirements = [
      { text: 'Минимум 6 символов', met: password.length >= 6 },
      { text: 'Хотя бы 1 цифра', met: /\d/.test(password) },
      { text: 'Хотя бы 1 строчная буква', met: /[a-z]/.test(password) },
      { text: 'Хотя бы 1 заглавная буква', met: /[A-Z]/.test(password) }
    ];

    return (
      <div className="password-requirements mt-2">
        {requirements.map((req, index) => (
          <div key={index} className={`requirement ${req.met ? 'text-success' : 'text-secondary'}`}>
            <i className={`bi ${req.met ? 'bi-check-circle' : 'bi-circle'}`}></i>
            <span className="ms-1">{req.text}</span>
          </div>
        ))}
      </div>
    );
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    setError("");
    setSuccessMessage("");
    setDebugInfo("");
    setValidationErrors({});

    if (!validateForm()) {
      return;
    }

    // Если email введен, но поля пароля еще не показаны
    if (formData.email && !showPasswordFields) {
      setShowPasswordFields(true);
      return;
    }

    setIsSubmitting(true);

    try {
      console.log("Отправляемые данные:", formData);

      const apiFormData = new FormData();

      // ВАЖНО: отправляем русские названия для вида животного
      // API ожидает "кот", "собака" и т.д.
      let kind = "";
      switch(formData.petType) {
        case 'cat':
          kind = "кот";
          break;
        case 'dog':
          kind = "собака";
          break;
        case 'rabbit':
          kind = "кролик";
          break;
        default:
          kind = "другое";
      }

      // Добавляем файлы (как в Addob.jsx)
      images.forEach((image, index) => {
        if (image) {
          apiFormData.append(`photo${index + 1}`, image);
        }
      });

      // Добавляем текстовые поля В ТОЧНОСТИ как в Addob.jsx
      apiFormData.append('kind', kind);
      apiFormData.append('description', formData.petDescription);
      apiFormData.append('district', formData.petLocation);
      apiFormData.append('name', formData.name);
      apiFormData.append('phone', formData.phone.replace(/\D/g, "")); // Убираем все не-цифры
      apiFormData.append('email', formData.email);
      apiFormData.append('confirm', formData.confirm ? 1 : 0);

      if (formData.mark.trim()) {
        apiFormData.append('mark', formData.mark);
      }

      // Пароли (как в Addob.jsx)
      apiFormData.append('password', formData.password);
      apiFormData.append('password_confirmation', formData.password_confirmation);

      setDebugInfo("Отправка запроса...");

      // Отправляем как в Addob.jsx
      const response = await fetch("https://pets.xn--80ahdri7a.site/api/pets", {
        method: "POST",
        body: apiFormData,
        headers: {
          'Accept': 'application/json',
        }
      });

      console.log("Response status:", response.status);
      setDebugInfo(`Статус ответа: ${response.status}`);

      let responseData;
      try {
        responseData = await response.json();
        console.log("Response data:", responseData);
      } catch (jsonError) {
        console.error("Ошибка парсинга JSON:", jsonError);
        const textResponse = await response.text();
        console.log("Text response:", textResponse);
        responseData = { error: { message: textResponse } };
      }

      if (!response.ok) {
        if (response.status === 422) {
          let errorMessage = "Ошибка валидации данных: ";

          if (responseData.errors) {
            setValidationErrors(responseData.errors);

            Object.keys(responseData.errors).forEach(key => {
              errorMessage += `${key}: ${responseData.errors[key].join(", ")}. `;
            });
          } else if (responseData.message) {
            errorMessage += responseData.message;
          } else {
            errorMessage += "Проверьте правильность заполнения всех полей.";
          }

          setError(errorMessage);
          setDebugInfo(`Ошибка 422: ${errorMessage}`);
        } else if (response.status === 401 || response.status === 403) {
          setError("Ошибка авторизации. Проверьте email и пароль.");
          setDebugInfo(`Ошибка ${response.status}: Требуется авторизация`);
        } else {
          setError(`Ошибка сервера: ${response.status} ${responseData?.message || ""}`);
          setDebugInfo(`Ошибка ${response.status}: ${responseData?.message || ""}`);
        }

        setIsSubmitting(false);
        return;
      }

      console.log("Success response:", responseData);
      setDebugInfo(`Успех! ID: ${responseData?.data?.id || 'не указан'}`);

      // Создаем карточку
      const newAnimalCard = {
        id: responseData?.data?.id || Date.now(),
        name: formData.name || `Найденный ${kind}`,
        location: formData.petLocation,
        description: formData.petDescription,
        features: formData.mark || "Требуется уточнение",
        contact: formData.phone,
        foundDate: new Date().toLocaleDateString('ru-RU', { 
          day: 'numeric', 
          month: 'long', 
          year: 'numeric' 
        }),
        animalType: formData.petType,
        imageUrl: imagePreviews[0] || null
      };

      if (onCreateAnimalCard) {
        onCreateAnimalCard(newAnimalCard);
      }

      setSuccessMessage(`Сообщение о находке отправлено успешно! ID: ${responseData?.data?.id || 'не указан'}`);

      // Сброс формы
      setFormData({
        petType: '',
        petDescription: '',
        mark: '',
        petLocation: '',
        name: '',
        phone: '',
        email: '',
        password: '',
        password_confirmation: '',
        confirm: false
      });
      setImages([null, null, null]);
      setImagePreviews([]);

      setShowPasswordFields(false);
      setValidationErrors({});

      setTimeout(() => {
        const closeBtn = document.querySelector('[data-bs-dismiss="modal"]');
        if (closeBtn) closeBtn.click();
        setSuccessMessage("");
        setDebugInfo("");
      }, 3000);

    } catch (error) {
      console.error("Network error:", error);
      setError(`Ошибка сети: ${error.message}. Проверьте подключение к интернету и CORS политику.`);
      setDebugInfo(`Ошибка сети: ${error.message}`);
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div>
      <div className="modal fade" id="newPost" tabIndex={-1} aria-labelledby="exampleModalLabel" aria-hidden="true">
        <div className="modal-dialog modal-lg">
          <div className="modal-content">
            <div className="modal-header">
              <h1 className="modal-title fs-5" id="exampleModalLabel">Сообщить о находке</h1>
              <button 
                type="button" 
                className="btn-close" 
                data-bs-dismiss="modal" 
                aria-label="Закрыть" 
                onClick={() => {
                  setError(""); 
                  setSuccessMessage(""); 
                  setDebugInfo("");
                  setShowPasswordFields(false);
                  setValidationErrors({});
                }} 
              />
            </div>
            <div className="modal-body">
              {/* Сообщения */}
              {error && (
                <div className="alert alert-danger alert-dismissible fade show" role="alert">
                  <strong>Ошибка!</strong> {error}
                  <button type="button" className="btn-close" onClick={() => setError("")}></button>
                </div>
              )}

              {successMessage && (
                <div className="alert alert-success alert-dismissible fade show" role="alert">
                  <strong>Успех!</strong> {successMessage}
                  <button type="button" className="btn-close" onClick={() => setSuccessMessage("")}></button>
                </div>
              )}

              {debugInfo && (
                <div className="alert alert-info alert-dismissible fade show" role="alert">
                  <strong>Отладка:</strong> {debugInfo}
                  <button type="button" className="btn-close" onClick={() => setDebugInfo("")}></button>
                </div>
              )}

              <form onSubmit={handleSubmit} id="animalForm">
                <div className="row">
                  <div className="col-md-6">
                    {/* Тип животного */}
                    <div className="mb-3">
                      <label htmlFor="petType" className="form-label">Вид животного*:</label>
                      <select 
                        className={`form-select ${validationErrors.kind ? 'is-invalid' : ''}`}
                        id="petType"
                        name="petType"
                        value={formData.petType}
                        onChange={handleInputChange}
                        required
                      >
                        <option value="">Выберите вид</option>
                        <option value="cat">Кот/Кошка</option>
                        <option value="dog">Собака</option>
                        <option value="rabbit">Кролик</option>
                        <option value="other">Другое</option>
                      </select>
                      {renderValidationError("kind")}
                    </div>

                    {/* Имя/Кличка */}
                    <div className="mb-3">
                      <label htmlFor="name" className="form-label">Имя/Кличка:</label>
                      <input 
                        type="text" 
                        className={`form-control ${validationErrors.name ? 'is-invalid' : ''}`}
                        id="name"
                        name="name"
                        placeholder="Например: Мурзик, Барсик и т.д."
                        value={formData.name}
                        onChange={handleInputChange}
                      />
                      {renderValidationError("name")}
                    </div>

                    {/* Особенности */}
                    <div className="mb-3">
                      <label htmlFor="mark" className="form-label">Клеймо/Особенности:</label>
                      <input 
                        type="text" 
                        className={`form-control ${validationErrors.mark ? 'is-invalid' : ''}`}
                        id="mark"
                        name="mark"
                        placeholder="Например: приучен к лотку, знает команды и т.д."
                        value={formData.mark}
                        onChange={handleInputChange}
                      />
                      {renderValidationError("mark")}
                    </div>

                    {/* Телефон */}
                    <div className="mb-3">
                      <label htmlFor="phone" className="form-label">Ваш телефон*:</label>
                      <input 
                        type="tel" 
                        className={`form-control ${validationErrors.phone ? 'is-invalid' : ''}`}
                        id="phone"
                        name="phone"
                        placeholder="+7 (999) 999-99-99"
                        value={formData.phone}
                        onChange={handleInputChange}
                        pattern="\+7\s?\(\d{3}\)\s?\d{3}-\d{2}-\d{2}"
                        required
                      />
                      <div className="form-text">Формат: +7 (999) 999-99-99</div>
                      {renderValidationError("phone")}
                    </div>
                  </div>

                  <div className="col-md-6">
                    {/* Место находки - выпадающий список районов */}
                    <div className="mb-3">
                      <label htmlFor="petLocation" className="form-label">Район находки*:</label>
                      <select 
                        className={`form-select ${validationErrors.district ? 'is-invalid' : ''}`}
                        id="petLocation"
                        name="petLocation"
                        value={formData.petLocation}
                        onChange={handleInputChange}
                        required
                      >
                        <option value="">Выберите район</option>
                        {districts.map((district, index) => (
                          <option key={index} value={district}>
                            {district}
                          </option>
                        ))}
                      </select>
                      {renderValidationError("district")}
                    </div>

                    {/* Email */}
                    <div className="mb-3">
                      <label htmlFor="email" className="form-label">Электронная почта*:</label>
                      <input 
                        type="email" 
                        className={`form-control ${validationErrors.email ? 'is-invalid' : ''}`}
                        id="email"
                        name="email"
                        placeholder="example@email.com"
                        value={formData.email}
                        onChange={handleInputChange}
                        required
                      />
                      <div className="form-text">Для авторизации</div>
                      {renderValidationError("email")}
                    </div>

                    {/* Фотографии (как в Addob.jsx) */}
                    <div className="mb-3">
                      <label className="form-label fw-bold">Фотография *</label>
                      <input 
                        type="file" 
                        className={`form-control ${validationErrors.photo1 ? 'is-invalid' : ''}`}
                        id="petPhoto1" 
                        accept="image/*" 
                        onChange={(e) => handleFileChange(e, 0)}
                        required
                      />
                      <div className="form-text">Формат: PNG, JPG. Максимальный размер: 5MB</div>
                      {renderValidationError("photo1")}

                      <label className="form-label fw-bold mt-3">Дополнительная фотография</label>
                      <input 
                        type="file" 
                        className={`form-control ${validationErrors.photo2 ? 'is-invalid' : ''}`}
                        id="petPhoto2" 
                        accept="image/*" 
                        onChange={(e) => handleFileChange(e, 1)}
                      />
                      {renderValidationError("photo2")}

                      <label className="form-label fw-bold mt-3">Дополнительная фотография</label>
                      <input 
                        type="file" 
                        className={`form-control ${validationErrors.photo3 ? 'is-invalid' : ''}`}
                        id="petPhoto3" 
                        accept="image/*" 
                        onChange={(e) => handleFileChange(e, 2)}
                      />
                      {renderValidationError("photo3")}

                      {/* Превью изображений */}
                      <div id="imagePreview" className="mt-3">
                        <div className="row">
                          {imagePreviews.map((preview, index) => (
                            preview && (
                              <div key={index} className="col-md-4 mb-3">
                                <div className="position-relative">
                                  <img 
                                    src={preview} 
                                    className="preview-image img-thumbnail w-100" 
                                    alt={`Preview ${index + 1}`}
                                    style={{ height: '150px', objectFit: 'cover' }}
                                  />
                                  <button
                                    type="button"
                                    className="btn btn-danger btn-sm position-absolute top-0 end-0 mt-1 me-1"
                                    onClick={() => removeImage(index)}
                                  >
                                    <i className="bi bi-x"></i>
                                  </button>
                                </div>
                              </div>
                            )
                          ))}
                        </div>
                      </div>
                    </div>
                  </div>
                </div>

                {/* Описание животного */}
                <div className="mb-3">
                  <label htmlFor="petDescription" className="form-label">Описание животного*:</label>
                  <textarea 
                    className={`form-control ${validationErrors.description ? 'is-invalid' : ''}`}
                    id="petDescription" 
                    name="petDescription"
                    rows="3"
                    value={formData.petDescription}
                    onChange={handleInputChange}
                    placeholder="Опишите животное, его поведение, состояние здоровья и т.д."
                    required
                  />
                  {renderValidationError("description")}
                </div>

                {/* Поля пароля (показываются после ввода email) */}
                {showPasswordFields && (
                  <div className="row mb-3 border-top pt-3">
                    <div className="col-md-6">
                      <div className="mb-3">
                        <label htmlFor="password" className="form-label">Пароль*:</label>
                        <input 
                          type="password" 
                          className={`form-control ${validationErrors.password ? 'is-invalid' : ''}`}
                          id="password"
                          name="password"
                          placeholder="Введите пароль"
                          value={formData.password}
                          onChange={handleInputChange}
                          required
                        />
                        <div className="form-text">Пароль должен содержать минимум 6 символов</div>
                        {renderPasswordRequirements()}
                        {renderValidationError("password")}
                      </div>
                    </div>
                    <div className="col-md-6">
                      <div className="mb-3">
                        <label htmlFor="password_confirmation" className="form-label">Подтверждение пароля*:</label>
                        <input 
                          type="password" 
                          className={`form-control ${validationErrors.password_confirmation ? 'is-invalid' : ''}`}
                          id="password_confirmation"
                          name="password_confirmation"
                          placeholder="Повторите пароль"
                          value={formData.password_confirmation}
                          onChange={handleInputChange}
                          required
                        />
                        {renderValidationError("password_confirmation")}
                      </div>
                    </div>
                  </div>
                )}

                {/* Согласие на обработку данных */}
                <div className="mb-3">
                  <div className="form-check">
                    <input 
                      className={`form-check-input ${validationErrors.confirm ? 'is-invalid' : ''}`}
                      type="checkbox" 
                      id="confirm" 
                      name="confirm"
                      checked={formData.confirm}
                      onChange={handleInputChange}
                      required
                    />
                    <label className="form-check-label" htmlFor="confirm">
                      Я согласен на обработку персональных данных*
                    </label>
                    {renderValidationError("confirm")}
                  </div>
                </div>

                <div className="modal-footer">
                  <button 
                    type="button" 
                    className="btn btn-secondary" 
                    data-bs-dismiss="modal" 
                    onClick={() => {
                      setError(""); 
                      setSuccessMessage(""); 
                      setDebugInfo("");
                      setShowPasswordFields(false);
                      setValidationErrors({});
                    }}
                  >
                    Отмена
                  </button>
                  <button 
                    type="submit" 
                    className="btn btn-primary"
                    disabled={isSubmitting}
                  >
                    {isSubmitting ? (
                      <>
                        <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span>
                        Отправка...
                      </>
                    ) : showPasswordFields ? "Подтвердить и отправить" : "Далее (ввести пароль)"}
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Nahodka;
