import React, { useState } from "react";

function Newsletter() {
  const [email, setEmail] = useState("");
  const [error, setError] = useState(null);

  async function handleSubmit(e) {
    e.preventDefault();
    const url = 'https://pets.xn--80ahdri7a.site/api/subscription';
    try {
      const response = await fetch(url, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'},
        body: JSON.stringify(
          {email: email}
        )
      });
      if (!response.ok) {
        setError('Некорректный ввод данных');
        return;
      }

      console.log(response)
      let result;
      if(response.status !== 204) { result = await response.json()};

      setError('Вы успешно подписались на новости');
      console.log(result);
    } catch (error) {
      console.log(error);
      setError('ГЕГЕ')
    }
 
  };

  return (
    <>
      <h2 className="text-center text-white bg-dark m-2">Подписка на новости</h2>
      <form className="w-50 m-auto p-3" style={{minWidth: 300}} onSubmit={handleSubmit}>
        <div className="mb-3">
          <label htmlFor="exampleInputEmail1" className="form-label">Введите адрес электронной почты</label>
          <input 
            type="text" 
            className="form-control" 
            id="exampleInputEmail1" 
            aria-describedby="emailHelp"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <div id="emailHelp" className="form-text">Мы никогда не делимся Вашими e-mail ни с кем.</div>
        </div>

    {
      error && (
        <div className="card mb-3">
  <div className="card-body">
    {error}
  </div>
</div>

      )
    }

    
        <button type="submit" className="btn btn-dark">
          Подписаться
        </button>

      </form>
    </>
  );
}

export default Newsletter;
