import React, { useState, useEffect } from 'react';
import AnimalCard from './AnimalCard';
import Pagination from '../common/Pagination';

const AnimalList = ({ 
  filters = {}, 
  itemsPerPage = 6,
  showPaginationInfo = false 
}) => {
  const [allAnimals, setAllAnimals] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [totalAnimals, setTotalAnimals] = useState(0);

  // Фильтрация животных
  const filterAnimals = (animals) => {
    let filtered = [...animals];
    
    // Фильтр по типу животного
    if (filters.animalType && filters.animalType !== 'all') {
      filtered = filtered.filter(animal => {
        const kind = animal.kind?.toLowerCase() || '';
        switch (filters.animalType) {
          case 'cat':
            return kind.includes('кот') || kind.includes('кошка');
          case 'dog':
            return kind.includes('собака');
          case 'rabbit':
            return kind.includes('кролик');
          case 'other':
            return !kind.includes('кот') && !kind.includes('кошка') && 
                   !kind.includes('собака') && !kind.includes('кролик');
          default:
            return true;
        }
      });
    }

    // Фильтр по дате
    if (filters.dateRange && filters.dateRange !== 'all') {
      const now = new Date();
      filtered = filtered.filter(animal => {
        if (!animal.date) return true;
        
        try {
          const [day, month, year] = animal.date.split('-');
          const animalDate = new Date(year, month - 1, day);
          const diffTime = Math.abs(now - animalDate);
          const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
          
          switch (filters.dateRange) {
            case 'week': return diffDays <= 7;
            case 'month': return diffDays <= 30;
            case 'older': return diffDays > 30;
            default: return true;
          }
        } catch (e) {
          return true;
        }
      });
    }

    return filtered;
  };

  // Получение текущей страницы животных
  const getCurrentPageAnimals = () => {
    const startIndex = (currentPage - 1) * itemsPerPage;
    const endIndex = startIndex + itemsPerPage;
    return allAnimals.slice(startIndex, endIndex);
  };

  // Загрузка животных
  const fetchAnimals = async () => {
    setLoading(true);
    setError(null);

    try {
      console.log('Загружаем данные о животных...');
      const response = await fetch('https://pets.xn--80ahdri7a.site/api/pets', {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json'
        }
      });
      
      if (!response.ok) {
        throw new Error(`Ошибка загрузки данных: ${response.status}`);
      }
      
      const data = await response.json();
      
      if (data.data?.orders) {
        let animals = data.data.orders.filter(animal => animal.registred);
        
        // Сортируем по дате (новые первыми)
        animals.sort((a, b) => {
          try {
            const dateA = a.date ? a.date.split('-').reverse().join('-') : '';
            const dateB = b.date ? b.date.split('-').reverse().join('-') : '';
            return dateB.localeCompare(dateA);
          } catch (e) {
            return 0;
          }
        });

        // Применяем фильтры
        const filteredAnimals = filterAnimals(animals);
        
        setAllAnimals(filteredAnimals);
        setTotalAnimals(filteredAnimals.length);
        setCurrentPage(1); // Сбрасываем на первую страницу при изменении фильтров
      }
    } catch (error) {
      console.error('Error fetching animals:', error);
      setError('Не удалось загрузить данные о животных');
    } finally {
      setLoading(false);
    }
  };

  // Загрузка при монтировании
  useEffect(() => {
    fetchAnimals();
  }, []);

  // Применение фильтров при их изменении
  useEffect(() => {
    if (allAnimals.length > 0) {
      const animals = JSON.parse(localStorage.getItem('allAnimals') || '[]');
      const filteredAnimals = filterAnimals(animals);
      setAllAnimals(filteredAnimals);
      setTotalAnimals(filteredAnimals.length);
      setCurrentPage(1);
    }
  }, [filters]);

  // Сохраняем всех животных в localStorage для повторной фильтрации
  useEffect(() => {
    if (allAnimals.length > 0) {
      localStorage.setItem('allAnimals', JSON.stringify(allAnimals));
    }
  }, [allAnimals]);

  if (loading && allAnimals.length === 0) {
    return (
      <div className="text-center py-5">
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Загрузка...</span>
        </div>
        <p className="mt-2">Загрузка животных...</p>
      </div>
    );
  }

  if (error && allAnimals.length === 0) {
    return (
      <div className="alert alert-danger text-center">
        <p>{error}</p>
        <button 
          className="btn btn-primary mt-2"
          onClick={() => fetchAnimals()}
        >
          Попробовать снова
        </button>
      </div>
    );
  }

  if (allAnimals.length === 0) {
    return (
      <div className="text-center py-5">
        <div className="alert alert-info">
          <i className="bi bi-info-circle me-2"></i>
          Животные не найдены
          <div className="mt-2">
            <button 
              className="btn btn-sm btn-outline-primary"
              onClick={() => fetchAnimals()}
            >
              Обновить
            </button>
          </div>
        </div>
      </div>
    );
  }

  const currentAnimals = getCurrentPageAnimals();
  const totalPages = Math.ceil(totalAnimals / itemsPerPage);

  return (
    <div className="animal-list">
      <div className="mb-3 d-flex justify-content-between align-items-center">
        <div>
          <span className="badge bg-primary">
            Всего найдено: {totalAnimals}
          </span>
          {showPaginationInfo && totalPages > 1 && (
            <span className="ms-2 text-muted">
              Страница {currentPage} из {totalPages}
            </span>
          )}
        </div>
        <button 
          className="btn btn-sm btn-outline-secondary"
          onClick={() => fetchAnimals()}
          disabled={loading}
        >
          {loading ? (
            <>
              <span className="spinner-border spinner-border-sm me-1"></span>
              Обновление...
            </>
          ) : (
            <>
              <i className="bi bi-arrow-clockwise me-1"></i>
              Обновить список
            </>
          )}
        </button>
      </div>
      
      <div className="row row-cols-1 row-cols-md-2 row-cols-lg-3 g-4 mb-4">
        {currentAnimals.map((animal) => (
          <div key={animal.id} className="col">
            <AnimalCard 
              animal={animal}
              onViewClick={() => {
                window.dispatchEvent(new CustomEvent('openAnimalModal', { 
                  detail: { animalId: animal.id } 
                }));
              }}
            />
          </div>
        ))}
      </div>

      {totalPages > 1 && (
        <div className="mt-4">
          <Pagination
            currentPage={currentPage}
            totalPages={totalPages}
            onPageChange={setCurrentPage}
          />
        </div>
      )}
    </div>
  );
};

export default AnimalList;
