import React, { useState, useEffect } from 'react';
import AnimalCard from './AnimalCard';

const AnimalList = ({ limit = null, filters = {} }) => {
  const [animals, setAnimals] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [lastFetchTime, setLastFetchTime] = useState(0);

  // Кэшируем данные на 5 минут
  const CACHE_DURATION = 5 * 60 * 1000; // 5 минут в миллисекундах

  const fetchAnimals = async (forceRefresh = false) => {
    const now = Date.now();
    
    // Проверяем, нужно ли обновлять данные
    if (!forceRefresh && now - lastFetchTime < CACHE_DURATION && animals.length > 0) {
      console.log('Используем кэшированные данные');
      return;
    }

    setLoading(true);
    setError(null);

    try {
      console.log('Загружаем данные о животных...');
      const response = await fetch('https://pets.xn--80ahdri7a.site/api/pets', {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json'
        },
        // Добавляем заголовки для предотвращения кэширования браузером
        cache: 'no-cache'
      });
      
      if (!response.ok) {
        throw new Error(`Ошибка загрузки данных: ${response.status}`);
      }
      
      const data = await response.json();
      console.log('Получены данные:', data);
      
      if (data.data?.orders) {
        setLastFetchTime(now);
        
        // Сохраняем в localStorage для быстрого доступа
        try {
          localStorage.setItem('animals_cache', JSON.stringify({
            data: data.data.orders,
            timestamp: now
          }));
        } catch (e) {
          console.warn('Не удалось сохранить в кэш:', e);
        }
        
        applyFiltersAndLimits(data.data.orders);
      } else {
        setAnimals([]);
      }
    } catch (error) {
      console.error('Error fetching animals:', error);
      setError('Не удалось загрузить данные о животных');
      
      // Пробуем загрузить из кэша в случае ошибки
      try {
        const cached = localStorage.getItem('animals_cache');
        if (cached) {
          const cacheData = JSON.parse(cached);
          if (now - cacheData.timestamp < CACHE_DURATION) {
            console.log('Используем кэш из localStorage после ошибки');
            applyFiltersAndLimits(cacheData.data);
          }
        }
      } catch (cacheError) {
        console.warn('Ошибка при чтении кэша:', cacheError);
      }
    } finally {
      setLoading(false);
    }
  };

  const applyFiltersAndLimits = (animalsData) => {
    let filteredAnimals = animalsData.filter(animal => {
      // Фильтр по регистрации
      if (!animal.registred) return false;
      
      // Фильтр по типу животного
      if (filters.animalType && filters.animalType !== 'all') {
        const kind = animal.kind?.toLowerCase() || '';
        switch (filters.animalType) {
          case 'cat':
            return kind.includes('кот') || kind.includes('кошка');
          case 'dog':
            return kind.includes('собака');
          case 'rabbit':
            return kind.includes('кролик');
          case 'other':
            return !kind.includes('кот') && !kind.includes('кошка') && 
                   !kind.includes('собака') && !kind.includes('кролик');
          default:
            return true;
        }
      }
      return true;
    });

    // Фильтр по дате
    if (filters.dateRange && filters.dateRange !== 'all') {
      const now = new Date();
      filteredAnimals = filteredAnimals.filter(animal => {
        if (!animal.date) return true;
        
        try {
          const [day, month, year] = animal.date.split('-');
          const animalDate = new Date(year, month - 1, day);
          const diffTime = Math.abs(now - animalDate);
          const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
          
          switch (filters.dateRange) {
            case 'week': return diffDays <= 7;
            case 'month': return diffDays <= 30;
            case 'older': return diffDays > 30;
            default: return true;
          }
        } catch (e) {
          return true;
        }
      });
    }

    // Сортируем по дате (новые первыми)
    filteredAnimals.sort((a, b) => {
      try {
        const dateA = a.date ? a.date.split('-').reverse().join('-') : '';
        const dateB = b.date ? b.date.split('-').reverse().join('-') : '';
        return dateB.localeCompare(dateA);
      } catch (e) {
        return 0;
      }
    });

    // Ограничиваем количество, если указано
    if (limit) {
      filteredAnimals = filteredAnimals.slice(0, limit);
    }

    setAnimals(filteredAnimals);
  };

  // Загружаем данные при монтировании
  useEffect(() => {
    console.log('AnimalList mounted, fetching data...');
    fetchAnimals();
  }, []);

  // Обновляем фильтрацию при изменении фильтров
  useEffect(() => {
    if (animals.length > 0 || lastFetchTime > 0) {
      // Загружаем свежие данные из localStorage или сервера
      try {
        const cached = localStorage.getItem('animals_cache');
        if (cached) {
          const cacheData = JSON.parse(cached);
          applyFiltersAndLimits(cacheData.data);
        } else {
          // Если нет кэша, загружаем с сервера
          fetchAnimals(true);
        }
      } catch (e) {
        fetchAnimals(true);
      }
    }
  }, [filters]);

  // Обновляем данные каждые 5 минут
  useEffect(() => {
    const interval = setInterval(() => {
      if (document.visibilityState === 'visible') {
        fetchAnimals(true);
      }
    }, CACHE_DURATION);

    return () => clearInterval(interval);
  }, []);

  // Обновляем данные при возвращении на вкладку
  useEffect(() => {
    const handleVisibilityChange = () => {
      if (document.visibilityState === 'visible') {
        const now = Date.now();
        if (now - lastFetchTime > CACHE_DURATION) {
          fetchAnimals(true);
        }
      }
    };

    document.addEventListener('visibilitychange', handleVisibilityChange);
    return () => {
      document.removeEventListener('visibilitychange', handleVisibilityChange);
    };
  }, [lastFetchTime]);

  if (loading && animals.length === 0) {
    return (
      <div className="text-center py-5">
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Загрузка...</span>
        </div>
        <p className="mt-2">Загрузка животных...</p>
      </div>
    );
  }

  if (error && animals.length === 0) {
    return (
      <div className="alert alert-danger text-center">
        <p>{error}</p>
        <button 
          className="btn btn-primary mt-2"
          onClick={() => fetchAnimals(true)}
        >
          Попробовать снова
        </button>
      </div>
    );
  }

  if (animals.length === 0) {
    return (
      <div className="text-center py-5">
        <div className="alert alert-info">
          <i className="bi bi-info-circle me-2"></i>
          Животные не найдены
          <div className="mt-2">
            <button 
              className="btn btn-sm btn-outline-primary"
              onClick={() => fetchAnimals(true)}
            >
              Обновить
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="animal-list">
      <div className="mb-3 d-flex justify-content-between align-items-center">
        <div>
          <span className="badge bg-primary">
            Найдено: {animals.length}
          </span>
        </div>
        <button 
          className="btn btn-sm btn-outline-secondary"
          onClick={() => fetchAnimals(true)}
          disabled={loading}
        >
          {loading ? (
            <>
              <span className="spinner-border spinner-border-sm me-1"></span>
              Обновление...
            </>
          ) : (
            <>
              <i className="bi bi-arrow-clockwise me-1"></i>
              Обновить
            </>
          )}
        </button>
      </div>
      
      <div className="row row-cols-1 row-cols-md-2 row-cols-lg-3 g-4">
        {animals.map((animal) => (
          <div key={animal.id} className="col">
            <AnimalCard 
              animal={animal}
              onViewClick={() => {
                window.dispatchEvent(new CustomEvent('openAnimalModal', { 
                  detail: { animalId: animal.id } 
                }));
              }}
            />
          </div>
        ))}
      </div>
    </div>
  );
};

export default AnimalList;
