import React, { useState } from "react";

function Nahodka({ onCreateAnimalCard }) {
  const [foundAnimalData, setFoundAnimalData] = useState({
    name: "",
    location: "",
    description: "",
    age: "",
    gender: "",
    features: "",
    contact: "+7 (999) 999-99-99", // по умолчанию
    foundDate: new Date().toLocaleDateString('ru-RU', { 
      day: 'numeric', 
      month: 'long', 
      year: 'numeric' 
    }),
    animalType: "cat",
    file: null,
    fileName: "",
    imageUrl: ""
  });

  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const imageUrl = URL.createObjectURL(file);
      setFoundAnimalData({
        ...foundAnimalData,
        file: file,
        fileName: file.name,
        imageUrl: imageUrl
      });
    }
  };

  const handleSubmit = () => {
    // Проверка обязательных полей
    const requiredFields = ['description', 'location'];
    for (const field of requiredFields) {
      if (!foundAnimalData[field].trim()) {
        alert(`Пожалуйста, заполните поле: ${field === 'description' ? 'Описание' : 'Место находки'}`);
        return;
      }
    }

    setIsSubmitting(true);

    // Имитация отправки данных
    setTimeout(() => {
      // Создаем объект карточки в формате cat_card.jsx
      const newAnimalCard = {
        name: foundAnimalData.name || `Найденный ${foundAnimalData.animalType === 'cat' ? 'кот' : 
               foundAnimalData.animalType === 'dog' ? 'пёс' : 'питомец'}`,
        location: foundAnimalData.location,
        description: foundAnimalData.description,
        age: foundAnimalData.age || "не определен",
        gender: foundAnimalData.gender || "не определен",
        features: foundAnimalData.features || "Требуется уточнение",
        contact: foundAnimalData.contact,
        foundDate: foundAnimalData.foundDate,
        animalType: foundAnimalData.animalType,
        imageUrl: foundAnimalData.imageUrl,
        fileName: foundAnimalData.fileName
      };

      // Передаем созданную карточку родительскому компоненту
      if (onCreateAnimalCard) {
        onCreateAnimalCard(newAnimalCard);
      }

      alert(`Сообщение о находке отправлено!\n\nСоздана карточка: "${newAnimalCard.name}"`);

      // Сброс формы
      setFoundAnimalData({
        name: "",
        location: "",
        description: "",
        age: "",
        gender: "",
        features: "",
        contact: "+7 (999) 999-99-99",
        foundDate: new Date().toLocaleDateString('ru-RU', { 
          day: 'numeric', 
          month: 'long', 
          year: 'numeric' 
        }),
        animalType: "cat",
        file: null,
        fileName: "",
        imageUrl: ""
      });
      setIsSubmitting(false);

      // Закрываем модальное окно
      const closeBtn = document.querySelector('[data-bs-dismiss="modal"]');
      if (closeBtn) closeBtn.click();
    }, 1500);
  };

  return (
    <div>
      <div className="modal fade" id="newPost" tabIndex={-1} aria-labelledby="exampleModalLabel" aria-hidden="true">
        <div className="modal-dialog">
          <div className="modal-content">
            <div className="modal-header">
              <h1 className="modal-title fs-5" id="exampleModalLabel">Сообщить о находке</h1>
              <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Закрыть" />
            </div>
            <div className="modal-body">
              <form>
                <div className="mb-3">
                  <label htmlFor="animalType" className="form-label">Тип животного:</label>
                  <select 
                    className="form-select" 
                    id="animalType"
                    value={foundAnimalData.animalType}
                    onChange={(e) => setFoundAnimalData({...foundAnimalData, animalType: e.target.value})}
                  >
                    <option value="cat">Кот/Кошка</option>
                    <option value="dog">Собака</option>
                    <option value="rabbit">Кролик</option>
                    <option value="other">Другое</option>
                  </select>
                </div>

                <div className="mb-3">
                  <label htmlFor="name" className="form-label">Имя/Кличка (если известно):</label>
                  <input 
                    type="text" 
                    className="form-control" 
                    id="name"
                    placeholder="Например: Мурзик, Барсик и т.д."
                    value={foundAnimalData.name}
                    onChange={(e) => setFoundAnimalData({...foundAnimalData, name: e.target.value})}
                  />
                </div>

                <div className="mb-3">
                  <label htmlFor="age" className="form-label">Примерный возраст:</label>
                  <input 
                    type="text" 
                    className="form-control" 
                    id="age"
                    placeholder="Например: 2 месяца, 1 год и т.д."
                    value={foundAnimalData.age}
                    onChange={(e) => setFoundAnimalData({...foundAnimalData, age: e.target.value})}
                  />
                </div>

                <div className="mb-3">
                  <label htmlFor="gender" className="form-label">Пол:</label>
                  <select 
                    className="form-select" 
                    id="gender"
                    value={foundAnimalData.gender}
                    onChange={(e) => setFoundAnimalData({...foundAnimalData, gender: e.target.value})}
                  >
                    <option value="">Не определен</option>
                    <option value="мужской">Мужской</option>
                    <option value="женский">Женский</option>
                  </select>
                </div>

                <div className="mb-3">
                  <label htmlFor="location" className="form-label">Место находки*:</label>
                  <input 
                    type="text" 
                    className="form-control" 
                    id="location"
                    placeholder="Укажите точное место, где нашли животное"
                    value={foundAnimalData.location}
                    onChange={(e) => setFoundAnimalData({...foundAnimalData, location: e.target.value})}
                    required
                  />
                </div>

                <div className="mb-3">
                  <label htmlFor="description" className="col-form-label">
                    Описание животного*:
                  </label>
                  <textarea 
                    className="form-control" 
                    id="description" 
                    rows="4"
                    value={foundAnimalData.description}
                    onChange={(e) => setFoundAnimalData({...foundAnimalData, description: e.target.value})}
                    placeholder="Опишите животное, его поведение, состояние здоровья и т.д."
                    required
                  />
                </div>

                <div className="mb-3">
                  <label htmlFor="features" className="form-label">Особенности:</label>
                  <textarea 
                    className="form-control" 
                    id="features" 
                    rows="2"
                    value={foundAnimalData.features}
                    onChange={(e) => setFoundAnimalData({...foundAnimalData, features: e.target.value})}
                    placeholder="Например: приучен к лотку, знает команды, требуется лечение и т.д."
                  />
                </div>

                <div className="mb-3">
                  <label htmlFor="contact" className="form-label">Ваши контакты:</label>
                  <input 
                    type="text" 
                    className="form-control" 
                    id="contact"
                    placeholder="Телефон для связи"
                    value={foundAnimalData.contact}
                    onChange={(e) => setFoundAnimalData({...foundAnimalData, contact: e.target.value})}
                  />
                </div>

                <div className="mb-3">
                  <label htmlFor="file" className="col-form-label">Добавьте фото</label><br />
                  <input 
                    type="file" 
                    id="file" 
                    accept="image/*"
                    onChange={handleFileChange}
                  />
                  {foundAnimalData.fileName && (
                    <div className="mt-2">
                      <small>Выбран файл: {foundAnimalData.fileName}</small>
                      {foundAnimalData.imageUrl && (
                        <div className="mt-2">
                          <img 
                            src={foundAnimalData.imageUrl} 
                            alt="Предпросмотр" 
                            style={{maxWidth: '200px', maxHeight: '200px', borderRadius: '5px'}}
                          />
                        </div>
                      )}
                    </div>
                  )}
                </div>
              </form>
            </div>
            <div className="modal-footer">
              <button type="button" className="btn btn-danger" data-bs-dismiss="modal">Закрыть</button>
              <button 
                type="button" 
                className="btn btn-dark"
                onClick={handleSubmit}
                disabled={isSubmitting}
              >
                {isSubmitting ? "Создание..." : "Отправить и создать карточку"}
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Nahodka;
