import React, { useState } from "react";

function Svyz({ siteInfo }) {
  const [messageData, setMessageData] = useState({
    recipient: siteInfo?.email || "helpAnimal@mail.ru",
    message: ""
  });

  const handleSendMessage = () => {
    if (messageData.message.trim() === "") {
      alert("Пожалуйста, введите сообщение");
      return;
    }

    alert(`Сообщение отправлено на ${messageData.recipient}\n\nСообщение: ${messageData.message}`);
    setMessageData({ ...messageData, message: "" });
  };

  return (
    <div>
      <div className="modal fade" id="contacts" tabIndex={-1} aria-labelledby="exampleModalLabel" aria-hidden="true">
        <div className="modal-dialog">
          <div className="modal-content">
            <div className="modal-header">
              <h1 className="modal-title fs-5" id="exampleModalLabel">Контакты</h1>
              <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Закрыть" />
            </div>
            <div className="modal-body">
              <form>
                <div className="mb-3">
                  <label htmlFor="recipient-name" className="col-form-label">Получатель:</label>
                  <input 
                    type="text" 
                    className="form-control" 
                    value={messageData.recipient}
                    onChange={(e) => setMessageData({...messageData, recipient: e.target.value})}
                  />
                </div>
                <div className="mb-3">
                  <label htmlFor="message-text" className="col-form-label">Сообщение:</label>
                  <textarea 
                    className="form-control" 
                    id="message-text" 
                    value={messageData.message}
                    onChange={(e) => setMessageData({...messageData, message: e.target.value})}
                    rows="4"
                  />
                </div>
              </form>

              <ul className="list-group mt-3">
                <h5><li className="list-group-item">Вы также можете позвонить нам в любое время</li></h5>
                <li className="list-group-item">{siteInfo?.phone || "+7 (931) 562-231-32"}</li>
                <li className="list-group-item">{siteInfo?.phone || "+7 (931) 562-231-32"}</li>
              </ul>
            </div>

            <div className="modal-footer">
              <button type="button" className="btn btn-danger" data-bs-dismiss="modal">Закрыть</button>
              <button 
                type="button" 
                className="btn btn-dark"
                onClick={handleSendMessage}
              >
                Отправить
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Svyz;
