import React, { useState } from "react";
import image2 from './image/image2.jfif';
import image3 from './image/image3.jfif';
import image from './image/image.jfif';
import Newsletter from './Newsletter';

function Content({ onNewsletterSubmit, showNewsletter, isLoading }) {
  const [animals] = useState([
    {
      id: 1,
      title: "Котенок, найден 12 октября",
      description: "Маленький белый котенок, найден в районе ул. Пушкина. Очень игривый и ласковый.",
      image: image,
      date: "12 октября 2025",
      type: "котенок"
    },
    {
      id: 2,
      title: "Собака породы мопс, найдена 8 октября",
      description: "Дружелюбная собака, найдена в пригороде. Знает основные команды, приучена к поводку.",
      image: image2,
      date: "8 октября 2025",
      type: "собака"
    },
    {
      id: 3,
      title: "Кролик, найден 5 октября",
      description: "Красивый Кролик, найден в центре города. Очень пушистый.",
      image: image3,
      date: "5 октября 2025",
      type: "кролик"
    }
  ]);

  return (
    <div>
      <div className="container mt-5">
        <div className="row mb-5">
          <div className="col-12 text-center">
            <h2>Помогите найти хозяев для этих животных</h2>
          </div>
        </div>
        <div className="row mb-5">
          {animals.map(animal => (
            <div key={animal.id} className="col-md-4 mb-4">
              <div className="card animal-card h-100 d-flex flex-column">
                <img 
                  src={animal.image} 
                  className="card-img-top" 
                  alt={animal.type}
                  style={{
                    width: '100%',
                    height: '300px',
                    objectFit: 'cover'
                  }}
                />
                <div className="card-body d-flex flex-column">
                  <h5 className="card-title">{animal.title}</h5>
                  <p className="card-text flex-grow-1">{animal.description}</p>
                  <div className="mt-auto">
                    <button className="btn btn-dark me-3" data-bs-toggle="modal" data-bs-target="#cart">
                      Подробнее
                    </button>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
        <div className="row mb-5">
          <div className="col-12">
            <div className="jumbotron jumbotron-fluid text-center rounded bg-dark text-white">
              <div className="container">
                <h1 className="display-4">Помогите животным найти дом!</h1>
                <p className="lead">Если вы потеряли своего питомца или готовы приютить одного из найденных животных, свяжитесь с нами.</p>
                <button type="button" className="btn btn-light" data-bs-toggle="modal" data-bs-target="#contacts">
                  Связаться с нами
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>

      {showNewsletter && (
        <Newsletter 
          onNewsletterSubmit={onNewsletterSubmit}
          isLoading={isLoading}
        />
      )}
    </div>
  );
}

export default Content;
