import React, { useState, useEffect } from "react";
import image from './image/image.jfif';
import image2 from './image/image3.jfif';
import image1 from './image/image2.jfif';

function Find_animal({ filters }) {
  const [animals, setAnimals] = useState([]);
  const [filteredAnimals, setFilteredAnimals] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Загружаем данные с API
  useEffect(() => {
    fetchAnimals();
  }, []);

  const fetchAnimals = async () => {
    try {
      setLoading(true);
      const response = await fetch('https://pets.xn--80ahdri7a.site/api/pets', {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json'
        }
      });
      
      if (!response.ok) {
        throw new Error('Ошибка при загрузке данных');
      }
      
      const data = await response.json();
      
      // Преобразуем данные API в формат нашего приложения
      const apiAnimals = data.data.orders
        .filter(animal => animal.registred && animal.photo) // Только зарегистрированные с фото
        .map(animal => {
          const imageUrl = animal.photo ? `https://pets.xn--80ahdri7a.site${animal.photo}` : null;
          
          // Проверяем, существует ли изображение
          return {
            id: animal.id,
            title: `${animal.kind}, найдено ${formatDate(animal.date)}`,
            description: animal.description,
            image: imageUrl,
            date: formatDateForSort(animal.date),
            type: getAnimalTypeFromKind(animal.kind),
            age: "неизвестно",
            phone: animal.phone,
            name: animal.name,
            mark: animal.mark,
            photo: animal.photo,
            hasValidImage: false // Флаг для проверки валидности изображения
          };
        })
        .sort((a, b) => new Date(b.date) - new Date(a.date)); // Сортировка по убыванию даты
      
      // Проверяем загрузку изображений
      const animalsWithImageCheck = await Promise.all(
        apiAnimals.map(async (animal) => {
          try {
            if (animal.image) {
              const imgCheck = await checkImageExists(animal.image);
              return {
                ...animal,
                hasValidImage: imgCheck
              };
            }
            return {
              ...animal,
              hasValidImage: false
            };
          } catch {
            return {
              ...animal,
              hasValidImage: false
            };
          }
        })
      );
      
      // Фильтруем только карточки с валидными изображениями
      const validAnimals = animalsWithImageCheck.filter(animal => animal.hasValidImage);
      
      setAnimals(validAnimals);
      setFilteredAnimals(validAnimals);
    } catch (error) {
      console.error('Ошибка при загрузке животных:', error);
      setError('Не удалось загрузить данные о животных');
    } finally {
      setLoading(false);
    }
  };

  // Функция для проверки существования изображения
  const checkImageExists = (url) => {
    return new Promise((resolve) => {
      const img = new Image();
      img.onload = () => resolve(true);
      img.onerror = () => resolve(false);
      img.src = url;
    });
  };

  // Функция для получения типа животного (cat/dog/rabbit/other) из kind
  const getAnimalTypeFromKind = (kind) => {
    const kindLower = kind.toLowerCase();
    if (kindLower.includes('кошка') || kindLower.includes('кот')) return 'cat';
    if (kindLower.includes('собака')) return 'dog';
    if (kindLower.includes('кролик')) return 'rabbit';
    return 'other';
  };

  // Форматирование даты для отображения
  const formatDate = (dateString) => {
    const [day, month, year] = dateString.split('-');
    return `${day}.${month}.${year}`;
  };

  // Форматирование даты для сортировки
  const formatDateForSort = (dateString) => {
    const [day, month, year] = dateString.split('-');
    return `${year}-${month}-${day}`;
  };

  useEffect(() => {
    let result = animals;
    
    if (filters.animalType !== 'all') {
      result = result.filter(animal => animal.type === filters.animalType);
    }
    
    if (filters.dateRange !== 'all') {
      const now = new Date();
      result = result.filter(animal => {
        const animalDate = new Date(animal.date);
        const diffTime = Math.abs(now - animalDate);
        const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
        
        if (filters.dateRange === 'week') return diffDays <= 7;
        if (filters.dateRange === 'month') return diffDays <= 30;
        if (filters.dateRange === 'older') return diffDays > 30;
        return true;
      });
    }
    
    setFilteredAnimals(result);
  }, [filters, animals]);

  const [sortOrder, setSortOrder] = useState('newest');

  const handleSort = (order) => {
    setSortOrder(order);
    const sorted = [...filteredAnimals].sort((a, b) => {
      if (order === 'newest') {
        return new Date(b.date) - new Date(a.date);
      } else {
        return new Date(a.date) - new Date(b.date);
      }
    });
    setFilteredAnimals(sorted);
  };

  if (loading) {
    return (
      <div className="container mt-5 text-center">
        <div className="spinner-border text-dark" role="status">
          <span className="visually-hidden">Загрузка...</span>
        </div>
        <p className="mt-2">Загрузка данных о животных...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container mt-5">
        <div className="alert alert-danger" role="alert">
          {error}
        </div>
      </div>
    );
  }

  return (
    <div>
      <div className="container mt-5">
        <div className="row mb-5">
          <div className="col-12 text-center">
            <h2>Помогите найти хозяев для этих животных</h2>
            <p className="lead">
              Найдено животных: {filteredAnimals.length}
              <button 
                className="btn btn-sm btn-outline-dark ms-3"
                onClick={() => handleSort(sortOrder === 'newest' ? 'oldest' : 'newest')}
              >
                Сортировать по {sortOrder === 'newest' ? 'старым' : 'новым'}
              </button>
            </p>
          </div>
        </div>
        
        {filteredAnimals.length === 0 ? (
          <div className="text-center">
            <h4>Животные по выбранным фильтрам не найдены</h4>
            <p>Попробуйте изменить параметры фильтрации</p>
          </div>
        ) : (
          <div className="row mb-5">
            {filteredAnimals.slice(0, 6).map(animal => ( // Показываем только 6 животных
              <div key={animal.id} className="col-md-4 mb-4">
                <div className="card animal-card h-100 d-flex flex-column">
                  <img 
                    src={animal.image} 
                    className="card-img-top" 
                    alt={animal.type}
                    style={{
                      width: '100%',
                      height: '300px',
                      objectFit: 'cover'
                    }}
                    onError={(e) => {
                      // Если изображение не загрузилось, скрываем карточку
                      e.target.parentElement.parentElement.style.display = 'none';
                    }}
                  />
                  <div className="card-body d-flex flex-column">
                    <h5 className="card-title">{animal.title}</h5>
                    <p className="card-text flex-grow-1">{animal.description}</p>
                    <div className="mb-2">
                      <span className="badge bg-secondary me-2">{animal.age}</span>
                      {animal.mark && <span className="badge bg-warning text-dark ms-2">Метка: {animal.mark}</span>}
                    </div>
                    <div className="mt-auto">
                      <button 
                        className="btn btn-dark me-3" 
                        data-bs-toggle="modal" 
                        data-bs-target="#cart"
                        onClick={() => {
                          alert(`Контакты: ${animal.phone}\nНашедший: ${animal.name}\nДата находки: ${formatDate(animal.date)}`);
                        }}
                      >
                        Подробнее
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

export default Find_animal;
