// AuthModal.jsx - основной компонент аутентификации
import React, { useState } from "react";
import Icon2 from './image/icon/mail.ico';
import Icon3 from './image/icon/z.ico';
import RegistrationForm from "./register";

function AuthModal({ userInfo, onLogin, onRegister }) {
  const [isLoginMode, setIsLoginMode] = useState(true);
  const [loginData, setLoginData] = useState({
    email: "",
    password: ""
  });
  const [errors, setErrors] = useState({});
  const [apiError, setApiError] = useState("");
  const [successMessage, setSuccessMessage] = useState("");
  const [isSubmitting, setIsSubmitting] = useState(false);

  // Валидация формы входа
  const validateLogin = () => {
    const newErrors = {};
    if (!loginData.email || !loginData.email.includes('@')) {
      newErrors.loginEmail = "Некорректный email";
    }
    if (!loginData.password || loginData.password.length < 6) {
      newErrors.loginPassword = "Пароль должен быть не менее 6 символов";
    }
    return newErrors;
  };

  // Обработка входа через API
  const handleLogin = async () => {
    // Сбрасываем предыдущие сообщения
    setApiError("");
    setSuccessMessage("");
    setIsSubmitting(true);
    
    // Валидация на клиенте
    const validationErrors = validateLogin();
    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors);
      setIsSubmitting(false);
      return;
    }

    try {
      // Используем правильный URL для API
      const response = await fetch('https://pets.xn--80ahdri7a.site/api/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json'
        },
        body: JSON.stringify({
          email: loginData.email,
          password: loginData.password
        })
      });

      // Пробуем получить ответ как JSON, но проверяем статус
      let data;
      const contentType = response.headers.get('content-type');
      
      if (contentType && contentType.includes('application/json')) {
        data = await response.json();
      } else {
        // Если ответ не JSON, пробуем получить текст
        const text = await response.text();
        console.warn('Response is not JSON:', text);
        data = {};
      }

      if (response.ok) {
        // Успешная аутентификация
        const token = data.data?.token || data.token;
        
        if (token) {
          // Сохраняем токен
          localStorage.setItem('authToken', token);
          
          // Сохраняем данные пользователя из ответа
          if (data.data?.user) {
            localStorage.setItem('userData', JSON.stringify(data.data.user));
          } else if (data.user) {
            localStorage.setItem('userData', JSON.stringify(data.user));
          } else {
            // Если сервер не возвращает данные пользователя, создаем минимальный объект
            localStorage.setItem('userData', JSON.stringify({
              email: loginData.email,
              // Другие данные можно будет получить отдельным запросом
            }));
          }
          
          // Вызываем колбэк onLogin с данными
          if (onLogin) {
            onLogin({
              token,
              userData: JSON.parse(localStorage.getItem('userData'))
            });
          }
          
          // Показываем сообщение об успехе
          setSuccessMessage("Успешный вход в систему!");
          
          // Закрываем модальное окно через 1.5 секунды
          setTimeout(() => {
            const closeBtn = document.getElementById('closeAuthModal');
            if (closeBtn) {
              closeBtn.click();
            }
          }, 1500);
        } else {
          setApiError("Токен не получен от сервера");
        }

      } else if (response.status === 422) {
        // Ошибка валидации
        setApiError("Ошибка валидации данных");
        if (data.error && data.error.errors) {
          const apiErrors = {};
          Object.keys(data.error.errors).forEach(key => {
            if (key === 'email') apiErrors.loginEmail = data.error.errors[key].join(', ');
            if (key === 'password') apiErrors.loginPassword = data.error.errors[key].join(', ');
          });
          setErrors(apiErrors);
        } else {
          setApiError(data.message || "Ошибка валидации");
        }

      } else if (response.status === 401) {
        // Неавторизован
        setApiError("Неверный email или пароль");
        setErrors({
          loginEmail: "Неверный email или пароль",
          loginPassword: "Неверный email или пароль"
        });

      } else if (response.status === 404) {
        // Endpoint не найден
        setApiError("Сервер авторизации временно недоступен");

      } else {
        // Другие ошибки
        setApiError(data.error?.message || data.message || `Ошибка сервера: ${response.status}`);
      }

    } catch (error) {
      console.error('Ошибка при входе:', error);
      
      // Более конкретные сообщения об ошибках
      if (error.name === 'TypeError' && error.message.includes('Failed to fetch')) {
        setApiError("Ошибка подключения к серверу. Проверьте интернет-соединение.");
      } else if (error.name === 'SyntaxError') {
        setApiError("Ошибка обработки ответа сервера");
      } else {
        setApiError("Ошибка соединения с сервером");
      }
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleChangeLogin = (field, value) => {
    setLoginData(prev => ({ ...prev, [field]: value }));
    
    // Сбрасываем ошибки при изменении поля
    if (errors[`login${field}`]) {
      setErrors(prev => ({ ...prev, [`login${field}`]: null }));
    }
    
    // Сбрасываем общую ошибку API
    if (apiError) {
      setApiError("");
    }
  };

  // Обработчик отправки формы
  const handleSubmit = (e) => {
    e.preventDefault();
    if (isLoginMode) {
      handleLogin();
    }
  };

  // Очистка при закрытии
  const handleClose = () => {
    setErrors({});
    setApiError("");
    setSuccessMessage("");
    setLoginData({ email: "", password: "" });
    setIsSubmitting(false);
  };

  return (
    <div className="modal fade" id="authModal" tabIndex={-1} aria-labelledby="authModalLabel" aria-hidden="true">
      <div className="modal-dialog">
        <div className="modal-content">
          <div className="modal-header">
            <h1 className="modal-title fs-5" id="authModalLabel">
              {isLoginMode ? "Вход в аккаунт" : "Регистрация"}
            </h1>
            <button 
              id="closeAuthModal"
              type="button" 
              className="btn-close" 
              data-bs-dismiss="modal" 
              aria-label="Закрыть"
              onClick={handleClose}
            />
          </div>
          
          <div className="modal-body">
            {/* Переключение между входом и регистрацией */}
            <div className="mb-4">
              <div className="btn-group w-100" role="group">
                <button
                  type="button"
                  className={`btn ${isLoginMode ? 'btn-dark' : 'btn-outline-dark'}`}
                  onClick={() => {
                    setIsLoginMode(true);
                    setErrors({});
                    setApiError("");
                    setSuccessMessage("");
                  }}
                  disabled={isSubmitting}
                >
                  Вход
                </button>
                <button
                  type="button"
                  className={`btn ${!isLoginMode ? 'btn-dark' : 'btn-outline-dark'}`}
                  onClick={() => {
                    setIsLoginMode(false);
                    setErrors({});
                    setApiError("");
                    setSuccessMessage("");
                  }}
                  disabled={isSubmitting}
                >
                  Регистрация
                </button>
              </div>
            </div>

            {/* Форма входа */}
            {isLoginMode ? (
              <form onSubmit={handleSubmit}>
                {/* Сообщение об успехе */}
                {successMessage && (
                  <div className="alert alert-success" role="alert">
                    {successMessage}
                  </div>
                )}
                
                {/* Сообщение об ошибке API */}
                {apiError && !successMessage && (
                  <div className="alert alert-danger" role="alert">
                    {apiError}
                  </div>
                )}
                
                <div className="input-group flex-nowrap mb-3">
                  <span className="input-group-text">
                    <img src={Icon2} alt="Email" width="20" height="20" />
                  </span>
                  <input 
                    type="email" 
                    className={`form-control ${errors.loginEmail ? 'is-invalid' : ''}`}
                    placeholder="Email" 
                    value={loginData.email}
                    onChange={(e) => handleChangeLogin('email', e.target.value)}
                    required
                    disabled={isSubmitting}
                  />
                  {errors.loginEmail && <div className="invalid-feedback">{errors.loginEmail}</div>}
                </div>
                
                <div className="input-group flex-nowrap mb-3">
                  <span className="input-group-text">
                    <img src={Icon3} alt="Пароль" width="20" height="20" />
                  </span>
                  <input 
                    type="password" 
                    className={`form-control ${errors.loginPassword ? 'is-invalid' : ''}`}
                    placeholder="Пароль" 
                    value={loginData.password}
                    onChange={(e) => handleChangeLogin('password', e.target.value)}
                    required
                    disabled={isSubmitting}
                  />
                  {errors.loginPassword && <div className="invalid-feedback">{errors.loginPassword}</div>}
                </div>
                
                <div className="form-text mb-3">
                  <p className="mb-1"><strong>Тестовый аккаунт:</strong></p>
                  <p className="mb-0">Email: user@user.rl</p>
                  <p className="mb-0">Пароль: paSSword1</p>
                </div>
              </form>
            ) : (
              <RegistrationForm/>
            )}
          </div>
          
          <div className="modal-footer">
            <button 
              type="button" 
              className="btn btn-danger" 
              data-bs-dismiss="modal"
              onClick={handleClose}
              disabled={isSubmitting}
            >
              Закрыть
            </button>
            <button 
              type="button" 
              className="btn btn-dark"
              onClick={isLoginMode ? handleLogin : () => {
                // При регистрации вызываем функцию onRegister, если она передана
                if (onRegister) onRegister();
              }}
              disabled={isSubmitting}
            >
              {isSubmitting ? (
                <>
                  <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span>
                  {isLoginMode ? "Вход..." : "Регистрация..."}
                </>
              ) : (
                isLoginMode ? "Войти" : "Зарегистрироваться"
              )}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default AuthModal;
