import React, { useState, useEffect } from "react";
import './css/style.css';

function Slider() {
  const [pets, setPets] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Функция для загрузки данных о животных
  const fetchPetsData = async () => {
    try {
      setLoading(true);
      setError(null);
      
      const url = 'https://pets.xn--80ahdri7a.site/api/pets/slider';
      const response = await fetch(url, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json'
        }
      });

      if (!response.ok) {
        throw new Error(`Ошибка загрузки: ${response.status}`);
      }

      const result = await response.json();
      
      // Проверяем наличие данных
      if (result.data && Array.isArray(result.data.pets)) {
        setPets(result.data.pets);
      } else {
        setPets([]);
      }
      
    } catch (error) {
      console.error('Ошибка при загрузке слайдера:', error);
      setError('Не удалось загрузить объявления. Попробуйте позже.');
      setPets([]);
    } finally {
      setLoading(false);
    }
  };

  // Загружаем данные при монтировании компонента
  useEffect(() => {
    fetchPetsData();
  }, []);

  // Если загрузка
  if (loading) {
    return (
      <div className="text-center py-5">
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Загрузка...</span>
        </div>
        <p className="mt-2">Загрузка объявлений...</p>
      </div>
    );
  }

  // Если ошибка
  if (error) {
    return (
      <div className="alert alert-warning text-center" role="alert">
        {error}
      </div>
    );
  }

  // Если нет данных
  if (!pets || pets.length === 0) {
    return null; // Блок не отображается
  }

  return (
    <div>
      <div id="animalCarousel" className="carousel slide" data-bs-ride="carousel">
        {/* Индикаторы слайдов */}
        <div className="carousel-indicators">
          {pets.map((pet, index) => (
            <button 
              key={pet.id}
              type="button" 
              data-bs-target="#animalCarousel" 
              data-bs-slide-to={index}
              className={index === 0 ? "active" : ""}
              aria-current={index === 0 ? "true" : "false"}
              aria-label={`Слайд ${index + 1}`}
            />
          ))}
        </div>

        {/* Слайды */}
        <div className="carousel-inner">
          {pets.map((pet, index) => (
            <div 
              key={pet.id}
              className={`carousel-item ${index === 0 ? "active" : ""}`}
            >
              {pet.image ? (
                <img 
                  src={url} 
                  className="d-block w-100" 
                  alt={`${pet.kind} - ${pet.description.substring(0, 50)}...`} 
                />
              ) : (
                <div className="d-block w-100 bg-secondary" style={{ height: "400px" }}>
                  <div className="h-100 d-flex align-items-center justify-content-center text-white">
                    <i className="bi bi-image fs-1"></i>
                    <span className="ms-2">Изображение отсутствует</span>
                  </div>
                </div>
              )}
              
              <div className="carousel-caption d-none d-md-block bg-dark bg-opacity-50 rounded p-3">
                <h5>{pet.kind ? `${pet.kind}` : "Животное"}</h5>
                <p>{pet.description || "Описание отсутствует"}</p>
              </div>
            </div>
          ))}
        </div>

        {/* Кнопки управления */}
        <button className="carousel-control-prev" type="button" data-bs-target="#animalCarousel" data-bs-slide="prev">
          <span className="carousel-control-prev-icon" aria-hidden="true" />
          <span className="visually-hidden">Предыдущий</span>
        </button>
        <button className="carousel-control-next" type="button" data-bs-target="#animalCarousel" data-bs-slide="next">
          <span className="carousel-control-next-icon" aria-hidden="true" />
          <span className="visually-hidden">Следующий</span>
        </button>
      </div>
    </div>
  );
}

export default Slider;
