import React, { useState, useEffect } from 'react';
import { useAuth } from '../auth/AuthContext';
import UserOrders from './UserOrders';

const ProfileModal = () => {
  const { user, updateUserData } = useAuth();
  const [userData, setUserData] = useState({
    name: '',
    email: '',
    phone: ''
  });
  const [userCreatedAt, setUserCreatedAt] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [message, setMessage] = useState({ type: '', text: '' });

  useEffect(() => {
    if (user) {
      fetchUserProfile();
    }
  }, [user]);

  const fetchUserProfile = async () => {
    if (!user?.token) return;
    
    try {
      const response = await fetch('https://pets.xn--80ahdri7a.site/api/users', {
        headers: { 'Authorization': `Bearer ${user.token}` }
      });
      
      if (response.ok) {
        const data = await response.json();
        setUserData({
          name: data.name || '',
          email: data.email || '',
          phone: data.phone || ''
        });
        // Сохраняем дату регистрации пользователя
        if (data.created_at) {
          setUserCreatedAt(data.created_at);
        }
      }
    } catch (error) {
      console.error('Error fetching profile:', error);
    }
  };

  // Функция для форматирования времени с момента регистрации
  const formatTimeSinceRegistration = () => {
    if (!userCreatedAt) return 'Неизвестно';
    
    const createdAt = new Date(userCreatedAt);
    const now = new Date();
    const diffMs = now - createdAt;
    const diffDays = Math.floor(diffMs / (1000 * 60 * 60 * 24));
    const diffHours = Math.floor((diffMs % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
    const diffMinutes = Math.floor((diffMs % (1000 * 60 * 60)) / (1000 * 60));
    const diffSeconds = Math.floor((diffMs % (1000 * 60)) / 1000);

    if (diffDays > 0) {
      const years = Math.floor(diffDays / 365);
      const months = Math.floor((diffDays % 365) / 30);
      const days = diffDays % 30;

      let result = '';
      if (years > 0) result += `${years} год${getRussianPlural(years, ['год', 'года', 'лет'])} `;
      if (months > 0) result += `${months} месяц${getRussianPlural(months, ['месяц', 'месяца', 'месяцев'])} `;
      if (days > 0) result += `${days} день${getRussianPlural(days, ['день', 'дня', 'дней'])}`;
      
      return result.trim();
    } else if (diffHours > 0) {
      return `${diffHours} час${getRussianPlural(diffHours, ['час', 'часа', 'часов'])} ${diffMinutes} минут${getRussianPlural(diffMinutes, ['у', 'ы', ''])}`;
    } else if (diffMinutes > 0) {
      return `${diffMinutes} минут${getRussianPlural(diffMinutes, ['а', 'ы', ''])}`;
    } else {
      return `${diffSeconds} секунд${getRussianPlural(diffSeconds, ['а', 'ы', ''])}`;
    }
  };

  // Функция для получения правильного склонения русского слова
  const getRussianPlural = (number, words) => {
    const cases = [2, 0, 1, 1, 1, 2];
    return words[(number % 100 > 4 && number % 100 < 20) ? 2 : cases[(number % 10 < 5) ? number % 10 : 5]];
  };

  // Функция для форматирования даты регистрации
  const formatRegistrationDate = () => {
    if (!userCreatedAt) return 'Неизвестно';
    
    const date = new Date(userCreatedAt);
    return date.toLocaleDateString('ru-RU', {
      day: 'numeric',
      month: 'long',
      year: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const validateForm = () => {
    const errors = {};
    
    if (!userData.email.trim()) {
      errors.email = 'Email обязателен';
    } else if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(userData.email)) {
      errors.email = 'Некорректный email';
    }
    
    if (!userData.phone.trim()) {
      errors.phone = 'Телефон обязателен';
    }
    
    return errors;
  };

  const handleSave = async () => {
    const errors = validateForm();
    if (Object.keys(errors).length > 0) {
      setMessage({ type: 'danger', text: 'Исправьте ошибки в форме' });
      return;
    }
    
    setIsLoading(true);
    setMessage({ type: '', text: '' });
    
    try {
      // Обновление email
      if (userData.email !== user.email) {
        const emailResponse = await fetch('https://pets.xn--80ahdri7a.site/api/users/email', {
          method: 'PATCH',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${user.token}`
          },
          body: JSON.stringify({ email: userData.email })
        });
        
        if (!emailResponse.ok) {
          throw new Error('Ошибка при обновлении email');
        }
      }
      
      // Обновление телефона
      if (userData.phone !== user.phone) {
        const phoneResponse = await fetch('https://pets.xn--80ahdri7a.site/api/users/phone', {
          method: 'PATCH',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${user.token}`
          },
          body: JSON.stringify({ phone: userData.phone })
        });
        
        if (!phoneResponse.ok) {
          throw new Error('Ошибка при обновлении телефона');
        }
      }
      
      // Обновляем данные в контексте
      updateUserData({
        email: userData.email,
        phone: userData.phone,
        name: userData.name
      });
      
      setMessage({ type: 'success', text: 'Данные успешно обновлены!' });
      setIsEditing(false);
      
      // Обновляем данные профиля
      fetchUserProfile();
    } catch (error) {
      setMessage({ type: 'danger', text: error.message || 'Ошибка при обновлении данных' });
    } finally {
      setIsLoading(false);
    }
  };

  const handleCancel = () => {
    setUserData({
      name: user?.name || '',
      email: user?.email || '',
      phone: user?.phone || ''
    });
    setIsEditing(false);
    setMessage({ type: '', text: '' });
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUserData(prev => ({ ...prev, [name]: value }));
  };

  return (
    <div className="modal fade" id="profileModal" tabIndex="-1">
      <div className="modal-dialog modal-lg">
        <div className="modal-content">
          <div className="modal-header">
            <h5 className="modal-title">
              {user ? `Личный кабинет - ${user.name}` : 'Личный кабинет'}
            </h5>
            <button type="button" className="btn-close" data-bs-dismiss="modal" />
          </div>
          
          <div className="modal-body">
            {!user ? (
              <div className="text-center p-4">
                <p>Для доступа к личному кабинету необходимо войти в систему.</p>
                <button 
                  className="btn btn-primary"
                  data-bs-toggle="modal"
                  data-bs-target="#authModal"
                  data-bs-dismiss="modal"
                >
                  Войти / Зарегистрироваться
                </button>
              </div>
            ) : (
              <>
                {message.text && (
                  <div className={`alert alert-${message.type}`}>
                    {message.text}
                  </div>
                )}
                
                <div className="row">
                  <div className="col-md-6">
                    <h5>Данные пользователя</h5>
                    <div className="mb-3">
                      <label className="form-label">Имя</label>
                      <input
                        type="text"
                        className="form-control"
                        value={userData.name}
                        disabled
                        readOnly
                      />
                      <small className="text-muted">Изменить имя можно только при регистрации</small>
                    </div>
                    
                    <div className="mb-3">
                      <label className="form-label">Email *</label>
                      <input
                        type="email"
                        className={`form-control ${message.type === 'danger' && !userData.email ? 'is-invalid' : ''}`}
                        name="email"
                        value={userData.email}
                        onChange={handleInputChange}
                        disabled={!isEditing || isLoading}
                      />
                      {isEditing && (
                        <small className="text-muted">Обязательное поле, должен быть валидным email</small>
                      )}
                    </div>
                    
                    <div className="mb-3">
                      <label className="form-label">Телефон *</label>
                      <input
                        type="tel"
                        className={`form-control ${message.type === 'danger' && !userData.phone ? 'is-invalid' : ''}`}
                        name="phone"
                        value={userData.phone}
                        onChange={handleInputChange}
                        disabled={!isEditing || isLoading}
                        placeholder="+79112345678"
                      />
                      {isEditing && (
                        <small className="text-muted">Обязательное поле</small>
                      )}
                    </div>
                    
                    <div className="mt-4 p-3 border rounded bg-light">
                      <h6>Информация об аккаунте</h6>
                      <div className="mt-2">
                        <strong>ID пользователя:</strong> {user.id || 'Не указан'}<br />
                        <strong>Статус:</strong> Активен<br />
                        <strong>Дата регистрации:</strong> {formatRegistrationDate()}<br />
                        <strong>На сайте:</strong> {formatTimeSinceRegistration()}
                      </div>
                      {userCreatedAt && (
                        <div className="mt-2">
                          <div className="progress" style={{ height: '6px' }}>
                            <div 
                              className="progress-bar bg-success" 
                              role="progressbar" 
                              style={{ 
                                width: `${Math.min(100, Math.floor((new Date() - new Date(userCreatedAt)) / (1000 * 60 * 60 * 24 * 365)))}%` 
                              }}
                              aria-valuenow={Math.min(100, Math.floor((new Date() - new Date(userCreatedAt)) / (1000 * 60 * 60 * 24 * 365)))}
                              aria-valuemin="0"
                              aria-valuemax="100"
                            />
                          </div>
                          <small className="text-muted">
                            Ваш прогресс: {Math.min(100, Math.floor((new Date() - new Date(userCreatedAt)) / (1000 * 60 * 60 * 24 * 365)))}% года
                          </small>
                        </div>
                      )}
                    </div>
                  </div>
                  
                  <div className="col-md-6">
                    <UserOrders />
                  </div>
                </div>
              </>
            )}
          </div>
          
          <div className="modal-footer">
            <button 
              type="button" 
              className="btn btn-secondary" 
              data-bs-dismiss="modal"
              onClick={handleCancel}
              disabled={isLoading}
            >
              Закрыть
            </button>
            
            {user && (
              <>
                <button 
                  type="button" 
                  className="btn btn-warning"
                  onClick={() => isEditing ? handleCancel() : setIsEditing(true)}
                  disabled={isLoading}
                >
                  {isEditing ? 'Отменить' : 'Редактировать'}
                </button>
                <button 
                  type="button" 
                  className="btn btn-primary"
                  onClick={handleSave}
                  disabled={!isEditing || isLoading}
                >
                  {isLoading ? 'Сохранение...' : 'Сохранить'}
                </button>
              </>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProfileModal;
