import React, { useState } from 'react';
import "./css/register.css"
const RegistrationForm = ({ onRegistrationSuccess }) => {
  // Состояния для полей формы
  const [name, setName] = useState('');
  const [phone, setPhone] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [passwordConfirmation, setPasswordConfirmation] = useState('');
  const [confirm, setConfirm] = useState(0);
  const [error, setError] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);

  // Валидация на клиенте
  const validateFormData = (data) => {
    const errors = [];
    
    // Валидация имени (кириллица, пробел, дефис)
    if (!data.name || data.name.trim() === '') {
      errors.push('Имя обязательно для заполнения');
    } else if (!/^[А-Яа-яЁё\s-]+$/.test(data.name)) {
      errors.push('Имя должно содержать только кириллицу, пробелы и дефисы');
    }
    
    // Валидация телефона (только цифры и +)
    if (!data.phone || data.phone.trim() === '') {
      errors.push('Телефон обязателен для заполнения');
    } else if (!/^[\d+]+$/.test(data.phone)) {
      errors.push('Телефон должен содержать только цифры и знак +');
    }
    
    // Валидация email
    if (!data.email || data.email.trim() === '') {
      errors.push('Email обязателен для заполнения');
    } else if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(data.email)) {
      errors.push('Некорректный формат email');
    }
    
    // Валидация пароля
    if (!data.password || data.password.trim() === '') {
      errors.push('Пароль обязателен для заполнения');
    } else {
      if (data.password.length < 7) {
        errors.push('Пароль должен содержать не менее 7 символов');
      }
      if (!/\d/.test(data.password)) {
        errors.push('Пароль должен содержать хотя бы одну цифру');
      }
      if (!/[a-z]/.test(data.password)) {
        errors.push('Пароль должен содержать хотя бы одну строчную букву');
      }
      if (!/[A-Z]/.test(data.password)) {
        errors.push('Пароль должен содержать хотя бы одну заглавную букву');
      }
    }
    
    // Проверка совпадения паролей
    if (data.password !== data.password_confirmation) {
      errors.push('Пароли не совпадают');
    }
    
    // Проверка согласия
    if (data.confirm !== 1) {
      errors.push('Необходимо согласие на обработку персональных данных');
    }
    
    return errors;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccessMessage('');
    setIsSubmitting(true);

    // Собираем все данные формы согласно требованиям
    const requestData = {
      name: name.trim(),
      phone: phone.trim(),
      email: email.trim(),
      password: password,
      password_confirmation: passwordConfirmation,
      confirm: confirm
    };

    // Валидация на клиенте
    const validationErrors = validateFormData(requestData);
    if (validationErrors.length > 0) {
      setError(validationErrors.join('. '));
      setIsSubmitting(false);
      return; // не отправляем запрос
    }

    const url = 'https://pets.xn--80ahdri7a.site/api/register';

    try {
      const response = await fetch(url, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(requestData)
      });

      const result = await response.json();

      if (response.ok) {
        // Успешная регистрация
        if (result.data && result.data.token) {
          // Сохраняем токен
          const token = result.data.token.trim();
          localStorage.setItem('authToken', token);
          
          // Вызываем callback с токеном
          if (onRegistrationSuccess) {
            onRegistrationSuccess(token);
          }
          
          setSuccessMessage('Вы успешно зарегистрировались и вошли в систему!');
        } else {
          setSuccessMessage('Вы успешно зарегистрировались!');
        }
        
        // Очистка формы
        setName('');
        setPhone('');
        setEmail('');
        setPassword('');
        setPasswordConfirmation('');
        setConfirm(0);
        
        // Автоматически закрываем модальное окно через 2 секунды
        setTimeout(() => {
          const closeButton = document.getElementById('closeAuthModal');
          if (closeButton) {
            closeButton.click();
          }
        }, 2000);
        
      } else if (response.status === 422) {
        // Ошибка валидации от сервера
        if (result.error && result.error.errors) {
          // Собираем все сообщения об ошибках в одну строку
          const errorMessages = [];
          
          // Проходим по всем полям с ошибками
          Object.keys(result.error.errors).forEach(key => {
            const fieldErrors = result.error.errors[key];
            fieldErrors.forEach(errorMessage => {
              errorMessages.push(errorMessage);
            });
          });
          
          // Показываем все ошибки
          setError(errorMessages.join('. '));
        } else {
          setError('Ошибка валидации данных');
        }
      } else {
        // Другие ошибки сервера
        setError(result.error?.message || 'Произошла ошибка сервера. Попробуйте позже.');
      }
    } catch (error) {
      console.error('Network error:', error);
      setError('Ошибка подключения к серверу. Проверьте интернет-соединение.');
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="registration-form-container">
      <h2>Регистрация</h2>
      <form onSubmit={handleSubmit} noValidate>
        <div className="form-group">
          <label htmlFor="name">Имя:</label>
          <input
            id="name"
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            placeholder="Иван Иванов"
            required
            disabled={isSubmitting}
          />
          <small>Только кириллица, пробелы и дефисы</small>
        </div>
        
        <div className="form-group">
          <label htmlFor="phone">Телефон:</label>
          <input
            id="phone"
            type="tel"
            value={phone}
            onChange={(e) => setPhone(e.target.value)}
            placeholder="+79001234567"
            required
            disabled={isSubmitting}
          />
          <small>Только цифры и знак +</small>
        </div>
        
        <div className="form-group">
          <label htmlFor="email">Email:</label>
          <input
            id="email"
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="user@example.ru"
            required
            disabled={isSubmitting}
          />
        </div>
        
        <div className="form-group">
          <label htmlFor="password">Пароль:</label>
          <input
            id="password"
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="paSSword1"
            required
            disabled={isSubmitting}
          />
          <small>Не менее 7 символов: 1 цифра, 1 строчная и 1 заглавная буква</small>
        </div>
        
        <div className="form-group">
          <label htmlFor="password_confirmation">Подтверждение пароля:</label>
          <input
            id="password_confirmation"
            type="password"
            value={passwordConfirmation}
            onChange={(e) => setPasswordConfirmation(e.target.value)}
            placeholder="paSSword1"
            required
            disabled={isSubmitting}
          />
        </div>
        
        <div className="form-group checkbox-group">
          <label className="checkbox-label">
            <input
              type="checkbox"
              checked={confirm === 1}
              onChange={(e) => setConfirm(e.target.checked ? 1 : 0)}
              disabled={isSubmitting}
              required
            />
            <span>Я согласен на обработку персональных данных</span>
          </label>
        </div>
        
        <button 
          type="submit" 
          disabled={isSubmitting}
          className="submit-button"
        >
          {isSubmitting ? 'Регистрация...' : 'Зарегистрироваться'}
        </button>
        
        {(error || successMessage) && (
          <div className={`message ${successMessage ? 'success' : 'error'}`}>
            {successMessage || error}
          </div>
        )}
      </form>
    </div>
  );
};

export default RegistrationForm;
