import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from "../auth/AuthServise";
import SearchBar from '../common/SearchBar';

const Header = ({ onAnimalClick }) => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();
  const [searchQuery, setSearchQuery] = useState('');

  const handleSearchSubmit = (e) => {
    e.preventDefault();
    if (searchQuery.trim().length >= 3) {
      navigate(`/search?q=${encodeURIComponent(searchQuery)}`);
      setSearchQuery('');
    }
  };

  const handleLogout = () => {
    logout();
    alert('Вы вышли из аккаунта');
  };

  return (
    <nav className="navbar navbar-expand-lg navbar-dark bg-dark sticky-top">
      <div className="container">
        <Link to="/" className="navbar-brand">
          <i className="bi bi-heart-fill me-2"></i>WebPets
        </Link>
        
        <button 
          className="navbar-toggler" 
          type="button" 
          data-bs-toggle="collapse" 
          data-bs-target="#navbarNav"
        >
          <span className="navbar-toggler-icon"></span>
        </button>
        
        <div className="collapse navbar-collapse" id="navbarNav">
          <ul className="navbar-nav me-auto">
            <li className="nav-item">
              <Link to="/" className="nav-link">Главная</Link>
            </li>
            <li className="nav-item">
              <Link 
                to="/find-animals" 
                className="nav-link"
                onClick={(e) => {
                  if (window.location.pathname === '/find-animals') {
                    e.preventDefault();
                    window.scrollTo(0, 0);
                  }
                }}
              >
                Найти питомца
              </Link>
            </li>
            <li className="nav-item">
              <button 
                className="nav-link btn btn-link" 
                data-bs-toggle="modal" 
                data-bs-target="#newPost"
              >
                Сообщить о находке
              </button>
            </li>
          </ul>
          
          <div className="d-flex align-items-center">
            {/* Поиск */}
            <div className="me-3">
              <form className="d-flex" onSubmit={handleSearchSubmit}>
                <input 
                  className="form-control me-2" 
                  type="search" 
                  placeholder="Поиск по объявлениям..." 
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  minLength={3}
                />
                <button 
                  className="btn btn-outline-success" 
                  type="submit"
                  disabled={searchQuery.trim().length < 3}
                >
                  Поиск
                </button>
              </form>
            </div>
            
            {/* Контакты */}
            <button 
              className="btn btn-light me-2" 
              data-bs-toggle="modal" 
              data-bs-target="#contacts"
            >
              Контакты
            </button>
            
            {/* Авторизация */}
            <button 
              className="btn btn-light me-2" 
              data-bs-toggle="modal" 
              data-bs-target="#authModal"
              onClick={() => user && handleLogout()}
            >
              {user ? 'Выйти' : 'Войти/Регистрация'}
            </button>
            
            {/* Профиль */}
            <button 
              className="btn btn-light" 
              data-bs-toggle="modal" 
              data-bs-target="#profileModal"
            >
              {user ? user.name : 'Профиль'}
            </button>
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Header;
