import React, { useState, useEffect } from "react";
import Newsletter from './Newsletter';

function Content({ onNewsletterSubmit, showNewsletter, isLoading }) {
  const [animals, setAnimals] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Загружаем данные с API
  useEffect(() => {
    fetchAnimals();
  }, []);

  const fetchAnimals = async () => {
    try {
      setLoading(true);
      const response = await fetch('https://pets.xn--80ahdri7a.site/api/pets', {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json'
        }
      });
      
      if (!response.ok) {
        throw new Error('Ошибка при загрузке данных');
      }
      
      const data = await response.json();
      
      // Преобразуем данные API в формат нашего приложения
      const apiAnimals = data.data.orders
        .filter(animal => animal.registred && animal.photo) // Только зарегистрированные с фото
        .map(animal => {
          const imageUrl = animal.photo ? `https://pets.xn--80ahdri7a.site${animal.photo}` : null;
          
          return {
            id: animal.id,
            title: `${animal.kind}, найдено ${formatDate(animal.date)}`,
            description: animal.description,
            image: imageUrl,
            date: formatDateForSort(animal.date),
            type: getAnimalTypeFromKind(animal.kind),
            age: "неизвестно",
            phone: animal.phone,
            name: animal.name,
            mark: animal.mark,
            photo: animal.photo,
            hasValidImage: false
          };
        })
        .sort((a, b) => new Date(b.date) - new Date(a.date)); // Сортировка по убыванию даты
      
      // Проверяем загрузку изображений
      const animalsWithImageCheck = await Promise.all(
        apiAnimals.slice(0, 10).map(async (animal) => { // Проверяем первые 10 для производительности
          try {
            if (animal.image) {
              const imgCheck = await checkImageExists(animal.image);
              return {
                ...animal,
                hasValidImage: imgCheck
              };
            }
            return {
              ...animal,
              hasValidImage: false
            };
          } catch {
            return {
              ...animal,
              hasValidImage: false
            };
          }
        })
      );
      
      // Фильтруем только карточки с валидными изображениями и берем 3 последних
      const validAnimals = animalsWithImageCheck
        .filter(animal => animal.hasValidImage)
        .slice(0, 3); // Берем только 3 последних
      
      setAnimals(validAnimals);
    } catch (error) {
      console.error('Ошибка при загрузке животных:', error);
      setError('Не удалось загрузить данные о животных');
    } finally {
      setLoading(false);
    }
  };

  // Функция для проверки существования изображения
  const checkImageExists = (url) => {
    return new Promise((resolve) => {
      const img = new Image();
      img.onload = () => resolve(true);
      img.onerror = () => resolve(false);
      img.src = url;
    });
  };

  // Функция для получения типа животного (cat/dog/rabbit/other) из kind
  const getAnimalTypeFromKind = (kind) => {
    const kindLower = kind.toLowerCase();
    if (kindLower.includes('кошка') || kindLower.includes('кот')) return 'cat';
    if (kindLower.includes('собака')) return 'dog';
    if (kindLower.includes('кролик')) return 'rabbit';
    return 'other';
  };

  // Форматирование даты для отображения
  const formatDate = (dateString) => {
    const [day, month, year] = dateString.split('-');
    return `${day}.${month}.${year}`;
  };

  // Форматирование даты для сортировки
  const formatDateForSort = (dateString) => {
    const [day, month, year] = dateString.split('-');
    return `${year}-${month}-${day}`;
  };

  if (loading) {
    return (
      <div className="container mt-5 text-center">
        <div className="spinner-border text-dark" role="status">
          <span className="visually-hidden">Загрузка...</span>
        </div>
        <p className="mt-2">Загрузка последних объявлений...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container mt-5">
        <div className="alert alert-danger" role="alert">
          {error}
        </div>
      </div>
    );
  }

  return (
    <div>
      <div className="container mt-5">
        <div className="row mb-5">
          <div className="col-12 text-center">
            <h2>Последние найденные животные</h2>
            <p className="lead">
              Всего животных: {animals.length}
            </p>
          </div>
        </div>
        
        {animals.length === 0 ? (
          <div className="text-center">
            <h4>На данный момент нет животных с фотографиями</h4>
            <p>Попробуйте позже</p>
          </div>
        ) : (
          <div className="row mb-5">
            {animals.map(animal => (
              <div key={animal.id} className="col-md-4 mb-4">
                <div className="card animal-card h-100 d-flex flex-column">
                  <img 
                    src={animal.image} 
                    className="card-img-top" 
                    alt={animal.type}
                    style={{
                      width: '100%',
                      height: '300px',
                      objectFit: 'cover'
                    }}
                    onError={(e) => {
                      // Если изображение не загрузилось, скрываем карточку
                      e.target.parentElement.parentElement.style.display = 'none';
                    }}
                  />
                  <div className="card-body d-flex flex-column">
                    <h5 className="card-title">
                      {animal.title}
                    </h5>
                    
                    <p className="card-text flex-grow-1">{animal.description}</p>
                    
                    <div className="mb-2">
                      <span className="badge bg-secondary me-2">
                        <i className="bi bi-calendar me-1"></i>
                        {formatDate(animal.date)}
                      </span>
                      <span className={`badge ${
                        animal.type === 'cat' ? 'bg-info' :
                        animal.type === 'dog' ? 'bg-warning text-dark' :
                        animal.type === 'rabbit' ? 'bg-purple' : 'bg-secondary'
                      }`}>
                        {animal.type === 'cat' ? 'кошка/кот' : 
                         animal.type === 'dog' ? 'собака' : 
                         animal.type === 'rabbit' ? 'кролик' : 'другое'}
                      </span>
                      <span className="badge bg-dark ms-2">
                        <i className="bi bi-clock-history me-1"></i>
                        {animal.age}
                      </span>
                    </div>
                    
                    <div className="mt-auto">
                      <button 
                        className="btn btn-dark me-3" 
                        data-bs-toggle="modal" 
                        data-bs-target="#cart"
                        onClick={() => {
                          alert(`Контакты: ${animal.phone}\nНашедший: ${animal.name}\nМетка: ${animal.mark || 'нет'}`);
                        }}
                      >
                        Подробнее
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
        
        {/* Призыв к действию */}
        <div className="row mb-5">
          <div className="col-12">
            <div className="jumbotron jumbotron-fluid text-center rounded bg-dark text-white p-5">
              <div className="container">
                <h1 className="display-5">Помогите животным найти дом!</h1>
                <p className="lead">
                  Если вы нашли животное или готовы приютить одного из них, заполните форму находки или свяжитесь с нами.
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>
      
      {showNewsletter && (
        <Newsletter 
          onNewsletterSubmit={onNewsletterSubmit}
          isLoading={isLoading}
        />
      )}

      <style>{`
        .bg-purple {
          background-color: #6f42c1 !important;
        }
        .animal-card {
          transition: transform 0.3s, box-shadow 0.3s;
        }
        .animal-card:hover {
          transform: translateY(-5px);
          box-shadow: 0 10px 20px rgba(0,0,0,0.1);
        }
        .card-img-top {
          border-top-left-radius: calc(0.375rem - 1px);
          border-top-right-radius: calc(0.375rem - 1px);
        }
      `}</style>
    </div>
  );
}

export default Content;
