import React, { useState, useEffect } from "react";

function Profile({ userInfo }) {
  const [userData, setUserData] = useState({
    email: "",
    phone: "",
    name: "",
    registrationDate: "",
    ordersCount: 0,
    petsCount: 0
  });
  
  const [isEditing, setIsEditing] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [isFetching, setIsFetching] = useState(true);
  const [error, setError] = useState("");
  const [successMessage, setSuccessMessage] = useState("");

  // Получаем email из localStorage
  const getUserEmail = () => {
    // Сначала пробуем из userInfo
    if (userInfo?.email) {
      return userInfo.email;
    }
    
    // Затем из localStorage
    const savedEmail = localStorage.getItem('userEmail');
    if (savedEmail) {
      return savedEmail;
    }
    
    // Или из userData в localStorage
    const userDataStr = localStorage.getItem('userData');
    if (userDataStr) {
      try {
        const data = JSON.parse(userDataStr);
        return data.email;
      } catch (e) {
        console.error("Ошибка парсинга userData:", e);
      }
    }
    
    return null;
  };

  // Получаем токен из localStorage
  const getAuthToken = () => {
    return localStorage.getItem('authToken');
  };

  // Проверяем, авторизован ли пользователь
  const isUserLoggedIn = () => {
    const token = getAuthToken();
    const email = getUserEmail();
    return !!(token && email);
  };

  // Функция для получения данных пользователя
  const fetchUserData = async () => {
    // Проверяем авторизацию
    if (!isUserLoggedIn()) {
      setError("Требуется авторизация");
      setIsFetching(false);
      return;
    }

    const token = getAuthToken();
    const userEmail = getUserEmail();
    
    if (!token || !userEmail) {
      setError("Требуется авторизация");
      setIsFetching(false);
      return;
    }

    try {
      setIsFetching(true);
      setError("");
      
      console.log("Запрос данных для email:", userEmail);
      console.log("Токен:", token ? "присутствует" : "отсутствует");
      
      // Вариант 1: Пытаемся получить данные текущего пользователя
      const apiUrl = 'https://pets.xn--80ahdri7a.site/api/users/';
      
      const response = await fetch(apiUrl, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        }
      });

      console.log("Статус ответа:", response.status);
      
      if (response.status === 401) {
        setError("Требуется авторизация. Пожалуйста, войдите снова.");
        return;
      }

      if (!response.ok) {
        throw new Error(`Ошибка сервера: ${response.status}`);
      }

      const result = await response.json();
      console.log("Ответ сервера:", result);
      
      // Ищем пользователя по email в ответе
      let foundUser = null;
      
      // Обрабатываем разные форматы ответа
      if (Array.isArray(result.data)) {
        foundUser = result.data.find(user => user.email === userEmail);
      } else if (result.data && Array.isArray(result.data.user)) {
        foundUser = result.data.user.find(user => user.email === userEmail);
      } else if (result.data && result.data.user && result.data.user.email === userEmail) {
        foundUser = result.data.user;
      } else if (result.user && result.user.email === userEmail) {
        foundUser = result.user;
      }
      
      if (foundUser) {
        setUserData({
          email: foundUser.email || userEmail,
          phone: foundUser.phone || "",
          name: foundUser.name || "",
          registrationDate: foundUser.registrationDate || foundUser.created_at || "",
          ordersCount: foundUser.ordersCount || foundUser.orders_count || 0,
          petsCount: foundUser.petsCount || foundUser.pets_count || 0
        });
        
        // Обновляем данные в localStorage
        const userDataToSave = {
          email: foundUser.email || userEmail,
          name: foundUser.name || "",
          phone: foundUser.phone || ""
        };
        localStorage.setItem('userData', JSON.stringify(userDataToSave));
        
      } else {
        // Пользователь не найден, но у нас есть email
        setUserData({
          email: userEmail,
          phone: "",
          name: "",
          registrationDate: "",
          ordersCount: 0,
          petsCount: 0
        });
        setError("Данные пользователя не найдены на сервере");
      }
      
    } catch (err) {
      console.error("Ошибка при загрузке данных:", err);
      setError("Не удалось загрузить данные. Проверьте подключение к интернету.");
      
      // Показываем хотя бы email из localStorage
      const email = getUserEmail();
      if (email) {
        setUserData({
          email: email,
          phone: "",
          name: "",
          registrationDate: "",
          ordersCount: 0,
          petsCount: 0
        });
      }
    } finally {
      setIsFetching(false);
    }
  };

  // Загружаем данные при монтировании
  useEffect(() => {
    fetchUserData();
  }, []);

  const handleSave = async () => {
    setIsLoading(true);
    setError("");
    setSuccessMessage("");

    const token = getAuthToken();
    const userEmail = getUserEmail();
    
    if (!token || !userEmail) {
      setError("Требуется авторизация");
      setIsLoading(false);
      return;
    }

    try {
      const updateData = {
        name: userData.name,
        phone: userData.phone,
        email: userData.email
      };
      
      const apiUrl = 'https://pets.xn--80ahdri7a.site/api/users/';
      
      const response = await fetch(apiUrl, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify(updateData)
      });

      if (!response.ok) {
        if (response.status === 401) {
          setError("Требуется авторизация");
        } else if (response.status === 422) {
          setError("Ошибка валидации данных");
        } else {
          throw new Error(`Ошибка сервера: ${response.status}`);
        }
        setIsLoading(false);
        return;
      }

      const result = await response.json();
      console.log("Результат обновления:", result);
      
      setSuccessMessage("Изменения успешно сохранены!");
      
      // Обновляем email в localStorage если он изменился
      if (userData.email !== userEmail) {
        localStorage.setItem('userEmail', userData.email);
      }
      
      // Обновляем полные данные
      const userDataToSave = {
        email: userData.email,
        name: userData.name,
        phone: userData.phone
      };
      localStorage.setItem('userData', JSON.stringify(userDataToSave));
      
      setTimeout(() => {
        setIsEditing(false);
        setSuccessMessage("");
      }, 2000);
      
    } catch (err) {
      console.error("Ошибка при сохранении:", err);
      setError("Не удалось сохранить изменения");
    } finally {
      setIsLoading(false);
    }
  };

  // Функция для выхода
  const handleLogout = () => {
    // Удаляем только токен, но сохраняем email
    localStorage.removeItem('authToken');
    localStorage.removeItem('userData');
    
    // Можно очистить или оставить userEmail для удобства
    // localStorage.removeItem('userEmail');
    
    alert("Вы успешно вышли из аккаунта");
    
    document.querySelector('#myPage .btn-close')?.click();
    
    setTimeout(() => {
      window.location.reload();
    }, 100);
  };

  // Проверяем авторизацию
  const isLoggedIn = isUserLoggedIn();
  const email = getUserEmail();
  const name = userData.name || email?.split('@')[0] || "Пользователь";

  if (!isLoggedIn) {
    return (
      <div className="modal fade" id="myPage" tabIndex={-1} aria-hidden="true">
        <div className="modal-dialog modal-lg">
          <div className="modal-content">
            <div className="modal-header">
              <h1 className="modal-title fs-5">Личный кабинет</h1>
              <button type="button" className="btn-close" data-bs-dismiss="modal" />
            </div>
            <div className="modal-body">
              <div className="text-center p-4">
                <div className="alert alert-warning" role="alert">
                  <h5 className="alert-heading">Требуется авторизация!</h5>
                  <p>Для доступа к личному кабинету необходимо войти в систему.</p>
                  <hr />
                  <button 
                    className="btn btn-dark"
                    data-bs-dismiss="modal"
                    data-bs-toggle="modal"
                    data-bs-target="#authModal"
                  >
                    Перейти к авторизации
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="modal fade" id="myPage" tabIndex={-1} aria-hidden="true">
      <div className="modal-dialog modal-lg">
        <div className="modal-content">
          <div className="modal-header">
            <h1 className="modal-title fs-5">
              Личный кабинет - {name}
            </h1>
            <button type="button" className="btn-close" data-bs-dismiss="modal" />
          </div>
          <div className="modal-body">
            {successMessage && (
              <div className="alert alert-success alert-dismissible fade show" role="alert">
                {successMessage}
                <button type="button" className="btn-close" onClick={() => setSuccessMessage("")} />
              </div>
            )}

            {error && !successMessage && (
              <div className="alert alert-danger alert-dismissible fade show" role="alert">
                {error}
                <button type="button" className="btn-close" onClick={() => setError("")} />
              </div>
            )}

            {isFetching ? (
              <div className="text-center p-4">
                <div className="spinner-border text-dark" role="status">
                  <span className="visually-hidden">Загрузка...</span>
                </div>
                <p className="mt-2">Загрузка данных...</p>
              </div>
            ) : (
              <div className="row">
                <div className="col-md-6">
                  <h5>Данные пользователя</h5>
                  
                  <div className="mb-3">
                    <label className="form-label small">Имя:</label>
                    <input 
                      type="text" 
                      className="form-control" 
                      placeholder="Имя" 
                      value={userData.name}
                      onChange={(e) => setUserData({...userData, name: e.target.value})}
                      disabled={!isEditing || isLoading}
                    />
                  </div>
                  
                  <div className="mb-3">
                    <label className="form-label small">Email:</label>
                    <input 
                      type="email" 
                      className="form-control" 
                      placeholder="email" 
                      value={userData.email}
                      onChange={(e) => setUserData({...userData, email: e.target.value})}
                      disabled={!isEditing || isLoading}
                    />
                  </div>
                  
                  <div className="mb-3">
                    <label className="form-label small">Телефон:</label>
                    <input 
                      type="text" 
                      className="form-control" 
                      placeholder="phone" 
                      value={userData.phone}
                      onChange={(e) => setUserData({...userData, phone: e.target.value})}
                      disabled={!isEditing || isLoading}
                    />
                  </div>
                  
                  <div className="mt-4">
                    <h6>Статистика</h6>
                    <div className="row text-center">
                      <div className="col-4">
                        <div className="card bg-light">
                          <div className="card-body">
                            <h5 className="card-title">{userData.ordersCount}</h5>
                            <p className="card-text small">Заказы</p>
                          </div>
                        </div>
                      </div>
                      <div className="col-4">
                        <div className="card bg-light">
                          <div className="card-body">
                            <h5 className="card-title">{userData.petsCount}</h5>
                            <p className="card-text small">Питомцы</p>
                          </div>
                        </div>
                      </div>
                      <div className="col-4">
                        <div className="card bg-light">
                          <div className="card-body">
                            <h5 className="card-title">{userData.registrationDate ? "Есть" : "Нет"}</h5>
                            <p className="card-text small">Дата регистрации</p>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                  
                  <div className="mt-3">
                    <p className="text-muted small">
                      <strong>Email:</strong> {userData.email}<br />
                      <strong>Дата регистрации:</strong> {userData.registrationDate || "Не указана"}<br />
                      <strong>Статус:</strong> Активен
                    </p>
                  </div>
                </div>
                
                <div className="col-md-6">
                  <h5>Управление аккаунтом</h5>
                  
                  <div className="alert alert-info">
                    <small>
                      <strong>Информация:</strong><br />
                      • Email сохраняется при регистрации/входе<br />
                      • При обновлении страницы вы остаетесь в системе<br />
                      • Для выхода нажмите кнопку ниже
                    </small>
                  </div>
                  
                  <div className="text-center p-4">
                    <p className="text-muted">Объявления пока не доступны</p>
                    <button 
                      className="btn btn-sm btn-outline-dark" 
                      data-bs-dismiss="modal"
                      data-bs-toggle="modal" 
                      data-bs-target="#newPost"
                      onClick={() => document.querySelector('#myPage .btn-close')?.click()}
                    >
                      Создать объявление
                    </button>
                  </div>
                  
                  <div className="mt-4 pt-3 border-top">
                    <button 
                      className="btn btn-outline-danger w-100"
                      onClick={handleLogout}
                      disabled={isLoading}
                    >
                      Выйти из аккаунта
                    </button>
                  </div>
                </div>
              </div>
            )}
          </div>
          <div className="modal-footer">
            <button 
              type="button" 
              className="btn btn-danger" 
              data-bs-dismiss="modal"
              disabled={isLoading}
            >
              Закрыть
            </button>
            
            <button 
              type="button" 
              className="btn btn-warning"
              onClick={() => {
                if (isEditing) {
                  fetchUserData();
                  setError("");
                  setSuccessMessage("");
                }
                setIsEditing(!isEditing);
              }}
              disabled={isLoading || isFetching}
            >
              {isEditing ? "Отменить" : "Редактировать"}
            </button>
            
            <button 
              type="button" 
              className="btn btn-dark"
              onClick={handleSave}
              disabled={!isEditing || isLoading || isFetching}
            >
              {isLoading ? (
                <>
                  <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span>
                  Сохранение...
                </>
              ) : "Сохранить"}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Profile;
