import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";

function Header({ siteTitle, userInfo, onLogout }) {
  const [isSearchOpen, setIsSearchOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");
  const [searchSuggestions, setSearchSuggestions] = useState([]);
  const [loadingSuggestions, setLoadingSuggestions] = useState(false);
  const [searchTimer, setSearchTimer] = useState(null);

  const handleSearch = (e) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      // Перенаправляем на страницу поиска или выполняем поиск
      window.location.href = `/find_animals?search=${encodeURIComponent(searchQuery)}`;
    }
  };

  // Debounce для подсказок
  useEffect(() => {
    if (searchQuery.length > 2) {
      if (searchTimer) {
        clearTimeout(searchTimer);
      }
      
      const timer = setTimeout(async () => {
        try {
          setLoadingSuggestions(true);
          const response = await fetch(
            `https://pets.xn--80ahdri7a.site/api/search?query=${encodeURIComponent(searchQuery)}`
          );
          
          if (response.ok) {
            const data = await response.json();
            if (data.data && data.data.orders) {
              const suggestions = data.data.orders.slice(0, 5).map(order => ({
                id: order.id,
                text: order.description.substring(0, 50) + '...',
                kind: order.kind
              }));
              setSearchSuggestions(suggestions);
            }
          }
        } catch (err) {
          console.error('Ошибка поиска подсказок:', err);
        } finally {
          setLoadingSuggestions(false);
        }
      }, 1000);
      
      setSearchTimer(timer);
    } else {
      setSearchSuggestions([]);
    }
    
    return () => {
      if (searchTimer) {
        clearTimeout(searchTimer);
      }
    };
  }, [searchQuery]);

  const handleLogoutClick = () => {
    if (userInfo?.isLoggedIn && onLogout) {
      onLogout();
      alert("Вы вышли из аккаунта");
    }
  };

  const handleSuggestionClick = (suggestion) => {
    setSearchQuery(suggestion.text);
    setSearchSuggestions([]);
    // Можно сразу перенаправить на страницу с этим животным
    window.location.href = `/find_animals?highlight=${suggestion.id}`;
  };

  return (
    <nav className="navbar navbar-expand-lg navbar-dark bg-dark sticky-top">
      <div className="container">
        <Link to={'/'} className="navbar-brand">
          <i className="bi bi-heart-fill me-2" />{siteTitle || "WebPets"}
        </Link>
        <button 
          className="navbar-toggler" 
          type="button" 
          data-bs-toggle="collapse" 
          data-bs-target="#navbarNav"
        >
          <span className="navbar-toggler-icon" />
        </button>
        <div className="collapse navbar-collapse" id="navbarNav">
          <ul className="navbar-nav me-auto">
            <li className="nav-item">
              <Link to={'/'} className="nav-link active">Главная</Link>
            </li>
            <li className="nav-item">
              <Link to={'/'} className="nav-link active" data-bs-toggle="modal" data-bs-target="#newPost">
                Сообщить о находке
              </Link>
            </li>
            <li className="nav-item">
              <Link to={'/find_animals'} className="nav-link active">Найти питомца</Link>
            </li>
          </ul>
          <div className="d-flex">
            <button className="btn btn-light me-2" data-bs-toggle="modal" data-bs-target="#contacts">
              Контакты
            </button>
            <button 
              className="btn btn-light me-2" 
              data-bs-toggle="modal" 
              data-bs-target="#authModal"
              onClick={() => {
                if (userInfo?.isLoggedIn) {
                  handleLogoutClick();
                }
              }}
            >
              {userInfo?.isLoggedIn ? "Выйти" : "Войти/Регистрация"}
            </button>
            <button className="btn btn-light me-3" data-bs-toggle="modal" data-bs-target="#myPage">
              {userInfo?.isLoggedIn ? userInfo.userName : "Профиль"}
            </button>
          </div>

          <form className="d-flex me-3 position-relative" role="search" onSubmit={handleSearch}>
            <input 
              className="form-control me-2" 
              type="search" 
              placeholder="Поиск по описанию" 
              aria-label="Поиск"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              onFocus={() => setIsSearchOpen(true)}
              onBlur={() => setTimeout(() => setIsSearchOpen(false), 200)}
            />
            <button className="btn btn-outline-success" type="submit">
              Поиск
            </button>
            
            {/* Подсказки поиска */}
            {(searchSuggestions.length > 0 || loadingSuggestions) && isSearchOpen && (
              <div className="position-absolute top-100 start-0 end-0 bg-white border rounded shadow-lg mt-1" style={{zIndex: 1050}}>
                {loadingSuggestions ? (
                  <div className="p-3 text-center">
                    <div className="spinner-border spinner-border-sm text-dark" role="status">
                      <span className="visually-hidden">Загрузка...</span>
                    </div>
                    <span className="ms-2">Поиск подсказок...</span>
                  </div>
                ) : (
                  <>
                    {searchSuggestions.map(suggestion => (
                      <div 
                        key={suggestion.id}
                        className="p-2 border-bottom hover-bg-light"
                        style={{cursor: 'pointer'}}
                        onMouseDown={() => handleSuggestionClick(suggestion)}
                      >
                        <div className="d-flex justify-content-between">
                          <span>{suggestion.text}</span>
                          <span className="badge bg-secondary">{suggestion.kind}</span>
                        </div>
                      </div>
                    ))}
                    <div className="p-2 text-center">
                      <small className="text-muted">
                        Найдено {searchSuggestions.length} совпадений
                      </small>
                    </div>
                  </>
                )}
              </div>
            )}
          </form>
        </div>
      </div>
    </nav>
  );
}

export default Header;
