// profile.jsx
import React, { useState } from "react";

function Profile({ userInfo }) {
  const [userData, setUserData] = useState({
    email: userInfo?.userData?.email || "",
    phone: userInfo?.userData?.phone || "",
    name: userInfo?.userData?.name || ""
  });

  const [posts, setPosts] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [isLoading, setIsLoading] = useState(false);

  // Демо-данные для постов
  const demoPosts = [
    { id: 1, title: "Нашел котенка", date: "2025-10-12", location: "Москва" },
    { id: 2, title: "Ищу хозяев для собаки", date: "2025-10-10", location: "Санкт-Петербург" }
  ];

  const handleSave = () => {
    setIsLoading(true);

    // Имитация задержки сохранения
    setTimeout(() => {
      alert("Изменения сохранены!");
      setIsEditing(false);
      setIsLoading(false);

      // В реальном приложении здесь был бы вызов API
      console.log("Сохраненные данные:", userData);
    }, 500);
  };

  const handleLogout = () => {
    if (userInfo?.logout) {
      userInfo.logout();
    }
    // Закрываем модальное окно профиля
    document.querySelector('#myPage .btn-close')?.click();
    alert("Вы вышли из системы");
  };

  return (
    <div className="modal fade" id="myPage" tabIndex={-1} aria-hidden="true">
      <div className="modal-dialog modal-lg">
        <div className="modal-content">
          <div className="modal-header">
            <h1 className="modal-title fs-5">
              {userInfo?.isLoggedIn ? `Личный кабинет - ${userInfo.userName}` : "Личный кабинет"}
            </h1>
            <button type="button" className="btn-close" data-bs-dismiss="modal" />
          </div>
          <div className="modal-body">
            {!userInfo?.isLoggedIn ? (
              <div className="text-center p-4">
                <p>Для доступа к личному кабинету необходимо войти в систему.</p>
                <button 
                  className="btn btn-dark" 
                  data-bs-toggle="modal" 
                  data-bs-target="#exampleModal"
                  onClick={() => document.querySelector('#myPage .btn-close')?.click()}
                >
                  Войти
                </button>
              </div>
            ) : (
              <div className="row">
                <div className="col-md-6">
                  <h5>Данные пользователя</h5>
                  <div className="mb-3">
                    <label className="form-label small">Имя:</label>
                    <input 
                      type="text" 
                      className="form-control" 
                      placeholder="Имя" 
                      value={userData.name}
                      onChange={(e) => setUserData({...userData, name: e.target.value})}
                      disabled={!isEditing || isLoading}
                    />
                  </div>
                  <div className="mb-3">
                    <label className="form-label small">Email:</label>
                    <input 
                      type="email" 
                      className="form-control" 
                      placeholder="email" 
                      value={userData.email}
                      onChange={(e) => setUserData({...userData, email: e.target.value})}
                      disabled={!isEditing || isLoading}
                    />
                  </div>
                  <div className="mb-3">
                    <label className="form-label small">Телефон:</label>
                    <input 
                      type="text" 
                      className="form-control" 
                      placeholder="phone" 
                      value={userData.phone}
                      onChange={(e) => setUserData({...userData, phone: e.target.value})}
                      disabled={!isEditing || isLoading}
                    />
                  </div>

                  <div className="mt-3">
                    <p className="text-muted small">
                      <strong>ID пользователя:</strong> {userInfo.userData?.id || "demo123"}<br />
                      <strong>Статус:</strong> Активен
                    </p>
                  </div>
                </div>

                <div className="col-md-6">
                  <h5>Мои объявления</h5>
                  {demoPosts.length > 0 ? (
                    <div className="list-group">
                      {demoPosts.map(post => (
                        <div key={post.id} className="list-group-item">
                          <div className="d-flex justify-content-between align-items-center">
                            <div>
                              <strong>{post.title}</strong>
                              <br />
                              <small className="text-muted">{post.location}</small>
                            </div>
                            <span className="badge bg-secondary">{post.date}</span>
                          </div>
                          <button 
                            className="btn btn-sm btn-outline-info mt-2"
                            data-bs-toggle="modal"
                            data-bs-target="#cart"
                            onClick={() => document.querySelector('#myPage .btn-close')?.click()}
                          >
                            Посмотреть
                          </button>
                        </div>
                      ))}
                    </div>
                  ) : (
                    <div className="text-center p-4">
                      <p className="text-muted">У вас еще нет объявлений</p>
                      <button 
                        className="btn btn-sm btn-outline-dark" 
                        data-bs-toggle="modal" 
                        data-bs-target="#newPost"
                        onClick={() => document.querySelector('#myPage .btn-close')?.click()}
                      >
                        Создать объявление
                      </button>
                    </div>
                  )}
                </div>
              </div>
            )}
          </div>
          <div className="modal-footer">
            <button 
              type="button" 
              className="btn btn-danger" 
              data-bs-dismiss="modal"
              disabled={isLoading}
            >
              Закрыть
            </button>
            {userInfo?.isLoggedIn && (
              <>
                <button 
                  type="button" 
                  className="btn btn-warning"
                  onClick={() => setIsEditing(!isEditing)}
                  disabled={isLoading}
                >
                  {isEditing ? "Отменить" : "Редактировать"}
                </button>
                <button 
                  type="button" 
                  className="btn btn-dark"
                  onClick={handleSave}
                  disabled={!isEditing || isLoading}
                >
                  {isLoading ? "Сохранение..." : "Сохранить"}
                </button>
              </>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Profile;
