import React, { useEffect, useState } from "react";
import image1 from './image/image1.jfif';
import image2 from './image/image2.jfif';
import image3 from './image/image3.jfif';
import './css/style.css';

function Slider({ animals, loading }) {
  const [activeIndex, setActiveIndex] = useState(0);
  
  useEffect(() => {
    if (animals.length > 0) {
      const interval = setInterval(() => {
        setActiveIndex((prevIndex) => (prevIndex + 1) % animals.length);
      }, 5000);
      return () => clearInterval(interval);
    }
  }, [animals.length]);

  if (loading) {
    return (
      <div className="text-center p-5 bg-light">
        <div className="spinner-border text-dark" role="status">
          <span className="visually-hidden">Загрузка слайдера...</span>
        </div>
        <p className="mt-2">Загрузка последних находок...</p>
      </div>
    );
  }

  if (!animals || animals.length === 0) {
    return null; // Слайдер не отображается если нет животных
  }

  return(
    <div>
      <div id="animalCarousel" className="carousel slide" data-bs-ride="carousel">
        <div className="carousel-indicators">
          {animals.map((_, index) => (
            <button 
              key={index}
              type="button" 
              data-bs-target="#animalCarousel" 
              data-bs-slide-to={index} 
              className={index === activeIndex ? "active" : ""}
              aria-current={index === activeIndex ? "true" : "false"}
              aria-label={`Слайд ${index + 1}`}
              onClick={() => setActiveIndex(index)}
            />
          ))}
        </div>
        
        <div className="carousel-inner">
          {animals.map((animal, index) => (
            <div 
              key={animal.id} 
              className={`carousel-item ${index === activeIndex ? 'active' : ''}`}
            >
              <img 
                src={animal.image || image1} 
                className="d-block w-100" 
                alt={animal.kind || "Найденное животное"}
                style={{ height: "500px", objectFit: "cover" }}
                onError={(e) => {
                  // Запасные изображения если основное не загрузилось
                  const images = [image1, image2, image3];
                  e.target.src = images[index % images.length];
                }}
              />
              <div className="carousel-caption d-none d-md-block bg-dark bg-opacity-50 rounded p-3">
                <h5>{animal.kind || "Найдено животное"}</h5>
                <p>{animal.description || "Ищет хозяев"}</p>
                {animal.date && (
                  <small>Дата находки: {animal.date}</small>
                )}
              </div>
            </div>
          ))}
        </div>
        
        <button 
          className="carousel-control-prev" 
          type="button" 
          data-bs-target="#animalCarousel" 
          data-bs-slide="prev"
          onClick={() => setActiveIndex((prev) => (prev - 1 + animals.length) % animals.length)}
        >
          <span className="carousel-control-prev-icon" aria-hidden="true" />
          <span className="visually-hidden">Предыдущий</span>
        </button>
        
        <button 
          className="carousel-control-next" 
          type="button" 
          data-bs-target="#animalCarousel" 
          data-bs-slide="next"
          onClick={() => setActiveIndex((prev) => (prev + 1) % animals.length)}
        >
          <span className="carousel-control-next-icon" aria-hidden="true" />
          <span className="visually-hidden">Следующий</span>
        </button>
      </div>
    </div>
  );
}

export default Slider;
