import React, { useState, useEffect } from 'react';
import Slider from '../layout/Slider';
import AnimalList from '../animals/AnimalList';
import NewsletterForm from '../forms/NewsletterForm';
import FoundAnimalForm from '../forms/FoundAnimalForm';
import ContactForm from '../forms/ContactForm';
import ProfileModal from '../profile/ProfileModal';
import AuthModal from '../auth/AuthModal';

const HomePage = () => {
  const [isInitialLoad, setIsInitialLoad] = useState(true);

  useEffect(() => {
    setIsInitialLoad(true);
    const timer = setTimeout(() => {
      setIsInitialLoad(false);
    }, 100);
    
    return () => clearTimeout(timer);
  }, []);

  return (
    <>
      <Slider />
      
      <div className="container mt-5">
        <div className="text-center mb-5">
          <h1>Найди своего питомца</h1>
          <p className="lead text-muted">
            Помогите животным найти новый дом или найдите друга для себя
          </p>
        </div>
        
        <h2 className="mb-4">Последние найденные животные</h2>
        
        {isInitialLoad ? (
          <div className="text-center py-5">
            <div className="spinner-border text-primary" role="status">
              <span className="visually-hidden">Загрузка...</span>
            </div>
            <p className="mt-2">Загрузка животных...</p>
          </div>
        ) : (
          <AnimalList limit={3} />
        )}
        
        <div className="row mt-5 mb-5">
          <div className="col-12">
            <div className="jumbotron jumbotron-fluid text-center rounded bg-dark text-white p-5">
              <div className="container">
                <h2 className="display-5 mb-3">Помогите животным найти дом!</h2>
                <p className="lead mb-4">
                  Если вы нашли животное или готовы приютить одного из них, 
                  заполните форму находки или свяжитесь с нами.
                </p>
                <div className="d-flex justify-content-center gap-3">
                  <button 
                    className="btn btn-light btn-lg"
                    data-bs-toggle="modal"
                    data-bs-target="#newPost"
                  >
                    <i className="bi bi-plus-circle me-2"></i>
                    Сообщить о находке
                  </button>
                  <button 
                    className="btn btn-outline-light btn-lg"
                    data-bs-toggle="modal"
                    data-bs-target="#contacts"
                  >
                    <i className="bi bi-telephone me-2"></i>
                    Связаться с нами
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
      
      <NewsletterForm />
      
      {/* Модальные окна */}
      <FoundAnimalForm />
      <ContactForm />
      <ProfileModal />
      <AuthModal />
    </>
  );
};

export default HomePage;
