import React, { useState, useEffect } from "react";
import image2 from './image/image2.jfif';
import image3 from './image/image3.jfif';
import image from './image/image.jfif';
import Newsletter from './Newsletter';

function Content({ onNewsletterSubmit, showNewsletter, isLoading, foundAnimals }) {
  const [animals, setAnimals] = useState([
    {
      id: 1,
      title: "Котенок, найден 12 октября",
      description: "Маленький белый котенок, найден в районе ул. Пушкина. Очень игривый и ласковый.",
      image: image,
      date: "12 октября 2025",
      type: "котенок",
      animalType: "cat"
    },
    {
      id: 2,
      title: "Собака породы мопс, найдена 8 октября",
      description: "Дружелюбная собака, найдена в пригороде. Знает основные команды, приучена к поводку.",
      image: image2,
      date: "8 октября 2025",
      type: "собака",
      animalType: "dog"
    },
    {
      id: 3,
      title: "Кролик, найден 5 октября",
      description: "Красивый Кролик, найден в центре города. Очень пушистый.",
      image: image3,
      date: "5 октября 2025",
      type: "кролик",
      animalType: "rabbit"
    }
  ]);

  // Добавляем новые карточки из формы находки
  useEffect(() => {
    if (foundAnimals && foundAnimals.length > 0) {
      // Добавляем новые карточки в начало списка
      const newAnimals = foundAnimals.map((animal, index) => ({
        id: animals.length + index + 1,
        title: animal.title || `Найдено: ${getAnimalTypeName(animal.animalType)}`,
        description: animal.description,
        image: animal.imageUrl || getDefaultImage(animal.animalType),
        date: animal.date || new Date().toLocaleDateString('ru-RU'),
        type: getAnimalTypeName(animal.animalType),
        animalType: animal.animalType,
        location: animal.location,
        age: animal.age,
        isNew: true // Флаг для выделения новых карточек
      }));

      setAnimals(prev => [...newAnimals, ...prev]);
    }
  }, [foundAnimals]);

  // Функция для получения имени типа животного
  const getAnimalTypeName = (type) => {
    switch(type) {
      case 'cat': return 'кошка/кот';
      case 'dog': return 'собака';
      case 'rabbit': return 'кролик';
      default: return 'животное';
    }
  };

  // Функция для получения изображения по умолчанию
  const getDefaultImage = (type) => {
    switch(type) {
      case 'cat': return image;
      case 'dog': return image2;
      case 'rabbit': return image3;
      default: return image;
    }
  };

  return (
    <div>
      <div className="container mt-5">
        {/* Уведомление о новых карточках */}
        {foundAnimals && foundAnimals.length > 0 && (
          <div className="alert alert-success alert-dismissible fade show" role="alert">
            <i className="bi bi-check-circle-fill me-2"></i>
            Добавлено {foundAnimals.length} новых {foundAnimals.length === 1 ? 'животное' : 'животных'} из формы находки
            <button type="button" className="btn-close" data-bs-dismiss="alert" aria-label="Закрыть"></button>
          </div>
        )}

        <div className="row mb-5">
          <div className="col-12 text-center">
            <h2>Помогите найти хозяев для этих животных</h2>
            <p className="lead">
              Всего животных: {animals.length}
              {foundAnimals && foundAnimals.length > 0 && (
                <span className="ms-2 badge bg-success">
                  Новых: {foundAnimals.length}
                </span>
              )}
            </p>
          </div>
        </div>

        <div className="row mb-5">
          {animals.map(animal => (
            <div key={animal.id} className="col-md-4 mb-4">
              <div className={`card animal-card h-100 d-flex flex-column ${animal.isNew ? 'border-success border-2' : ''}`}>
                {animal.isNew && (
                  <div className="position-absolute top-0 end-0 m-2">
                    <span className="badge bg-success">Новое</span>
                  </div>
                )}

                <img 
                  src={animal.image} 
                  className="card-img-top" 
                  alt={animal.type}
                  style={{
                    width: '100%',
                    height: '300px',
                    objectFit: 'cover'
                  }}
                  onError={(e) => {
                    e.target.src = getDefaultImage(animal.animalType);
                  }}
                />
                <div className="card-body d-flex flex-column">
                  <h5 className="card-title">
                    {animal.title}
                    {animal.location && (
                      <small className="d-block text-muted">
                        <i className="bi bi-geo-alt me-1"></i>
                        {animal.location}
                      </small>
                    )}
                  </h5>

                  <p className="card-text flex-grow-1">{animal.description}</p>

                  <div className="mb-2">
                    <span className="badge bg-secondary me-2">
                      <i className="bi bi-calendar me-1"></i>
                      {animal.date}
                    </span>
                    <span className={`badge ${
                      animal.animalType === 'cat' ? 'bg-info' :
                      animal.animalType === 'dog' ? 'bg-warning text-dark' :
                      animal.animalType === 'rabbit' ? 'bg-purple' : 'bg-secondary'
                    }`}>
                      {animal.type}
                    </span>
                    {animal.age && (
                      <span className="badge bg-dark ms-2">
                        <i className="bi bi-clock-history me-1"></i>
                        {animal.age}
                      </span>
                    )}
                  </div>

                  <div className="mt-auto">
                    <button 
                      className="btn btn-dark me-3" 
                      data-bs-toggle="modal" 
                      data-bs-target="#cart"
                      onClick={() => {
                        // Здесь можно передать данные животного в модальное окно
                        // если нужно показывать разные карточки
                        if (animal.id > 3) {
                          // Для новых карточек можно создать отдельное модальное окно
                          // или обновить существующее
                          alert(`Открыта карточка: ${animal.title}\n\nДля полной интеграции можно создать динамическое модальное окно.`);
                        }
                      }}
                    >
                      Подробнее
                    </button>
                    {animal.isNew && (
                      <button className="btn btn-outline-success btn-sm">
                        <i className="bi bi-star-fill me-1"></i>
                        Забрать
                      </button>
                    )}
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* Призыв к действию */}
        <div className="row mb-5">
          <div className="col-12">
            <div className="jumbotron jumbotron-fluid text-center rounded bg-dark text-white p-5">
              <div className="container">
                <h1 className="display-5">Помогите животным найти дом!</h1>
                <p className="lead">
                  Если вы нашли животное или готовы приютить одного из них, заполните форму находки или свяжитесь с нами.
                </p>
                <div className="mt-4">
                  <button 
                    className="btn btn-light btn-lg me-3" 
                    data-bs-toggle="modal" 
                    data-bs-target="#newPost"
                  >
                    <i className="bi bi-plus-circle me-2"></i>
                    Сообщить о находке
                  </button>
                  <button 
                    className="btn btn-outline-light btn-lg"
                    data-bs-toggle="modal" 
                    data-bs-target="#contacts"
                  >
                    <i className="bi bi-telephone me-2"></i>
                    Связаться с нами
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Информационный блок */}
        <div className="row mb-5">
          <div className="col-md-6 mb-4">
            <div className="card h-100">
              <div className="card-body">
                <h5 className="card-title">
                  <i className="bi bi-heart-pulse text-danger me-2"></i>
                  Как мы помогаем животным?
                </h5>
                <ul className="list-group list-group-flush">
                  <li className="list-group-item">
                    <i className="bi bi-check-circle-fill text-success me-2"></i>
                    Размещаем информацию о найденных животных
                  </li>
                  <li className="list-group-item">
                    <i className="bi bi-check-circle-fill text-success me-2"></i>
                    Автоматически создаем карточки из формы находки
                  </li>
                  <li className="list-group-item">
                    <i className="bi bi-check-circle-fill text-success me-2"></i>
                    Помогаем найти временный приют
                  </li>
                  <li className="list-group-item">
                    <i className="bi bi-check-circle-fill text-success me-2"></i>
                    Ищем постоянных хозяев
                  </li>
                  <li className="list-group-item">
                    <i className="bi bi-check-circle-fill text-success me-2"></i>
                    Консультируем по уходу за животными
                  </li>
                </ul>
              </div>
            </div>
          </div>
          <div className="col-md-6 mb-4">
            <div className="card h-100">
              <div className="card-body">
                <h5 className="card-title">
