import React, { useState } from "react";

function Newsletter({ onNewsletterSubmit, isLoading }) {
  const [email, setEmail] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    if (onNewsletterSubmit) {
      onNewsletterSubmit(e);
    }
  };

  return (
    <>
      <h2 className="text-center text-white bg-dark m-2">Подписка на новости</h2>
      <form className="w-50 m-auto p-3" style={{minWidth: 300}} onSubmit={handleSubmit}>
        <div className="mb-3">
          <label htmlFor="exampleInputEmail1" className="form-label">Введите адрес электронной почты</label>
          <input 
            type="email" 
            className="form-control" 
            id="exampleInputEmail1" 
            aria-describedby="emailHelp"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <div id="emailHelp" className="form-text">Мы никогда не делимся Вашими e-mail ни с кем.</div>
        </div>
        <button type="submit" className="btn btn-dark" disabled={isLoading}>
          {isLoading ? "Подписка..." : "Подписаться"}
        </button>
      </form>
    </>
  );
}

export default Newsletter;
