import React, { useState, useEffect, useMemo } from 'react';
import AnimalCard from './AnimalCard';

const AnimalList = ({ limit = null, filters = {}, itemsPerPage = 6, showPaginationInfo = true }) => {
  const [animals, setAnimals] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [isMounted, setIsMounted] = useState(true);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalItems, setTotalItems] = useState(0);

  useEffect(() => {
    setIsMounted(true);
    setCurrentPage(1); // Сбрасываем на первую страницу при загрузке
    return () => {
      setIsMounted(false);
      setAnimals([]);
      setLoading(false);
    };
  }, [filters]);

  const fetchAnimals = async () => {
    if (!isMounted) return;
    
    setLoading(true);
    setError(null);

    try {
      const response = await fetch('https://pets.xn--80ahdri7a.site/api/pets', {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json'
        }
      });
      
      if (!response.ok) {
        throw new Error(`Ошибка загрузки данных: ${response.status}`);
      }
      
      const data = await response.json();
      
      if (!isMounted) return;
      
      if (data.data?.orders) {
        let filteredAnimals = data.data.orders.filter(animal => animal.registred);
        
        // Применяем фильтры
        if (filters.animalType && filters.animalType !== 'all') {
          filteredAnimals = filteredAnimals.filter(animal => {
            const kind = animal.kind?.toLowerCase() || '';
            switch (filters.animalType) {
              case 'cat':
                return kind.includes('кот') || kind.includes('кошка');
              case 'dog':
                return kind.includes('собака');
              case 'rabbit':
                return kind.includes('кролик');
              case 'other':
                return !kind.includes('кот') && !kind.includes('кошка') && 
                       !kind.includes('собака') && !kind.includes('кролик');
              default:
                return true;
            }
          });
        }

        // Фильтр по дате
        if (filters.dateRange && filters.dateRange !== 'all') {
          const now = new Date();
          filteredAnimals = filteredAnimals.filter(animal => {
            if (!animal.date) return true;
            
            try {
              const [day, month, year] = animal.date.split('-');
              const animalDate = new Date(year, month - 1, day);
              const diffTime = Math.abs(now - animalDate);
              const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
              
              switch (filters.dateRange) {
                case 'week': return diffDays <= 7;
                case 'month': return diffDays <= 30;
                case 'older': return diffDays > 30;
                default: return true;
              }
            } catch (e) {
              return true;
            }
          });
        }

        // Сортируем по дате (новые первыми)
        filteredAnimals.sort((a, b) => {
          try {
            const dateA = a.date ? a.date.split('-').reverse().join('-') : '';
            const dateB = b.date ? b.date.split('-').reverse().join('-') : '';
            return dateB.localeCompare(dateA);
          } catch (e) {
            return 0;
          }
        });

        // Ограничиваем количество, если указано (только для главной страницы)
        if (limit) {
          filteredAnimals = filteredAnimals.slice(0, limit);
        }

        setTotalItems(filteredAnimals.length);
        setAnimals(filteredAnimals);
      }
    } catch (error) {
      if (!isMounted) return;
      console.error('Error fetching animals:', error);
      setError('Не удалось загрузить данные о животных');
    } finally {
      if (isMounted) {
        setLoading(false);
      }
    }
  };

  // Загружаем данные при монтировании
  useEffect(() => {
    if (isMounted) {
      fetchAnimals();
    }
  }, [isMounted]);

  // Рассчитываем пагинацию
  const { paginatedAnimals, totalPages, startIndex, endIndex } = useMemo(() => {
    const total = animals.length;
    const totalPages = Math.ceil(total / itemsPerPage);
    const startIndex = (currentPage - 1) * itemsPerPage;
    const endIndex = Math.min(startIndex + itemsPerPage, total);
    const paginatedAnimals = animals.slice(startIndex, endIndex);
    
    return {
      paginatedAnimals,
      totalPages,
      startIndex: startIndex + 1,
      endIndex
    };
  }, [animals, currentPage, itemsPerPage]);

  // Обновляем фильтрацию при изменении фильтров
  useEffect(() => {
    if (isMounted && animals.length > 0) {
      fetchAnimals();
    }
  }, [filters, isMounted]);

  // Сброс страницы при изменении фильтров
  useEffect(() => {
    setCurrentPage(1);
  }, [filters]);

  // Функции для управления пагинацией
  const handleNextPage = () => {
    if (currentPage < Math.ceil(animals.length / itemsPerPage)) {
      setCurrentPage(prev => prev + 1);
      window.scrollTo({ top: 0, behavior: 'smooth' });
    }
  };

  const handlePrevPage = () => {
    if (currentPage > 1) {
      setCurrentPage(prev => prev - 1);
      window.scrollTo({ top: 0, behavior: 'smooth' });
    }
  };

  const handlePageClick = (pageNumber) => {
    setCurrentPage(pageNumber);
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  if (loading && animals.length === 0) {
    return (
      <div className="text-center py-5">
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Загрузка...</span>
        </div>
        <p className="mt-2">Загрузка животных...</p>
      </div>
    );
  }

  if (error && animals.length === 0) {
    return (
      <div className="alert alert-danger text-center">
        <p>{error}</p>
        <button 
          className="btn btn-primary mt-2"
          onClick={() => fetchAnimals()}
        >
          Попробовать снова
        </button>
      </div>
    );
  }

  if (animals.length === 0) {
    return (
      <div className="text-center py-5">
        <div className="alert alert-info">
          <i className="bi bi-info-circle me-2"></i>
          Животные не найдены
          <div className="mt-2">
            <button 
              className="btn btn-sm btn-outline-primary"
              onClick={() => fetchAnimals()}
            >
              Обновить
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="animal-list">
      {showPaginationInfo && (
        <div className="mb-3 d-flex justify-content-between align-items-center">
          <div>
            <span className="badge bg-primary">
              Всего найдено: {animals.length}
            </span>
            {animals.length > itemsPerPage && (
              <span className="badge bg-info ms-2">
                Страница {currentPage} из {totalPages} ({startIndex}-{endIndex} из {animals.length})
              </span>
            )}
          </div>
          <button 
            className="btn btn-sm btn-outline-secondary"
            onClick={() => fetchAnimals()}
            disabled={loading}
          >
            {loading ? (
              <>
                <span className="spinner-border spinner-border-sm me-1"></span>
                Обновление...
              </>
            ) : (
              <>
                <i className="bi bi-arrow-clockwise me-1"></i>
                Обновить
              </>
            )}
          </button>
        </div>
      )}
      
      <div className="row row-cols-1 row-cols-md-2 row-cols-lg-3 g-4 mb-4">
        {paginatedAnimals.map((animal) => (
          <div key={animal.id} className="col">
            <AnimalCard 
              animal={animal}
              onViewClick={() => {
                window.dispatchEvent(new CustomEvent('openAnimalModal', { 
                  detail: { animalId: animal.id } 
                }));
              }}
            />
          </div>
        ))}
      </div>
      
      {/* Пагинация */}
      {animals.length > itemsPerPage && (
        <nav aria-label="Навигация по страницам">
          <ul className="pagination justify-content-center">
            <li className={`page-item ${currentPage === 1 ? 'disabled' : ''}`}>
              <button 
                className="page-link" 
                onClick={handlePrevPage}
                disabled={currentPage === 1}
              >
                <i className="bi bi-chevron-left"></i> Назад
              </button>
            </li>
            
            {/* Первая страница */}
            {currentPage > 2 && (
              <li className="page-item">
                <button 
                  className="page-link" 
                  onClick={() => handlePageClick(1)}
                >
                  1
                </button>
              </li>
            )}
            
            {/* Многоточие если нужно */}
            {currentPage > 3 && (
              <li className="page-item disabled">
                <span className="page-link">...</span>
              </li>
            )}
            
            {/* Страницы вокруг текущей */}
            {Array.from({ length: Math.min(5, totalPages) }, (_, i) => {
              let pageNum;
              if (currentPage <= 3) {
                pageNum = i + 1;
              } else if (currentPage >= totalPages - 2) {
                pageNum = totalPages - 4 + i;
              } else {
                pageNum = currentPage - 2 + i;
              }
              
              if (pageNum > 0 && pageNum <= totalPages) {
                return (
                  <li 
                    key={pageNum} 
                    className={`page-item ${currentPage === pageNum ? 'active' : ''}`}
                  >
                    <button 
                      className="page-link" 
                      onClick={() => handlePageClick(pageNum)}
                    >
                      {pageNum}
                    </button>
                  </li>
                );
              }
              return null;
            })}
            
            {/* Многоточие если нужно */}
            {currentPage < totalPages - 2 && (
              <li className="page-item disabled">
                <span className="page-link">...</span>
              </li>
            )}
            
            {/* Последняя страница если не показана */}
            {currentPage < totalPages - 1 && totalPages > 1 && (
              <li className="page-item">
                <button 
                  className="page-link" 
                  onClick={() => handlePageClick(totalPages)}
                >
                  {totalPages}
                </button>
              </li>
            )}
            
            <li className={`page-item ${currentPage === totalPages ? 'disabled' : ''}`}>
              <button 
                className="page-link" 
                onClick={handleNextPage}
                disabled={currentPage === totalPages}
              >
                Вперед <i className="bi bi-chevron-right"></i>
              </button>
            </li>
          </ul>
          
          <div className="text-center mt-2">
            <small className="text-muted">
              Показано {paginatedAnimals.length} из {animals.length} объявлений
            </small>
          </div>
        </nav>
      )}
    </div>
  );
};

export default AnimalList;
