import React, { useState } from 'react';
import { Routes, Route } from 'react-router-dom';
import Home from './components/Home';
import Find_Animals from './components/Find_Animals';
import Header from './components/header';
import Footer from './components/footer';
import AuthModal from './components/AuthModal';
function App() {
  const siteInfo = {
    title: "WebPets",
    year: 2025,
    email: "helpAnimal@mail.ru",
    phone: "+7 (931) 562-231-32"
  };

  const [userInfo, setUserInfo] = useState({
    isLoggedIn: false,
    userName: "Гость",
    email: "",
    phone: ""
  });

  // Функция для входа
  const handleLogin = (userData) => {
    setUserInfo({
      isLoggedIn: true,
      userName: userData.name || "Пользователь",
      email: userData.email,
      phone: userData.phone
    });
  };

  // Функция для выхода
  const handleLogout = () => {
    setUserInfo({
      isLoggedIn: false,
      userName: "Гость",
      email: "",
      phone: ""
    });
  };

  return (
    <div>
      <Header 
        siteTitle={siteInfo.title} 
        userInfo={userInfo}
        onLogout={handleLogout}
      />
      <Routes>
        <Route 
          path={'/'} 
          element={
            <Home 
              siteInfo={siteInfo}
              userInfo={userInfo}
              onLogin={handleLogin}
              onLogout={handleLogout}
              />
          }
        />
        <Route 
          path={'/find_animals'} 
          element={
            <Find_Animals 
              siteInfo={siteInfo}
              userInfo={userInfo}
              onLogin={handleLogin}
              onLogout={handleLogout}
            />
          }
        />
      </Routes>
      <Footer 
        siteInfo={siteInfo}
      />
    </div>
  );
}

export default App;
