import React, { useState } from "react";

function Newsletter({ onNewsletterSubmit, isLoading }) {
  const [email, setEmail] = useState("");
  const [error, setError] = useState(null);

  async function handleSubmit(e) {
    e.preventDefault();
    const url = 'https://pets.сделай.site/api/subscription';
    try {
      const response = await fetch(url, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'},
        body: [
          {email: 'email'}
        ]
      });
      if (!response.ok) {
        throw new Error(`Response status: ${response.status}`);
      }
  
      const result = await response.json();

      if(result.error) {
        setError(result.error);
      }

      console.log(result);
    } catch (error) {
      console.error(error.message);
    }
    // if (onNewsletterSubmit) {
    //   onNewsletterSubmit(e);
    // }
  };

  return (
    <>
      <h2 className="text-center text-white bg-dark m-2">Подписка на новости</h2>
      <form className="w-50 m-auto p-3" style={{minWidth: 300}} onSubmit={handleSubmit}>
        <div className="mb-3">
          <label htmlFor="exampleInputEmail1" className="form-label">Введите адрес электронной почты</label>
          <input 
            type="email" 
            className="form-control" 
            id="exampleInputEmail1" 
            aria-describedby="emailHelp"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <div id="emailHelp" className="form-text">Мы никогда не делимся Вашими e-mail ни с кем.</div>
        </div>

    {
      error && (
        <div className="card">
  <div className="card-body">
    {error}
  </div>
</div>

      )
    }

        <button type="submit" className="btn btn-dark" disabled={isLoading}>
          {isLoading ? "Подписка..." : "Подписаться"}
        </button>

      </form>
    </>
  );
}

export default Newsletter;
