import React, { useState } from "react";
import Icon2 from './image/icon/mail.ico';
import Icon3 from './image/icon/z.ico';
import RegistrationForm from "./register";

function AuthModal({ userInfo, onLogin, onRegister }) {
  const [isLoginMode, setIsLoginMode] = useState(true);
  const [loginData, setLoginData] = useState({
    email: "",
    password: ""
  });
  const [errors, setErrors] = useState({});
  const [apiError, setApiError] = useState("");
  const [successMessage, setSuccessMessage] = useState("");

  // Валидация формы входа
  const validateLogin = () => {
    const newErrors = {};
    if (!loginData.email || !loginData.email.includes('@')) {
      newErrors.loginEmail = "Некорректный email";
    }
    if (!loginData.password || loginData.password.length < 6) {
      newErrors.loginPassword = "Пароль должен быть не менее 6 символов";
    }
    return newErrors;
  };

  // Обработка входа через API
  const handleLogin = async () => {
    // Сбрасываем предыдущие сообщения
    setApiError("");
    setSuccessMessage("");

    // Валидация на клиенте
    const validationErrors = validateLogin();
    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors);
      return;
    }

    try {
      // Отправляем запрос на API аутентификации
      const response = await fetch('https://pets.xn--80ahdri7a.site/api/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          email: loginData.email,
          password: loginData.password
        })
      });

      const data = await response.json();

      if (response.status === 200) {
        // Успешная аутентификация
        const token = data.data.token;

        // Очищаем токен перед сохранением
        const cleanToken = token.trim();

        // Сохраняем токен
        localStorage.setItem('authToken', cleanToken);

        // Сохраняем email для использования в профиле
        localStorage.setItem('userEmail', loginData.email);

        // Вызываем колбэк onLogin с токеном
        onLogin(cleanToken);

        // Показываем сообщение об успехе
        setSuccessMessage("Успешный вход в систему");

        // Закрываем модальное окно через 1 секунду
        setTimeout(() => {
          document.getElementById('closeAuthModal')?.click();
        }, 1000);

      } else if (response.status === 422) {
        // Ошибка валидации
        setApiError("Ошибка валидации данных");
        if (data.error && data.error.errors) {
          const apiErrors = {};
          Object.keys(data.error.errors).forEach(key => {
            if (key === 'email') apiErrors.loginEmail = data.error.errors[key].join(', ');
            if (key === 'password') apiErrors.loginPassword = data.error.errors[key].join(', ');
          });
          setErrors(apiErrors);
        }

      } else if (response.status === 401) {
        // Неавторизован
        setApiError("Неверный email или пароль");
        setErrors({
          loginEmail: "Неверный email или пароль",
          loginPassword: "Неверный email или пароль"
        });

      } else {
        // Другие ошибки
        setApiError(data.error?.message || "Произошла ошибка при входе");
      }

    } catch (error) {
      console.error('Ошибка при входе:', error);
      setApiError("Ошибка соединения с сервером");
    }
  };

  const handleChangeLogin = (field, value) => {
    setLoginData(prev => ({ ...prev, [field]: value }));

    // Сбрасываем ошибки при изменении поля
    if (errors[`login${field}`]) {
      setErrors(prev => ({ ...prev, [`login${field}`]: null }));
    }

    // Сбрасываем общую ошибку API
    if (apiError) {
      setApiError("");
    }
  };

  // Обработчик отправки формы
  const handleSubmit = (e) => {
    e.preventDefault();
    if (isLoginMode) {
      handleLogin();
    }
  };

  return (
    <div className="modal fade" id="authModal" tabIndex={-1} aria-labelledby="authModalLabel" aria-hidden="true">
      <div className="modal-dialog">
        <div className="modal-content">
          <div className="modal-header">
            <h1 className="modal-title fs-5" id="authModalLabel">
              {isLoginMode ? "Вход в аккаунт" : "Регистрация"}
            </h1>
            <button 
              id="closeAuthModal"
              type="button" 
              className="btn-close" 
              data-bs-dismiss="modal" 
              aria-label="Закрыть"
              onClick={() => {
                setErrors({});
                setApiError("");
                setSuccessMessage("");
                setLoginData({ email: "", password: "" });
              }}
            />
          </div>

          <div className="modal-body">
            {/* Переключение между входом и регистрацией */}
            <div className="mb-4">
              <div className="btn-group w-100" role="group">
                <button
                  type="button"
                  className={`btn ${isLoginMode ? 'btn-dark' : 'btn-outline-dark'}`}
                  onClick={() => {
                    setIsLoginMode(true);
                    setErrors({});
                    setApiError("");
                    setSuccessMessage("");
                  }}
                >
                  Вход
                </button>
                <button
                  type="button"
                  className={`btn ${!isLoginMode ? 'btn-dark' : 'btn-outline-dark'}`}
                  onClick={() => {
                    setIsLoginMode(false);
                    setErrors({});
                    setApiError("");
                    setSuccessMessage("");
                  }}
                >
                  Регистрация
                </button>
              </div>
            </div>

            {/* Форма входа */}
            {isLoginMode ? (
              <form onSubmit={handleSubmit}>
                {/* Сообщение об успехе */}
                {successMessage && (
                  <div className="alert alert-success" role="alert">
                    {successMessage}
                  </div>
                )}

                {/* Сообщение об ошибке API */}
                {apiError && !successMessage && (
                  <div className="alert alert-danger" role="alert">
                    {apiError}
                  </div>
                )}

                <div className="input-group flex-nowrap mb-3">
                  <span className="input-group-text">
                    <img src={Icon2} alt="Email" width="20" height="20" />
                  </span>
                  <input 
                    type="email" 
                    className={`form-control ${errors.loginEmail ? 'is-invalid' : ''}`}
                    placeholder="Email" 
                    value={loginData.email}
                    onChange={(e) => handleChangeLogin('email', e.target.value)}
                    required
                  />
                  {errors.loginEmail && <div className="invalid-feedback">{errors.loginEmail}</div>}
                </div>

                <div className="input-group flex-nowrap mb-3">
                  <span className="input-group-text">
                    <img src={Icon3} alt="Пароль" width="20" height="20" />
                  </span>
                  <input 
                    type="password" 
                    className={`form-control ${errors.loginPassword ? 'is-invalid' : ''}`}
                    placeholder="Пароль" 
                    value={loginData.password}
                    onChange={(e) => handleChangeLogin('password', e.target.value)}
                    required
                  />
                  {errors.loginPassword && <div className="invalid-feedback">{errors.loginPassword}</div>}
                </div>

                <div className="form-text mb-3">
                  Тестовый аккаунт: user@user.rl / paSSword1
                </div>
              </form>
            ) : (
              <RegistrationForm/>
            )}
          </div>

          <div className="modal-footer">
            <button 
              type="button" 
              className="btn btn-danger" 
              data-bs-dismiss="modal"
              onClick={() => {
                setErrors({});
                setApiError("");
                setSuccessMessage("");
                setLoginData({ email: "", password: "" });
              }}
            >
              Закрыть
            </button>
            <button 
              type="button" 
              className="btn btn-dark"
              onClick={isLoginMode ? handleLogin : () => {
                document.getElementById('closeAuthModal')?.click();
              }}
            >
              {isLoginMode ? "Войти" : "Зарегистрироваться"}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default AuthModal;
