import React, { useState } from 'react';
import Find_animal from './find_animals_page';
import AuthModal from './AuthModal';
import Profile from './profile';
import Nahodka from './nahodka';
import Svyz from './svyz';
import Modal_Cat from './cat_card';

function Find_Animals({ siteInfo, userInfo, onLogin, onLogout }) {
  const [filters, setFilters] = useState({
    animalType: 'all',
    dateRange: 'all'
  });

  const handleFilterChange = (filterType, value) => {
    setFilters(prev => ({
      ...prev,
      [filterType]: value
    }));
  };

  return (
    <div className="App">
      <AuthModal 
        userInfo={userInfo} 
        onLogin={onLogin}
        onRegister={(userData) => {
          onLogin(userData);
        }}
      />
      <Profile userInfo={userInfo} />
      <Modal_Cat/>
      <Svyz siteInfo={siteInfo} />
      <Nahodka />
      
      <main style={{ minHeight: '70vh' }}>
        <div className="container mt-3">
          <div className="row mb-3">
            <div className="col-md-6">
              <select 
                className="form-select"
                value={filters.animalType}
                onChange={(e) => handleFilterChange('animalType', e.target.value)}
              >
                <option value="all">Все животные</option>
                <option value="cat">Кошки</option>
                <option value="dog">Собаки</option>
                <option value="rabbit">Кролики</option>
                <option value="other">Другие</option>
              </select>
            </div>
            <div className="col-md-6">
              <select 
                className="form-select"
                value={filters.dateRange}
                onChange={(e) => handleFilterChange('dateRange', e.target.value)}
              >
                <option value="all">Все даты</option>
                <option value="week">За неделю</option>
                <option value="month">За месяц</option>
                <option value="older">Старые</option>
              </select>
            </div>
          </div>
        </div>
        <Find_animal filters={filters} />
      </main>
    </div>
  );
}

export default Find_Animals;
