import React, { useState, useEffect, useRef } from "react";
import './css/style.css';

function Slider() {
  const [pets, setPets] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const carouselRef = useRef(null);

  // Функция для загрузки данных о животных
  const fetchPetsData = async () => {
    try {
      setLoading(true);
      setError(null);

      const url = 'https://pets.xn--80ahdri7a.site/api/pets/slider';
      const response = await fetch(url, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json'
        }
      });

      if (!response.ok) {
        throw new Error(`Ошибка загрузки: ${response.status}`);
      }

      const result = await response.json();

      // Проверяем наличие данных
      if (result.data && Array.isArray(result.data.pets)) {
        setPets(result.data.pets);
      } else {
        setPets([]);
      }

    } catch (error) {
      console.error('Ошибка при загрузке слайдера:', error);
      setError('Не удалось загрузить объявления. Попробуйте позже.');
      setPets([]);
    } finally {
      setLoading(false);
    }
  };

  // Загружаем данные при монтировании компонента
  useEffect(() => {
    fetchPetsData();
  }, []);

  // Инициализируем Bootstrap карусель после загрузки данных
  useEffect(() => {
    if (pets.length > 0 && typeof window !== 'undefined' && window.bootstrap) {
      // Даем время DOM обновиться
      setTimeout(() => {
        const carouselElement = carouselRef.current;
        if (carouselElement) {
          const carousel = new window.bootstrap.Carousel(carouselElement, {
            interval: 5000, // Автопереключение каждые 5 секунд
            wrap: true, // Зацикливание
            touch: true // Поддержка касаний на мобильных
          });
        }
      }, 100);
    }
  }, [pets]);

  // Если загрузка
  if (loading) {
    return (
      <div className="text-center py-5">
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Загрузка...</span>
        </div>
        <p className="mt-2">Загрузка объявлений...</p>
      </div>
    );
  }

  // Если ошибка
  if (error) {
    return (
      <div className="alert alert-warning text-center" role="alert">
        {error}
      </div>
    );
  }

  // Если нет данных
  if (!pets || pets.length === 0) {
    return null;
  }

  return (
    <div className="slider-container">
      <div 
        ref={carouselRef}
        id="animalCarousel" 
        className="carousel slide" 
        data-bs-ride="carousel"
        data-bs-interval="5000"
      >
        {/* Индикаторы слайдов */}
        <div className="carousel-indicators">
          {pets.map((_, index) => (
            <button 
              key={index}
              type="button" 
              data-bs-target="#animalCarousel" 
              data-bs-slide-to={index}
              className={index === 0 ? "active" : ""}
              aria-current={index === 0 ? "true" : "false"}
              aria-label={`Слайд ${index + 1}`}
            />
          ))}
        </div>

        {/* Слайды */}
        <div className="carousel-inner">
          {pets.map((pet, index) => (
            <div 
              key={pet.id}
              className={`carousel-item ${index === 0 ? "active" : ""}`}
            >
              <div className="carousel-image-container">
                {pet.image ? (
                  <img 
                    src={`https://pets.xn--80ahdri7a.site${pet.image}`} 
                    className="d-block w-100 carousel-image"
                    alt={`${pet.kind || 'Животное'}`}
                    style={{ 
                      height: '500px',
                      objectFit: 'cover',
                      objectPosition: 'center'
                    }}
                  />
                ) : (
                  <div 
                    className="d-block w-100 bg-secondary d-flex align-items-center justify-content-center"
                    style={{ height: '500px' }}
                  >
                    <div className="text-white text-center">
                      <i className="bi bi-image fs-1 d-block"></i>
                      <span>Изображение отсутствует</span>
                    </div>
                  </div>
                )}
              </div>

              <div className="carousel-caption d-none d-md-block">
                <div className="bg-dark bg-opacity-75 rounded p-3">
                  <h5>{pet.kind || "Животное"}</h5>
                  <p className="mb-0">{pet.description || "Описание отсутствует"}</p>
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* Кнопки управления */}
        <button 
          className="carousel-control-prev" 
          type="button" 
          data-bs-target="#animalCarousel" 
          data-bs-slide="prev"
        >
          <span className="carousel-control-prev-icon" aria-hidden="true" />
          <span className="visually-hidden">Предыдущий</span>
        </button>
        <button 
          className="carousel-control-next" 
          type="button" 
          data-bs-target="#animalCarousel" 
          data-bs-slide="next"
        >
          <span className="carousel-control-next-icon" aria-hidden="true" />
          <span className="visually-hidden">Следующий</span>
        </button>
      </div>
    </div>
  );
}

export default Slider;
