import React, { useState, useEffect } from 'react';
import { Routes, Route } from 'react-router-dom';
import Home from './components/Home';
import Find_Animals from './components/Find_Animals';
import Header from './components/header';
import Footer from './components/footer';
import AuthModal from './components/AuthModal';
import Profile from './components/profile';

function App() {
  const siteInfo = {
    title: "WebPets",
    year: 2025,
    email: "helpAnimal@mail.ru",
    phone: "+7 (931) 562-231-32"
  };

  const [userInfo, setUserInfo] = useState({
    isLoggedIn: false,
    userName: "Гость",
    email: "",
    phone: ""
  });

  // Проверяем токен при загрузке приложения
  useEffect(() => {
    const token = localStorage.getItem('authToken');
    if (token) {
      // Декодируем токен для получения информации о пользователе
      try {
        const parts = token.split('.');
        if (parts.length === 3) {
          const base64Url = parts[1];
          const base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
          const jsonPayload = decodeURIComponent(
            atob(base64)
              .split('')
              .map(c => '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2))
              .join('')
          );
          const payload = JSON.parse(jsonPayload);
          
          // Получаем сохраненные данные пользователя
          const savedUserData = JSON.parse(localStorage.getItem('userData') || '{}');
          
          setUserInfo({
            isLoggedIn: true,
            userName: savedUserData.name || payload.name || payload.email || "Пользователь",
            email: savedUserData.email || payload.email || "",
            phone: savedUserData.phone || payload.phone || ""
          });
        }
      } catch (error) {
        console.error("Ошибка при проверке токена:", error);
        // Если токен невалиден, удаляем его
        localStorage.removeItem('authToken');
        localStorage.removeItem('userData');
      }
    }
  }, []);

  // Функция для входа
  const handleLogin = (token) => {
    // Токен уже сохранен в localStorage, нужно получить данные пользователя
    if (token) {
      try {
        const parts = token.split('.');
        if (parts.length === 3) {
          const base64Url = parts[1];
          const base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
          const jsonPayload = decodeURIComponent(
            atob(base64)
              .split('')
              .map(c => '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2))
              .join('')
          );
          const payload = JSON.parse(jsonPayload);
          
          // Получаем сохраненные данные пользователя
          const savedUserData = JSON.parse(localStorage.getItem('userData') || '{}');
          
          setUserInfo({
            isLoggedIn: true,
            userName: savedUserData.name || payload.name || payload.email || "Пользователь",
            email: savedUserData.email || payload.email || "",
            phone: savedUserData.phone || payload.phone || ""
          });
        }
      } catch (error) {
        console.error("Ошибка при входе:", error);
      }
    }
  };

  // Функция для выхода
  const handleLogout = () => {
    localStorage.removeItem('authToken');
    localStorage.removeItem('userData');
    setUserInfo({
      isLoggedIn: false,
      userName: "Гость",
      email: "",
      phone: ""
    });
  };

  // Функция для обновления данных пользователя (после редактирования профиля)
  const updateUserInfo = (updatedData) => {
    setUserInfo(prev => ({
      ...prev,
      ...updatedData
    }));
  };

  return (
    <div>
      <Header 
        siteTitle={siteInfo.title} 
        userInfo={userInfo}
        onLogout={handleLogout}
      />
      <Routes>
        <Route 
          path={'/'} 
          element={
            <Home 
              siteInfo={siteInfo}
              userInfo={userInfo}
              onLogin={handleLogin}
              onLogout={handleLogout}
              />
          }
        />
        <Route 
          path={'/find_animals'} 
          element={
            <Find_Animals 
              siteInfo={siteInfo}
              userInfo={userInfo}
              onLogin={handleLogin}
              onLogout={handleLogout}
            />
          }
        />
      </Routes>
      <Footer 
        siteInfo={siteInfo}
      />
      
      {/* Модальные окна */}
      <AuthModal 
        userInfo={userInfo}
        onLogin={handleLogin}
      />
      
      <Profile 
        userInfo={userInfo}
        onUserUpdate={updateUserInfo}
      />
    </div>
  );
}

export default App;
