import React, { useState, useEffect } from "react";

function Profile({ userInfo }) {
  const [userData, setUserData] = useState({
    email: "",
    phone: "",
    name: "",
    registrationDate: "",
    ordersCount: 0,
    petsCount: 0
  });
  
  const [userPosts, setUserPosts] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [isFetching, setIsFetching] = useState(true);
  const [isFetchingPosts, setIsFetchingPosts] = useState(true);
  const [error, setError] = useState("");
  const [successMessage, setSuccessMessage] = useState("");
  const [daysSinceRegistration, setDaysSinceRegistration] = useState(0);

  // Функция для вычисления дней с момента регистрации
  const calculateDaysSinceRegistration = (registrationDate) => {
    if (!registrationDate) return 0;
    
    try {
      // Парсим дату в формате "DD-MM-YYYY"
      const [day, month, year] = registrationDate.split('-').map(Number);
      const regDate = new Date(year, month - 1, day);
      const currentDate = new Date();
      
      const diffTime = Math.abs(currentDate - regDate);
      return Math.floor(diffTime / (1000 * 60 * 60 * 24));
    } catch (err) {
      console.error("Ошибка парсинга даты:", err);
      return 0;
    }
  };

  // Функция для получения email пользователя
  const getUserEmail = () => {
    // 1. Из userInfo
    if (userInfo?.userData?.email) {
      return userInfo.userData.email;
    }
    
    // 2. Из localStorage (сохраняем при логине)
    const savedEmail = localStorage.getItem('userEmail');
    if (savedEmail) {
      return savedEmail;
    }
    
    return null;
  };

  // Функция для получения данных пользователя по email
  const fetchUserData = async () => {
    if (!userInfo?.isLoggedIn) {
      setIsFetching(false);
      return;
    }

    const token = localStorage.getItem('authToken');
    const userEmail = getUserEmail();
    
    if (!token || !userEmail) {
      setError("Требуется авторизация");
      setIsFetching(false);
      return;
    }

    try {
      setIsFetching(true);
      setError("");
      
      // Запрашиваем данные пользователя по email
      const apiUrl = `https://pets.xn--80ahdri7a.site/api/users/?email=${encodeURIComponent(userEmail)}`;
      console.log("Fetching user data from:", apiUrl);
      
      const response = await fetch(apiUrl, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.status === 401) {
        setError("Требуется авторизация");
        document.querySelector('#myPage .btn-close')?.click();
        setTimeout(() => {
          document.getElementById('authModalButton')?.click();
        }, 300);
        return;
      }

      if (!response.ok) {
        throw new Error(`Ошибка сервера: ${response.status}`);
      }

      const result = await response.json();
      console.log("User data response:", result);
      
      // Обрабатываем ответ
      let user = null;
      
      if (result.data && result.data.user) {
        user = Array.isArray(result.data.user) ? result.data.user[0] : result.data.user;
      } else if (result.user) {
        user = Array.isArray(result.user) ? result.user[0] : result.user;
      } else if (result.data) {
        user = Array.isArray(result.data) ? result.data[0] : result.data;
      }
      
      if (user) {
        setUserData({
          email: user.email || userEmail,
          phone: user.phone || "",
          name: user.name || "",
          registrationDate: user.registrationDate || user.created_at || "",
          ordersCount: user.ordersCount || user.orders_count || 0,
          petsCount: user.petsCount || user.pets_count || 0
        });
        
        // Сохраняем email в localStorage для будущего использования
        localStorage.setItem('userEmail', user.email || userEmail);
        
        // Сохраняем полные данные пользователя
        localStorage.setItem('userData', JSON.stringify({
          name: user.name || "",
          email: user.email || userEmail,
          phone: user.phone || ""
        }));
        
        // Вычисляем количество дней с момента регистрации
        if (user.registrationDate || user.created_at) {
          const days = calculateDaysSinceRegistration(user.registrationDate || user.created_at);
          setDaysSinceRegistration(days);
        }
      } else {
        // Если не нашли пользователя, создаем базовые данные
        setUserData({
          email: userEmail,
          phone: "",
          name: "",
          registrationDate: "",
          ordersCount: 0,
          petsCount: 0
        });
      }
    } catch (err) {
      console.error("Ошибка при загрузке данных пользователя:", err);
      setError(`Не удалось загрузить данные: ${err.message}`);
    } finally {
      setIsFetching(false);
    }
  };

  // Функция для получения объявлений пользователя
  const fetchUserPosts = async () => {
    if (!userInfo?.isLoggedIn) {
      setIsFetchingPosts(false);
      return;
    }

    const token = localStorage.getItem('authToken');
    const userEmail = getUserEmail();
    
    if (!token || !userEmail) {
      setIsFetchingPosts(false);
      return;
    }

    try {
      setIsFetchingPosts(true);
      
      // Временно пустой массив
      setUserPosts([]);
      
    } catch (err) {
      console.error("Ошибка при загрузке объявлений:", err);
      setUserPosts([]);
    } finally {
      setIsFetchingPosts(false);
    }
  };

  // Загружаем данные при монтировании
  useEffect(() => {
    if (userInfo?.isLoggedIn) {
      fetchUserData();
      fetchUserPosts();
    } else {
      setIsFetching(false);
      setIsFetchingPosts(false);
    }
  }, [userInfo?.isLoggedIn]);

  const handleSave = async () => {
    setIsLoading(true);
    setError("");
    setSuccessMessage("");

    const token = localStorage.getItem('authToken');
    const userEmail = getUserEmail();
    
    if (!token || !userEmail) {
      setError("Требуется авторизация");
      setIsLoading(false);
      return;
    }

    try {
      // Формируем данные для обновления
      const updateData = {
        name: userData.name,
        phone: userData.phone,
        email: userData.email
      };
      
      // URL для обновления по email
      const apiUrl = `https://pets.xn--80ahdri7a.site/api/users/?email=${encodeURIComponent(userEmail)}`;
      console.log("Updating user at:", apiUrl, "with data:", updateData);

      const response = await fetch(apiUrl, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify(updateData)
      });

      if (!response.ok) {
        if (response.status === 422) {
          setError("Ошибка валидации данных");
        } else if (response.status === 401) {
          setError("Требуется авторизация");
        } else if (response.status === 404) {
          setError("Пользователь не найден");
        } else {
          throw new Error(`Ошибка сервера: ${response.status}`);
        }
        setIsLoading(false);
        return;
      }

      const result = await response.json();
      console.log("Update response:", result);
      
      // Показываем сообщение об успехе
      setSuccessMessage("Изменения успешно сохранены!");
      
      // Обновляем данные в localStorage
      localStorage.setItem('userEmail', userData.email);
      localStorage.setItem('userData', JSON.stringify({
        name: userData.name,
        email: userData.email,
        phone: userData.phone
      }));
      
      // Отключаем режим редактирования
      setTimeout(() => {
        setIsEditing(false);
        setSuccessMessage("");
      }, 2000);
    } catch (err) {
      console.error("Ошибка при сохранении данных:", err);
      setError(`Не удалось сохранить изменения: ${err.message}`);
    } finally {
      setIsLoading(false);
    }
  };

  // Функция для выхода из аккаунта
  const handleLogout = () => {
    localStorage.removeItem('authToken');
    localStorage.removeItem('userData');
    localStorage.removeItem('userEmail');
    
    alert("Вы успешно вышли из аккаунта");
    
    document.querySelector('#myPage .btn-close')?.click();
    
    setTimeout(() => {
      window.location.reload();
    }, 100);
  };

  // Если пользователь не авторизован
  if (!userInfo?.isLoggedIn) {
    return (
      <div className="modal fade" id="myPage" tabIndex={-1} aria-hidden="true">
        <div className="modal-dialog modal-lg">
          <div className="modal-content">
            <div className="modal-header">
              <h1 className="modal-title fs-5">Личный кабинет</h1>
              <button type="button" className="btn-close" data-bs-dismiss="modal" />
            </div>
            <div className="modal-body">
              <div className="text-center p-4">
                <div className="alert alert-warning" role="alert">
                  <h5 className="alert-heading">Требуется авторизация!</h5>
                  <p>Для доступа к личному кабинету необходимо войти в систему.</p>
                  <hr />
                  <button 
                    className="btn btn-dark"
                    data-bs-dismiss="modal"
                    data-bs-toggle="modal"
                    data-bs-target="#authModal"
                  >
                    Перейти к авторизации
                  </button>
                </div>
              </div>
            </div>
            <div className="modal-footer">
              <button 
                type="button" 
                className="btn btn-danger" 
                data-bs-dismiss="modal"
              >
                Закрыть
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="modal fade" id="myPage" tabIndex={-1} aria-hidden="true">
      <div className="modal-dialog modal-lg">
        <div className="modal-content">
          <div className="modal-header">
            <h1 className="modal-title fs-5">
              Личный кабинет - {userData.name || userInfo.userName || "Пользователь"}
            </h1>
            <button type="button" className="btn-close" data-bs-dismiss="modal" />
          </div>
          <div className="modal-body">
            {successMessage && (
              <div className="alert alert-success alert-dismissible fade show" role="alert">
                {successMessage}
                <button type="button" className="btn-close" onClick={() => setSuccessMessage("")} />
              </div>
            )}

            {error && !successMessage && (
              <div className="alert alert-danger alert-dismissible fade show" role="alert">
                {error}
                <button type="button" className="btn-close" onClick={() => setError("")} />
              </div>
            )}

            {isFetching ? (
              <div className="text-center p-4">
                <div className="spinner-border text-dark" role="status">
                  <span className="visually-hidden">Загрузка...</span>
                </div>
                <p className="mt-2">Загрузка данных пользователя...</p>
              </div>
            ) : (
              <div className="row">
                <div className="col-md-6">
                  <h5>Данные пользователя</h5>
                  
                  <div className="mb-3">
                    <label className="form-label small">Имя:</label>
                    <input 
                      type="text" 
                      className="form-control" 
                      placeholder="Имя" 
                      value={userData.name}
                      onChange={(e) => setUserData({...userData, name: e.target.value})}
                      disabled={!isEditing || isLoading}
                    />
                  </div>
                  
                  <div className="mb-3">
                    <label className="form-label small">Email:</label>
                    <input 
                      type="email" 
                      className="form-control" 
                      placeholder="email" 
                      value={userData.email}
                      onChange={(e) => setUserData({...userData, email: e.target.value})}
                      disabled={!isEditing || isLoading}
                    />
                  </div>
                  
                  <div className="mb-3">
                    <label className="form-label small">Телефон:</label>
                    <input 
                      type="text" 
                      className="form-control" 
                      placeholder="phone" 
                      value={userData.phone}
                      onChange={(e) => setUserData({...userData, phone: e.target.value})}
                      disabled={!isEditing || isLoading}
                    />
                  </div>
                  
                  <div className="mt-4">
                    <h6>Статистика</h6>
                    <div className="row text-center">
                      <div className="col-4">
                        <div className="card bg-light">
                          <div className="card-body">
                            <h5 className="card-title">{userData.ordersCount}</h5>
                            <p className="card-text small">Заказы</p>
                          </div>
                        </div>
                      </div>
                      <div className="col-4">
                        <div className="card bg-light">
                          <div className="card-body">
                            <h5 className="card-title">{userData.petsCount}</h5>
                            <p className="card-text small">Питомцы</p>
                          </div>
                        </div>
                      </div>
                      <div className="col-4">
                        <div className="card bg-light">
                          <div className="card-body">
                            <h5 className="card-title">{daysSinceRegistration}</h5>
                            <p className="card-text small">Дней</p>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                  
                  <div className="mt-3">
                    <p className="text-muted small">
                      <strong>Email:</strong> {userData.email || "Не указан"}<br />
                      <strong>Дата регистрации:</strong> {userData.registrationDate || "Не указана"}<br />
                      <strong>На сайте:</strong> {daysSinceRegistration} дней
                    </p>
                  </div>
                </div>
                
                <div className="col-md-6">
                  <h5>Мои объявления</h5>
                  
                  {isFetchingPosts ? (
                    <div className="text-center p-3">
                      <div className="spinner-border spinner-border-sm text-secondary" role="status">
                        <span className="visually-hidden">Загрузка...</span>
                      </div>
                      <p className="mt-2 small">Загрузка объявлений...</p>
                    </div>
                  ) : userPosts.length > 0 ? (
                    <div className="list-group">
                      {userPosts.map(post => (
                        <div key={post.id} className="list-group-item">
                          <div className="d-flex justify-content-between align-items-center">
                            <div>
                              <strong>{post.title}</strong>
                              <br />
                              <small className="text-muted">{post.location}</small>
                            </div>
                            <span className="badge bg-secondary">{post.date}</span>
                          </div>
                          <button 
                            className="btn btn-sm btn-outline-info mt-2"
                            onClick={() => document.querySelector('#myPage .btn-close')?.click()}
                          >
                            Посмотреть
                          </button>
                        </div>
                      ))}
                    </div>
                  ) : (
                    <div className="text-center p-4">
                      <p className="text-muted">У вас еще нет объявлений</p>
                      <button 
                        className="btn btn-sm btn-outline-dark" 
                        data-bs-dismiss="modal"
                        data-bs-toggle="modal" 
                        data-bs-target="#newPost"
                        onClick={() => document.querySelector('#myPage .btn-close')?.click()}
                      >
                        Создать объявление
                      </button>
                    </div>
                  )}
                  
                  {/* Кнопка выхода */}
                  <div className="mt-4 pt-3 border-top">
                    <button 
                      className="btn btn-outline-danger w-100"
                      onClick={handleLogout}
                      disabled={isLoading}
                    >
                      Выйти из аккаунта
                    </button>
                  </div>
                </div>
              </div>
            )}
          </div>
          <div className="modal-footer">
            <button 
              type="button" 
              className="btn btn-danger" 
              data-bs-dismiss="modal"
              disabled={isLoading}
            >
              Закрыть
            </button>
            
            <button 
              type="button" 
              className="btn btn-warning"
              onClick={() => {
                if (isEditing) {
                  fetchUserData();
                  setError("");
                  setSuccessMessage("");
                }
                setIsEditing(!isEditing);
              }}
              disabled={isLoading || isFetching}
            >
              {isEditing ? "Отменить" : "Редактировать"}
            </button>
            
            <button 
              type="button" 
              className="btn btn-dark"
              onClick={handleSave}
              disabled={!isEditing || isLoading || isFetching}
            >
              {isLoading ? (
                <>
                  <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span>
                  Сохранение...
                </>
              ) : "Сохранить"}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Profile;
