import React, { useState, useEffect } from "react";

function Profile({ userInfo }) {
  const [userData, setUserData] = useState({
    email: userInfo?.email || "",
    phone: userInfo?.phone || "",
    posts: []
  });

  const [isEditing, setIsEditing] = useState(false);

  useEffect(() => {
    if (userInfo?.isLoggedIn) {
      setUserData({
        email: "user@example.com",
        phone: "+7 (999) 999-99-99",
        posts: [
          { id: 1, title: "Нашел котенка", date: "2025-10-12" },
          { id: 2, title: "Ищу хозяев для собаки", date: "2025-10-10" }
        ]
      });
    }
  }, [userInfo]);

  const handleSave = () => {
    alert("Изменения сохранены!");
    setIsEditing(false);
  };

  return (
    <div className="modal fade" id="myPage" tabIndex={-1} aria-labelledby="exampleModalLabel" aria-hidden="true">
      <div className="modal-dialog">
        <div className="modal-content">
          <div className="modal-header">
            <h1 className="modal-title fs-5" id="exampleModalLabel">
              {userInfo?.isLoggedIn ? `Личный кабинет - ${userInfo.userName}` : "Личный кабинет"}
            </h1>
            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Закрыть" />
          </div>
          <div className="modal-body">
            <ul className="list-group">
              <h5>{userInfo?.isLoggedIn ? "Данные пользователя" : "Войдите для просмотра"}</h5>
              <li className="list-group-item">
                <input 
                  type="text" 
                  className="form-control" 
                  placeholder="email" 
                  value={userData.email}
                  onChange={(e) => setUserData({...userData, email: e.target.value})}
                  disabled={!isEditing}
                />
              </li>
              <li className="list-group-item">
                <input 
                  type="text" 
                  className="form-control" 
                  placeholder="phone" 
                  value={userData.phone}
                  onChange={(e) => setUserData({...userData, phone: e.target.value})}
                  disabled={!isEditing}
                />
              </li>
            </ul>
            {userInfo?.isLoggedIn && userData.posts.length > 0 && (
              <div>
                <h5 className="mt-3">Мои посты</h5>
                <ul className="list-group">
                  {userData.posts.map(post => (
                    <li key={post.id} className="list-group-item">
                      {post.title} <small className="text-muted">({post.date})</small>
                    </li>
                  ))}
                </ul>
              </div>
            )}
          </div>
          <div className="modal-footer">
            <button type="button" className="btn btn-danger" data-bs-dismiss="modal">Закрыть</button>
            {userInfo?.isLoggedIn && (
              <>
                <button 
                  type="button" 
                  className="btn btn-warning"
                  onClick={() => setIsEditing(!isEditing)}
                >
                  {isEditing ? "Отменить" : "Редактировать"}
                </button>
                <button 
                  type="button" 
                  className="btn btn-dark"
                  onClick={handleSave}
                  disabled={!isEditing}
                >
                  Сохранить изменения
                </button>
              </>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Profile;
