import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import AnimalCard from '../animals/AnimalCard';

const SearchPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const [searchResults, setSearchResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedAnimalId, setSelectedAnimalId] = useState(null);

  useEffect(() => {
    const params = new URLSearchParams(location.search);
    const query = params.get('q') || '';
    setSearchQuery(query);
    
    if (query.length >= 3) {
      performSearch(query);
    } else {
      setSearchResults([]);
    }
  }, [location.search]);

  const performSearch = async (query) => {
    setLoading(true);
    try {
      const response = await fetch(
        `https://pets.xn--80ahdri7a.site/api/search?query=${encodeURIComponent(query)}`
      );
      
      if (!response.ok) {
        throw new Error('Ошибка поиска');
      }
      
      const data = await response.json();
      
      if (data.data?.orders) {
        setSearchResults(data.data.orders);
      } else {
        setSearchResults([]);
      }
    } catch (error) {
      console.error('Search error:', error);
      setSearchResults([]);
    } finally {
      setLoading(false);
    }
  };

  const handleNewSearch = (e) => {
    e.preventDefault();
    if (searchQuery.trim().length >= 3) {
      navigate(`/search?q=${encodeURIComponent(searchQuery)}`);
    }
  };

  return (
    <div className="container mt-5">
      <div className="row mb-4">
        <div className="col-12">
          <div className="card shadow-sm">
            <div className="card-body">
              <h2 className="card-title mb-4">
                {searchQuery ? (
                  <>Результаты поиска: <span className="text-primary">"{searchQuery}"</span></>
                ) : (
                  'Поиск объявлений'
                )}
              </h2>
              
              <form onSubmit={handleNewSearch} className="mb-4">
                <div className="input-group">
                  <input
                    type="text"
                    className="form-control form-control-lg"
                    placeholder="Введите запрос для поиска..."
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    minLength={3}
                  />
                  <button 
                    className="btn btn-primary btn-lg" 
                    type="submit"
                    disabled={searchQuery.trim().length < 3}
                  >
                    Поиск
                  </button>
                </div>
                {searchQuery.trim().length > 0 && searchQuery.trim().length < 3 && (
                  <div className="alert alert-warning mt-2">
                    Введите минимум 3 символа для поиска
                  </div>
                )}
              </form>
              
              <div className="search-stats mb-3">
                {searchQuery.length >= 3 && (
                  <p className="text-muted">
                    Найдено объявлений: {loading ? '...' : searchResults.length}
                  </p>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>

      {loading ? (
        <div className="text-center py-5">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Загрузка...</span>
          </div>
          <p className="mt-3">Идет поиск объявлений...</p>
        </div>
      ) : searchQuery.length < 3 ? (
        <div className="text-center py-5">
          <div className="alert alert-info">
            <i className="bi bi-search me-2"></i>
            Для поиска введите минимум 3 символа в поле поиска выше
          </div>
        </div>
      ) : searchResults.length === 0 ? (
        <div className="text-center py-5">
          <div className="alert alert-warning">
            <i className="bi bi-exclamation-triangle me-2"></i>
            По запросу "{searchQuery}" ничего не найдено
          </div>
          <p className="text-muted mt-3">Попробуйте изменить поисковый запрос</p>
        </div>
      ) : (
        <>
          <div className="row row-cols-1 row-cols-md-2 row-cols-lg-3 g-4">
            {searchResults.map((animal) => (
              <div key={animal.id} className="col">
                <AnimalCard 
                  animal={animal}
                  onViewClick={setSelectedAnimalId}
                />
              </div>
            ))}
          </div>
          
          <div className="mt-5 pt-4 border-top">
            <div className="row">
              <div className="col-md-6">
                <div className="alert alert-light">
                  <h5 className="alert-heading">
                    <i className="bi bi-info-circle me-2"></i>
                    Информация о поиске
                  </h5>
                  <p className="mb-0">
                    Найдено <strong>{searchResults.length}</strong> объявлений по запросу "{searchQuery}"
                  </p>
                </div>
              </div>
              <div className="col-md-6">
                <div className="alert alert-light">
                  <h5 className="alert-heading">
                    <i className="bi bi-lightbulb me-2"></i>
                    Советы по поиску
                  </h5>
                  <p className="mb-0">
                    Используйте более конкретные ключевые слова для точного поиска
                  </p>
                </div>
              </div>
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default SearchPage;
