import React, { useState, useEffect } from "react";
import './css/style.css';

function Slider() {
  const [pets, setPets] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [activeIndex, setActiveIndex] = useState(0);

  // Функция для загрузки данных о животных
  const fetchPetsData = async () => {
    try {
      setLoading(true);
      setError(null);
      
      const url = 'https://pets.xn--80ahdri7a.site/api/pets/slider';
      const response = await fetch(url, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json'
        }
      });

      if (!response.ok) {
        throw new Error(`Ошибка загрузки: ${response.status}`);
      }

      const result = await response.json();
      
      // Проверяем наличие данных
      if (result.data && Array.isArray(result.data.pets)) {
        setPets(result.data.pets);
      } else {
        setPets([]);
      }
      
    } catch (error) {
      console.error('Ошибка при загрузке слайдера:', error);
      setError('Не удалось загрузить объявления. Попробуйте позже.');
      setPets([]);
    } finally {
      setLoading(false);
    }
  };

  // Загружаем данные при монтировании компонента
  useEffect(() => {
    fetchPetsData();
  }, []);

  // Обработчики для карусели
  const handlePrev = () => {
    setActiveIndex((prevIndex) => 
      prevIndex === 0 ? pets.length - 1 : prevIndex - 1
    );
  };

  const handleNext = () => {
    setActiveIndex((prevIndex) => 
      prevIndex === pets.length - 1 ? 0 : prevIndex + 1
    );
  };

  // Если загрузка
  if (loading) {
    return (
      <div className="text-center py-5">
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Загрузка...</span>
        </div>
        <p className="mt-2">Загрузка объявлений...</p>
      </div>
    );
  }

  // Если ошибка
  if (error) {
    return (
      <div className="alert alert-warning text-center" role="alert">
        {error}
      </div>
    );
  }

  // Если нет данных
  if (!pets || pets.length === 0) {
    return null; // Блок не отображается
  }

  return (
    <div className="slider-container">
      <div id="animalCarousel" className="carousel slide" data-bs-ride="carousel">
        {/* Индикаторы слайдов */}
        <div className="carousel-indicators">
          {pets.map((_, index) => (
            <button 
              key={index}
              type="button" 
              data-bs-target="#animalCarousel" 
              data-bs-slide-to={index}
              className={index === activeIndex ? "active" : ""}
              aria-current={index === activeIndex ? "true" : "false"}
              aria-label={`Слайд ${index + 1}`}
              onClick={() => setActiveIndex(index)}
            />
          ))}
        </div>

        {/* Слайды */}
        <div className="carousel-inner">
          {pets.map((pet, index) => (
            <div 
              key={pet.id}
              className={`carousel-item ${index === activeIndex ? "active" : ""}`}
            >
              <div className="carousel-image-container">
                {pet.image ? (
                  <img 
                    src={`https://pets.xn--80ahdri7a.site${pet.image}`} 
                    className="carousel-image"
                    alt={`${pet.kind} - ${pet.description ? pet.description.substring(0, 50) : 'описание отсутствует'}...`} 
                  />
                ) : (
                  <div className="carousel-image-placeholder">
                    <div className="placeholder-content">
                      <i className="bi bi-image fs-1"></i>
                      <span className="ms-2">Изображение отсутствует</span>
                    </div>
                  </div>
                )}
              </div>
              
              <div className="carousel-caption d-none d-md-block">
                <div className="caption-content bg-dark bg-opacity-75 rounded p-3">
                  <h5>{pet.kind ? `${pet.kind}` : "Животное"}</h5>
                  <p>{pet.description || "Описание отсутствует"}</p>
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* Кнопки управления */}
        <button 
          className="carousel-control-prev" 
          type="button" 
          onClick={handlePrev}
        >
          <span className="carousel-control-prev-icon" aria-hidden="true" />
          <span className="visually-hidden">Предыдущий</span>
        </button>
        <button 
          className="carousel-control-next" 
          type="button" 
          onClick={handleNext}
        >
          <span className="carousel-control-next-icon" aria-hidden="true" />
          <span className="visually-hidden">Следующий</span>
        </button>
      </div>
      
      {/* Добавьте этот стиль в ваш CSS файл или как инлайн стиль */}
      <style jsx="true">{`
        .slider-container {
          width: 100%;
          height: 500px; /* Фиксированная высота слайдера */
          overflow: hidden;
          position: relative;
        }
        
        .carousel {
          height: 100%;
        }
        
        .carousel-inner {
          height: 100%;
        }
        
        .carousel-item {
          height: 100%;
          transition: transform 0.6s ease;
        }
        
        .carousel-image-container {
          width: 100%;
          height: 100%;
          position: relative;
        }
        
        .carousel-image {
          width: 100%;
          height: 100%;
          object-fit: cover; /* Это ключевое свойство для правильного отображения */
          object-position: center;
        }
        
        .carousel-image-placeholder {
          width: 100%;
          height: 100%;
          background: linear-gradient(45deg, #6c757d, #495057);
          display: flex;
          align-items: center;
          justify-content: center;
        }
        
        .placeholder-content {
          display: flex;
          flex-direction: column;
          align-items: center;
          color: white;
        }
        
        .carousel-caption {
          position: absolute;
          bottom: 20px;
          left: 50%;
          transform: translateX(-50%);
          width: 90%;
          max-width: 800px;
        }
        
        .caption-content {
          max-width: 100%;
        }
        
        /* Стили для индикаторов */
        .carousel-indicators button {
          width: 12px;
          height: 12px;
          border-radius: 50%;
          margin: 0 5px;
          border: 2px solid #fff;
          background-color: transparent;
        }
        
        .carousel-indicators button.active {
          background-color: #fff;
        }
        
        /* Адаптивность */
        @media (max-width: 768px) {
          .slider-container {
            height: 400px;
          }
          
          .carousel-caption {
            bottom: 10px;
            padding: 0 10px;
          }
          
          .caption-content {
            padding: 10px !important;
          }
          
          .caption-content h5 {
            font-size: 1rem;
            margin-bottom: 5px;
          }
          
          .caption-content p {
            font-size: 0.85rem;
            margin-bottom: 0;
          }
        }
        
        @media (max-width: 576px) {
          .slider-container {
            height: 300px;
          }
          
          .carousel-indicators {
            margin-bottom: 5px;
          }
          
          .carousel-indicators button {
            width: 10px;
            height: 10px;
            margin: 0 3px;
          }
        }
      `}</style>
    </div>
  );
}

export default Slider;
