import React, { useState, useEffect } from 'react';
import './css/card-style.css';

function Modal_Cat({ petId, onClose }) {
  const [animalInfo, setAnimalInfo] = useState({
    name: "",
    location: "",
    description: "",
    age: "",
    gender: "",
    features: "",
    contact: "",
    foundDate: "",
    photos: [],
    kind: "",
    mark: ""
  });

  const [isInterested, setIsInterested] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(true);

  // Загружаем данные о питомце с API
  useEffect(() => {
    async function fetchPetData() {
      try {
        // Используем правильный endpoint согласно документации
        const response = await fetch(`https://pets.xn--80ahdri7a.site/api/pets/${petId}`, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json'
          }
        });

        console.log('Response status:', response.status);

        if (response.status === 404) {
          setError("Информация о питомце не найдена (404)");
          setAnimalInfo(null);
          setLoading(false);
          return;
        }

        if (!response.ok) {
          setError(`Ошибка при загрузке данных: ${response.status}`);
          setLoading(false);
          return;
        }

        const result = await response.json();
        console.log('API Response:', result);

        // Проверяем структуру ответа согласно документации
        if (result && result.data && result.data.pet && result.data.pet.length > 0) {
          const petData = result.data.pet[0];
          console.log('Pet data:', petData);

          // Преобразуем данные из API в формат компонента
          setAnimalInfo({
            name: petData.name || "", // Имя нашедшего
            location: petData.district || "",
            description: petData.description || "",
            age: "", // Не приходит с API
            gender: "", // Не приходит с API
            features: petData.mark || "", // Используем метку как особенность
            contact: `${petData.phone || ""} ${petData.email || ""}`,
            foundDate: petData.date || "",
            photos: petData.photos || [],
            kind: petData.kind || "",
            mark: petData.mark || "",
            phone: petData.phone || "",
            email: petData.email || ""
          });
        } else {
          console.log('No pet data in response');
          setError("Нет данных о питомце в ответе");
          setAnimalInfo(null);
        }
        setLoading(false);
      } catch (error) {
        console.error('Ошибка:', error);
        setError("Ошибка соединения с сервером");
        setLoading(false);
      }
    }

    if (petId) {
      fetchPetData();
    } else {
      setError("ID питомца не указан");
      setLoading(false);
    }
  }, [petId]);

  const handleAdopt = () => {
    setIsInterested(true);
    alert(`Вы проявили интерес к ${animalInfo?.kind || 'питомцу'}. Мы свяжемся с вами по телефону!`);
  };

  const handleClose = () => {
    setIsModalOpen(false);
    if (onClose) {
      onClose();
    }
  };

  // Если модальное окно закрыто, не рендерим ничего
  if (!isModalOpen) {
    return null;
  }

  if (loading) {
    return (
      <div className="modal-backdrop fade show">
        <div className="modal fade show d-block" tabIndex={-1}>
          <div className="modal-dialog modal-lg modal-dialog-centered">
            <div className="modal-content">
              <div className="modal-body text-center py-5">
                <div className="spinner-border" role="status">
                  <span className="visually-hidden">Загрузка...</span>
                </div>
                <p className="mt-2">Загрузка информации о питомце...</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (error || !animalInfo) {
    return (
      <div className="modal-backdrop fade show">
        <div className="modal fade show d-block" tabIndex={-1}>
          <div className="modal-dialog modal-lg modal-dialog-centered">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Ошибка</h5>
                <button 
                  type="button" 
                  className="btn-close" 
                  onClick={handleClose}
                  aria-label="Закрыть" 
                />
              </div>
              <div className="modal-body text-center py-5">
                <p className="text-danger">{error || "Информация о питомце не найдена"}</p>
              </div>
              <div className="modal-footer">
                <button type="button" className="btn btn-secondary" onClick={handleClose}>
                  Закрыть
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="modal-backdrop fade show">
      <div className="modal fade show d-block" tabIndex={-1}>
        <div className="modal-dialog modal-lg modal-dialog-centered">
          <div className="modal-content">
            <div className="modal-header">
              <h1 className="modal-title fs-5">
                {animalInfo.kind || "Питомец"}
                {animalInfo.mark && <span className="text-muted ms-2">({animalInfo.mark})</span>}
              </h1>
              <button 
                type="button" 
                className="btn-close" 
                onClick={handleClose}
                aria-label="Закрыть" 
              />
            </div>
            <div className="modal-body">
              <div className="row">
                <div className="col-md-6">
                  <div className="info-img">
                    {animalInfo.photos && animalInfo.photos.length > 0 ? (
                      <img 
                        src={`https://pets.xn--80ahdri7a.site${animalInfo.photos[0]}`} 
                        className="card-img-bottom info-img-img w-100 h-auto" 
                        alt={animalInfo.kind || "Питомец"} 
                        style={{ maxHeight: '400px', objectFit: 'cover' }}
                        onError={(e) => {
                          e.target.onerror = null;
                          e.target.src = "https://pets.xn--80ahdri7a.site/img/placeholder.jpg";
                        }}
                      />
                    ) : (
                      <div className="text-center py-5 bg-light">
                        <span>Нет изображения</span>
                      </div>
                    )}
                    {/* Маленькие превью дополнительных фото */}
                    {animalInfo.photos && animalInfo.photos.length > 1 && (
                      <div className="d-flex mt-3">
                        {animalInfo.photos.slice(1).map((photo, index) => (
                          <img
                            key={index}
                            src={`https://pets.xn--80ahdri7a.site${photo}`}
                            className="me-2"
                            style={{ width: '60px', height: '60px', objectFit: 'cover', cursor: 'pointer' }}
                            alt={`Фото ${index + 2}`}
                            onClick={() => {
                              // Можно добавить логику для показа фото в большом размере
                              const mainImg = document.querySelector('.info-img-img');
                              if (mainImg) {
                                mainImg.src = `https://pets.xn--80ahdri7a.site${photo}`;
                              }
                            }}
                          />
                        ))}
                      </div>
                    )}
                  </div>
                </div>
                <div className="col-md-6">
                  <p><strong>Дата находки:</strong> {animalInfo.foundDate}</p>
                  <p><strong>Место находки:</strong> {animalInfo.location}</p>
                  <p><strong>Нашедший:</strong> {animalInfo.name}</p>
                  <p><strong>Описание:</strong> {animalInfo.description}</p>
                  {animalInfo.age && <p><strong>Возраст:</strong> {animalInfo.age}</p>}
                  {animalInfo.gender && <p><strong>Пол:</strong> {animalInfo.gender}</p>}
                  {animalInfo.features && <p><strong>Метка:</strong> {animalInfo.features}</p>}
                  {animalInfo.phone && <p><strong>Телефон:</strong> {animalInfo.phone}</p>}
                  {animalInfo.email && <p><strong>Email:</strong> {animalInfo.email}</p>}
                  <p><strong>Контакты для связи:</strong> {animalInfo.contact}</p>
                </div>
              </div>
            </div>
            <div className="modal-footer">
              <button type="button" className="btn btn-secondary" onClick={handleClose}>
                Закрыть
              </button>
              <button 
                type="button" 
                className="btn btn-dark"
                onClick={handleAdopt}
                disabled={isInterested}
              >
                {isInterested ? "Заявка отправлена" : "Забрать"}
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Modal_Cat;
